    init() {
        databank.append(DataModel(title: "செம்மொழியான  தமிழ் மொழியாம்", data: "பிறப்புக்கும் எல்லா உயிர்க்கும் பிறந்த பின்னர், <br/>யாதும் ஊரே, யாவரும் கேளிர்<br/>உண்பது நாழி உடுப்பது இரண்டே<br/>உறைவிடம் என்பது ஒன்றே<br/>உரைத்து வாழ்ந்தோம் உழைத்து வாழ்வோம்<br/>தீதும் நன்றும் பிறர் தர வாரா எனும்<br/>நன் மொழியே நம் பொன் மொழியாம்<br/>போரை புறம் தள்ளி பொருளைப் பொதுவாக்கவே<br/>அமைதி வழி காட்டும் அன்பு மொழி<br/>அய்யன் வள்ளுவரின் வாய்மொழியாம்<br/>செம்மொழியான  தமிழ் மொழியாம்,<br/>கம்ப நாட்டாழ்வாரும் கவியரசி அவ்வை நல்லாளும் <br/>எம்மதமும் ஏற்றுப் புகழ்கின்ற<br/>எத்தனையோ ஆயிரம் கவிதை நெய்வோர் தரும் <br/>புத்தாடை அனைத்துக்கும் வித்தாக விளங்கும் மொழி <br/>செம்மொழியான தமிழ் மொழியாம்<br/>அகமென்றும் புறமென்றும் <br/>வாழ்வை அழகாக வகுத்தளித்து<br/>ஆதி அந்தமிலாது இருக்கின்ற இனியமொழி<br/>ஓதி வளரும் உயிரான உலக மொழி<br/>நம்மொழி நம் மொழி அதுவே<br/>செம்மொழியான  தமிழ் மொழியாம்<br/>தமிழ் மொழி தமிழ் மொழி தமிழ் மொழியாம்<br/>செம்மொழியான  தமிழ் மொழியாம்<br/>தமிழ் மொழியாம் எங்கள் தமிழ் மொழியாம்<br/>வாழிய வாழியவே தமிழ் வாழிய வாழியவே<br/>செம்மொழியான  தமிழ் மொழியாம்"))
        
        databank.append(DataModel(title: "வாழ்கை என்னும் ஓடம்", data: " ஒருவனுக்கு ஒருத்தி என்ற<br/> உயிர் மூச்சி உள்ளடக்கி<br/> ஆறம் எனப்பட்டதே இல்வாழ்கை<br/> அகுதும் பிரம் பழிபதிலாயின் நன்று,<br/> என்னும் இரு குறளை மறவாதே<br/> திருக்குறளை மறவாதே<br/> திசை தவறி போகாதே.<br/> வாழ்கை என்னும் ஓடம், வழங்குகின்ற பாடம்.<br/> மானிடரின் மனதினிலே மறக்க ஒண்ணா வேதம்.<br/> வாழ்கை என்னும் ஓடம்<br/> வாலிபம் என்பது கழிகின்ற வேடம், அதில்<br/> வந்தது வரட்டும் என்பவன் முழு மூடன்<br/> வரும் முன் காப்பவன் தான் அறிவாளி, புயல்<br/> வரும் முன் காப்பவன் தான் அறிவாளி.<br/> அது வந்தபின்னே தவிப்பவன் தான் ஏமாளி.<br/> துடுப்புகள் இல்லா படகு,<br/> அலைகள் அழிகின்ற திசை எல்லாம் போகும்.<br/> தீமையை தடுபவரில்லா வாழ்வும்,<br/> அந்த படகின் நிலை போலே ஆகும்.<br/> அந்த படகின் நிலை போலே ஆகும்."))
        
        databank.append(DataModel(title: "பூ மாலை", data: "பூ மாலை நீயே புழுதி மண் மேலே வீணே<br/>வந்தேன் தவழ்ந்தாய்<br/>பூ மாலை நீயே புழுதி மண் மேலே வீணே<br/>வந்தேன் தவழ்ந்தாய்<br/>பாராயோ என் பன்னீர் செல்வமே<br/>சீரோடு வாழ்ந்தோம் வேரோடு சாய்ந்தோம்<br/>ராஜாங்கம் போலே வாழ்ந்த வாழ்வும் வீழ்ந்தே<br/>பாழான எந்தன் வயிற்றில் பிறந்தாய் ராஜா<br/>நீ ஏன் பிறந்தாய்<br/>பூ மாலை நீயே புழுதி மண் மேலே வீணே<br/>வந்தேன் தவழ்ந்தாய்<br/>பறவை வீட்டில் நீ பிறந்திருந்தாலும்<br/>பசி நீங்கவே உணவும் பறந்தோடி வந்திடும்<br/>கோடானு கோடி ஏழை மாந்தர் வாடும்<br/>சீமான்கள் வாழும் நாட்டில் பிறந்தாய் ராஜா<br/>நீ ஏன் பிறந்தாய்<br/>பூ மாலை…<br/>பூ மாலை நீயே புழுதி மண் மேலே வீணே<br/>வந்தேன் தவழ்ந்தாய்…"))
        
        databank.append(DataModel(title: "இல் வாழ்வினிலே", data: "இல் வாழ்வினிலே ஒளி ஏற்றும் தீபம்<br/>என் இதய ராணி ரூபம்<br/>இல் வாழ்வினிலே ஒளி ஏற்றும் தீபம்<br/>என் இதய ராணி ரூபம்<br/>ஓ… இந்த தீபத்திலே சுடராய்<br/>திகழ்வதென் தீரனின் ரூபம்<br/>இள மாமயில் ரூபம்<br/>ஓ… இந்த தீபத்திலே சுடராய்<br/>திகழ்வதென் தீரனின் ரூபம்<br/>இள மாமயில் ரூபம்<br/>இல் வாழ்வினிலே ஒளி ஏற்றும் தீபம்<br/>நம் காதல் தீபம்<br/>கனியே கன்னல் தமிழே அமுதே<br/>பனி தூங்கும் மலரே நிலவே<br/>கனிவான மொழியால் எனையே<br/>கவர்ந்தீரே காந்தச் சிலையே ( இசை )<br/>கனியே கன்னல் தமிழே அமுதே<br/>பனி தூங்கும் மலரே நிலவே<br/>கனிவான மொழியால் எனையே<br/>கவர்ந்தீரே காந்தச் சிலையே<br/>ஓ… உன்னை காணும் போது நெஞ்சில்<br/>ஊறிடும் உணர்ச்சி தனையே<br/>சொல்ல ஓர் வார்த்தை இல்லையே<br/>ஓ… உன்னை காணும் போது நெஞ்சில்<br/>ஊறிடும் உணர்ச்சி தனையே<br/>எழுத ஓர் எழுத்தும் இல்லையே<br/>திருநாளினைப் போல் தினம் ஆடிப் பாடியே<br/>சேர்ந்தே வாழ்வோம்<br/>திருநாளினைப் போல் தினம் ஆடிப் பாடியே<br/>சேர்ந்தே வாழ்வோம்<br/>இல் வாழ்வினிலே ஒளி ஏற்றும் தீபம்<br/>நம் காதல் தீபம்"))
         databank.append(DataModel(title: "நிலா பெண்ணே", data: "வான் முகத்தில் நகக்குறி போல்<br/>இருக்கின்ற நிலாப் பெண்ணின்<br/>தேன் கிண்ண இதழ்களிலே முத்தமிட்டு-<br/><br/>இந்திய நாட்டுத்<br/>தேசியக்கொடிதனையும் கையில்<br/>கொடுத்து விட்டுத் திரும்பி வரும்<br/>திறமைமிகு விஞ்ஞானத்தின் கரம் குலுக்கி<br/>வாழ்த்துகின்றோம்.<br/><br/>புராணத்தில் வரும் சந்திரனோ;<br/>குருவின் மனைவி தாரைக்கு;<br/>புதியதோர் காதலன் என்று புராணமே<br/>கூறி வணங்கும்!<br/><br/>பாம்புகள் இரண்டு ராகுவும் கேதுவும்<br/>சந்திரனை விழுங்குமாம்;<br/>பஞ்சாங்கம் அதனை \"கிரகணம்'' என<br/>விளம்புமாம்!<br/><br/>அனைத்தும் பொய், புளுகு, கற்பனையெனத்<br/>தூக்கி அடித்து;<br/><br/>அமெரிக்க, ரஷ்ய, அய்ரோப்பிய<br/>கூட்டமைப்பு நாடுகளின்<br/>அணிவகுப்பில் இணைந்து அருஞ்சாதனை<br/>புரிந்து விஞ்ஞானம்;<br/>அசோக சக்கரக்கொடியை அம்புலியில்<br/>நாட்டியது;<br/><br/>அறிவியக்க வரலாற்றில் ஓர் அற்புதமாம்;<br/>அதுவும் நம் நாட்டில் என்பதிலோர் பெருமிதமாம்!"))
        
        databank.append(DataModel(title: "நிலா பெண்ணே", data: "வான் முகத்தில் நகக்குறி போல்<br/>இருக்கின்ற நிலாப் பெண்ணின்<br/>தேன் கிண்ண இதழ்களிலே முத்தமிட்டு-<br/><br/>இந்திய நாட்டுத்<br/>தேசியக்கொடிதனையும் கையில்<br/>கொடுத்து விட்டுத் திரும்பி வரும்<br/>திறமைமிகு விஞ்ஞானத்தின் கரம் குலுக்கி<br/>வாழ்த்துகின்றோம்.<br/><br/>புராணத்தில் வரும் சந்திரனோ;<br/>குருவின் மனைவி தாரைக்கு;<br/>புதியதோர் காதலன் என்று புராணமே<br/>கூறி வணங்கும்!<br/><br/>பாம்புகள் இரண்டு ராகுவும் கேதுவும்<br/>சந்திரனை விழுங்குமாம்;<br/>பஞ்சாங்கம் அதனை \"கிரகணம்'' என<br/>விளம்புமாம்!<br/><br/>அனைத்தும் பொய், புளுகு, கற்பனையெனத்<br/>தூக்கி அடித்து;<br/><br/>அமெரிக்க, ரஷ்ய, அய்ரோப்பிய<br/>கூட்டமைப்பு நாடுகளின்<br/>அணிவகுப்பில் இணைந்து அருஞ்சாதனை<br/>புரிந்து விஞ்ஞானம்;<br/>அசோக சக்கரக்கொடியை அம்புலியில்<br/>நாட்டியது;<br/><br/>அறிவியக்க வரலாற்றில் ஓர் அற்புதமாம்;<br/>அதுவும் நம் நாட்டில் என்பதிலோர் பெருமிதமாம்!"))
        
        databank.append(DataModel(title: "தமிழர் திருநாளில் தமிழர்", data: "தமிழர் திருநாள்!<br/>உயர் தமிழர் திருநாள்!<br/>உடன் பிறப்பெ!<br/>என்ன செய்கிறீர்கள்?<br/>இல்லம் பதுங்காதீர்!<br/>ஒய்வு எடுக்காதீர்!<br/><br/>தமிழ் ஆண்டை சித்திரைக்கு<br/>மாற்றி விட்டார்களா?<br/>வருந்தாதே உடன் பிறப்பே!<br/>மீண்டும் தமிழ் ஆண்டு<br/>தையில் மலரும்.<br/><br/>தை தமிழ்ஆண்டு பிறப்பாக்க<br/>ஆடு மாடுகளுக்கு அலங்காரம்<br/>செய்து உங்களுக்கு நாங்கள்<br/>சளைத்தவர்களல்ல என<br/>எட்டு திக்கும் முழங்குவோம்.<br/><br/>கேடுகளைந்து செம்தமிழர்<br/>மறைந்தொழிந்து நாம் மட்டும்<br/>பீடு நடை போடுவதற்கு<br/><br/>தமிழனை மறப்போம்!<br/>தமிழக உரிமையை இழப்போம்!<br/>தமிழக உரிமைகளை பறித்தவர்களை<br/>மன்னிப்போம். அவர்களுடன்<br/>கூடிக்குலாவி உயர் தமிழர் திரு நாளை<br/>கொண்டாடுவோம்.<br/><br/>உடன்பிறப்பே!<br/>தமிழை தமிழனை மறந்திடுங்கள்<br/>வாழ்க! நம்தமிழ்பற்று.! வளர்க!<br/>உயர்தமிழன் என்று வாய் மணக்க<br/>எம்மை வாழ்த்துங்கள்.<br/><br/>"))
        databank.append(DataModel(title: "வலவன் ஏவா வான ஊர்தி", data: "சேற்றுவளர் தாமரை பயந்த, ஒண் கேழ்,<br/>நூற்றிதழ் அலரின் நிறைகண் டன்ன,<br/>வேற்றுமை இல்லா விழுத்திணைப் பிறந்து,<br/>வீற்றிருந் தோரை எண்ணுங் காலை,<br/>உரையும் பாட்டும் உடையோர் சிலரே,<br/>மரைஇலை போல மாய்ந்திசினோர் பலரே;<br/>புலவர் பாடும் புகழுடையார் விசும்பின்<br/>வலவன் ஏவா வான ஊர்தி<br/>எய்துப, என்ப, தம் செய்வினை முடித்து \" எனக்<br/>கேட்பல்; எந்தை! சேட்சென்னி! நலங்கிள்ளி!<br/>தேய்தல் உண்மையும், பெருகல் உண்மையும்,<br/>மாய்தல் உண்மையும், பிறத்தல் உண்மையும்,<br/>அறியா தோரையும், அறியக் காட்டித்,<br/>திங்கட் புத்தேள் திரிதரும் உலகத்து<br/>வல்லார் ஆயினும், வல்லுநர் ஆயினும்,<br/>வருந்தி வந்தோர் மருங்கு நோக்கி,<br/>அருள வல்லை ஆகுமதி; அருளிலர்<br/>கொடா அமை வல்லர் ஆகுக;<br/>கெடாஅத் துப்பின்நின் பகைஎதிர்ந் தோரே."))
        databank.append(DataModel(title: "விரைவில் நாள் குறிக்கப்படும்", data: "கற்கண்டு தமிழ்க் கவிதைகள் கசக்குதென்று<br/>அவற்றின் மீது கரி பூசி மறைக்கும்<br/>கர்த்தப அறிவுடையோர்க்கு சம்மட்டி அடி<br/>கொடுத்தது போல் வந்தது தீர்ப்பு!<br/><br/>தமிழர்களின் தொடர்ந்து வரும் தூய வரலாற்றினை<br/>தடுத்து நிறுத்தலாம் என்ற<br/>தீய எண்ணத்தோடு<br/>பக்கங்களைக் கிழிக்கும் பாவிகளே!<br/><br/>‘‘பளார்’’, ‘‘பளார்’’ என உங்கள்<br/>கன்னங்களில் அறைவது போல்,<br/>வந்துள்ள நெருப்புத் தீர்ப்பை<br/>பஞ்சுப் பொதிகளால் அணைக்கலாம் என்று<br/>பகற் கனவு காணாதீர்!<br/><br/>இளம் அம்புலிக்கீற்றாக<br/>இன்றெமக்குக் கிடைத்த வெற்றி<br/>தங்க ஒளி நிலவாகதகத்தகாய கதிராக<br/>மாறப் போகும் காலம் நீங்கள்<br/>மறைக்கப் பார்க்கும் திறமையினால்<br/>அழிவதில்லை&அழிவதில்லை!<br/><br/>அதனை உணர்வீர் இன்றே& என்று<br/>இளைய தலைமுறை எக்காள முழக்கமிடுவோம்&<br/>எல்லோரும் வாருங்கள் என்று அழைக்கின்ற<br/>தன்மான அணிக்கு தவறாமல் வந்திடுவீர்!"))
         databank.append(DataModel(title: "இரங்கல் கவிதை...", data: "என் இனிய நண்பா<br/>இளவேனிற் கவிதைகளால்<br/>இதயசுகம் தந்தவனே! உன்<br/>இதயத்துடிப்பை ஏன் நிறுத்திக் கொண்டாய்!<br/><br/>தென்றலாக வீசியவன் நீ - என்நெஞ்சில்<br/>தீயாகச் சுட்டவனும் நீ! அப்போதும்<br/>அன்றிலாக நம் நட்பு நிகழ்ந்ததேயன்றி<br/>அணைந்த தீபமாக ஆனதே இல்லை; நண்பா!<br/><br/>கண்ணதாசா! என்<br/>எண்ணமெல்லாம் இனிக்கும் நேசா!<br/>கவிதை மலர்த் தோட்டம் நீ - உன்னைக்<br/>காலமெனும் பூகம்பம் தகர்த்துத்<br/>தரைமட்டம் ஆக்கிவிட்டதே!<br/><br/>கைநீட்டிக் கொஞ்சுவோர் பக்கமெல்லாம்<br/>கரம் நீட்டித் தாவுகின்ற குழந்தை நீ!<br/>கல்லறைப் பெண்ணின் மடியிலும்<br/>அப்படித்தான் தாவி விட்டாயோ<br/>அமைதிப்பால் அருந்தித் தூங்கி விட!<br/><br/>இயக்க இசைபாடிக்களித்த குயில் உன்னை<br/>மயக்க மருந்திட்டுப் பிரித்தார் முன்னை<br/>தாக்குதல் கணை எத்தனைதான் நீ<br/>தொடுத்தாலும்<br/>தாங்கிக் கொண்ட என்நெஞ்சே உன் அன்னை!<br/>திட்டுவதும் தமிழில் நீ திட்டியதால் - சுவைப்பிட்டு என ஏற்றுக் கொண்ட என்னை;<br/><br/>தித்திக்கும் கவித்தமிழா! பிரிவின்<br/>மத்தியிலே ஏன் விட்டுச் சென்றாய் ?<br/>அடடா! இந்த இளமைக் கழனியில்<br/>அன்பெனும் நாற்று நட்டோம்!<br/><br/>ஆயிரங்காலத்துப் பயிர் நம் தோழமையென ஆயிரங்கோடி கனவு கண்டோம்!<br/>அறுவடைக்கு யாரோ வந்தார்!<br/>உன்னை மட்டும் அறுத்துச் சென்றார்<br/><br/>நிலையில்லா மனம் உனக்கு! ஆனால்<br/>நிலைபெற்ற புகழ் உனக்கு!<br/><br/>இந்த அதிசயத்தை விளைவிக்க உன்பால்<br/>இனியதமிழ் அன்னை துணை நின்றாள்!<br/><br/>என் நண்பா!<br/><br/>இனிய தோழா!<br/><br/>எத்தனையோ தாலாட்டுப்பாடிய உன்னை<br/>இயற்கைத் தாய் தாலாட்டித் தூங்க வைத்தாள்!<br/>எத்தனையோ பாராட்டுப் பெற்ற உனக்கு<br/>இயற்கைத்தாயின் சீராட்டுத்தான் இனிக்கிறதா?<br/><br/>எனை மறந்தாய்! எமை மறந்தாய்! உனை<br/>மறக்க முடியாமல் உள்ளமெல்லாம் நிறைந்தாய்!"))
        
         databank.append(DataModel(title: "ஒரு தாயின் தாலாட்டு!", data: "பகைவெல்லச் சென்றிருக்கும் கணவனது<br/>பனைவைர நெஞ்சத்தின் வீரத்தை நினைத்தவாறு<br/><br/>பகல்முழுதும் குடும்பத்தின் பணிமுடித்துச் சற்றுப்<br/>படுத்துறக்கம் கொள்வதற்குச் செல்லும் முன்னர்<br/><br/>பாசமுடன் மகனைக் கூவியழைத்துக் \"கண்ணா!<br/>பாலமுதம் இதோ பருகிட வாடா;\" என்பேன்!<br/><br/>\"வரமாட்டேன் போ\" என்று நிலாவெள்ளி ஒளியினிலே<br/>வட்டமிட்டு விளையாட ஓடிடுவான் எதிர்வீட்டுப் பிள்ளையுடன்!<br/><br/>வந்துவிடும் எனக்குக் கோபடம் - கோலொன்றை எடுத்தோங்கி<br/>\"வரமாட்டேன் என்றா சொன்னாய்? இதோ வந்துவிட்டேன்\" எனத்<br/><br/>துரத்திடுவேன்; அடிக்கமாட்டேன்! ஆனாலும் என்<br/>தூயமகன் அஞ்சி நடுங்கிப் பதுங்கிடுவான்!<br/><br/>கோலுக்கே இந்த பயம் கொள்கின்ற பிள்ளாய்; நீ - நாளை<br/>வேலுக்குப் போர்முனையில் என்ன பதில் சொல்வாய் என்பேன்!<br/><br/>இழுத்து வந்து செல்லமாய்க் கன்னம் கிள்ளிப்<br/>பழுத்திருக்கும் கோவைக்கனியாம் அவன் இதழில்;<br/><br/>தேன்கலந்த பாலின் கிண்ணம் மெல்லக் கவிழ்த்து<br/>தெம்பாக இருப்பதற்கு முழுவதையும் அருந்தென்பேன்!<br/><br/>அழகுதமிழ் மழலைச்செல்வன் அதனை மறுக்காமல்<br/>அச்சத்தின் காரணமாய் அனைத்தும் பருகிடுவான்!<br/><br/>பிறைபோல இருந்த பிள்ளை முழுநிலவாக உருவெடுத்து<br/>குறையில்லாத் தோற்றமுடன் குன்றம் போல் வளர்ந்து விட்டான்!<br/><br/>தாலாட்டப் பிள்ளையொன்றைத் தந்துவிட்டுப்<br/>போராட்டக்களம் சென்ற என்னவரோ;<br/><br/>தமிழ் மண்ணின் பண்பாட்டுக் கேற்றவாறு<br/>தட மார்பில் வேல்தாங்கி நடுகல்லாய் நின்றுவிட்டார்!<br/><br/>நானும் கல்லாக மனத்தை ஆக்கிக்கொண்டு<br/>வானதிரப் புவி வாழ்த்துகின்ற மானத்தை<br/><br/>ஊனமின்றிக் காப்பதற்கு உறுதிபூண்டு<br/>உயர் வீரக்குடிமகனாய்ப் பிள்ளைதனை வளர்த்தேன்!<br/><br/>கட்டியணைத்துக் களித்த என் கணவனது நெஞ்சகத்தை<br/>வெட்டிப்பிளந்து வீழ்த்திவிட்ட பகைப்புலத்தை;<br/><br/>எட்டியுதைத்துத் துரத்தி ஏற்றிடுக வெற்றிக்கொடி யென்றும்,<br/>எதிரிகளின் கோட்டையிலே நம் முரசு முழங்கிடுமென்றும்,<br/><br/>உச்சிமோந்து திலகமிட்டுக் குடுமிதனைக் கோதிவிட்டு<br/>உடைவாளைக் கையில்தந்து, \"சென்றுவா மகனே\" என அனுப்பி வைத்தேன்<br/><br/>அவனோ:<br/><br/>கொடுவாளால் களிறுகளைக் கொன்றுபோட்டுக்<br/>குன்றுகளைப் பந்தாடுதல் போல் அவற்றை உருட்டிவிட்டுப்<br/><br/>படுகளத்தில் விளையாடி வினை முடிக்கின்றான் என்றும்,<br/>நெடுமரம் போல் பகைப்படை வீழ்கின்றதென்றும்,<br/><br/>அடுக்கடுக்காய்த் தித்திப்புச் செய்திகள் வந்திடவே<br/>அடக்கவொண்ணா ஆவலுடன் அது காணக் களம் புகுந்தேன்!<br/><br/>ஐயத்திற் கிடமேயில்லை வெற்றி நமக்கேதான்...<br/>ஆனால் ஒன்று; அங்கே என் மகனைத்தான் காணவில்லை!<br/><br/>குருதி மணம் கமழுகின்ற போர்க்களத்தில்<br/>குமுத மலர் மொட்டுக்களாய் முளைக்கும் தலைகள்!<br/><br/>வீழ்ந்துள்ள வீரர்தம் உடல்களோக கவிழ்ந்துவிட்ட படகுகளாம்!<br/>வெட்டுண்ட கைகால்கள் வேலையின்றி மிதக்கின்ற துடுப்புகளாம்!<br/><br/>சூழ்ந்துள்ள பிணக்குவியல் நடுவில் சென்றேன்.<br/>\"ஆழ்ந்துள்ளேன் கவலையினில்; அருமை மகனே! நீ<br/><br/>போர்க்களத்துப் பிணமாகி வாழ்கின்றாயா? அன்றி<br/>புறமுதுகிட்டோடிச் செத்துத் தொலைந்தாயா? எனக்கேட்டுத் துடித்திட்டேன்!<br/><br/>\"வாழ்கின்றேனம்மா வீரனாக!\" என்றொரு குரல்<br/>தாழ்கின்ற என் தலையை நிமிர்த்திற்றாங்கே!<br/><br/>குரல் வந்த திசைநோக்கிப் பறந்துபோனேன்! - \"உள்ள<br/>உரம் வாய்ந்த மகனே! நீ எங்கே?\" என்றேன்.<br/><br/>\"இங்கே\" என்றான்; கண்டு கொண்டேன்! ளவலம்புரிச்<br/>சங்கே!\" என்றவாறு தழுவிக்கொண்டேன்! கையில்;<br/><br/>தாங்கிப் போரிட்ட கேடயம் தரையில் கிடக்க; அதில்,<br/>தங்கமகன் மார்பில் அம்புதாங்கி வீழ்ந்து கிடந்தான்!<br/><br/>\"வெற்றியம்மா வெற்றி\" என்றான் - அவன்<br/>நெற்றியிலே முத்தமிட்டு, \"என் நித்திலமே!<br/><br/>போர்முனையில் உயிர்விட்டு மானம் காத்த தந்தைக்கு<br/>நேர்வழித் தோன்றலாய் வந்துதித்தாய் வாழ்க!\" என்றேன்!<br/><br/>\"ஊடுருவியிருக்கின்றதே மார்பில் அம்பு - வலி<br/>உனக்குத் தாங்காதே மகனே!\" என்றேன்; அழுதுவிட்டேன்!<br/><br/>\"உண்மையா அம்மா? உன் கண்ணால் கண்டு நீ எனக்கு;<br/>உரைக்கும் வரை நான் உணரவில்லையம்மா!\" என்றான்!<br/><br/>உயிர்விட்டான் மடிமீது! போய்விட்டான்;<br/>உயிரோடு எனைமட்டும் விட்டுவிட்டு!<br/><br/>ஊர்ப்பிள்ளைகளுடன் விளையாடிக்கொண்டு<br/>உண்பதற்குப் பிடிவாதம் பிடித்தபோது - நான்<br/><br/>ஓங்கிய கோல் கண்டு ஓடிய என் மகனா; மார்பில்<br/>தாங்கிய கணையுடனே தாயகத்தை மீட்கும் போரில்<br/><br/>தன்னுயிரைத் தருவதற்கும் துணிந்து நின்றான்!<br/>என்னுயிரே! என்மகனே! நீ வாழ்க என வாழ்த்துகின்றேன்!"))
        databank.append(DataModel(title: "தலைகேட்டான் தம்பி!", data: "கரும்பெடுத்துத் தமிழ் பிழிந்து - மலரில்<br/>சுரும்பெடுத்து வருகின்ற தேன் கலந்து<br/>அரும்பெடுத்துச் சிரிக்கின்ற மகளிர் கூட்டம்,<br/>\"எறும்பெடுத்துச் செல்கின்ற உணவு கூட<br/>எம்மன்னன் குமண வள்ளல் தந்த\" தென்று<br/>எழுச்சி நடை போடுகின்ற கொங்குநாடு.<br/><br/>தமிழ் பாடும் புலவரெல்லாம், குமணன்<br/>புகழ்பாடி வந்திடுவார் -<br/>இமிழ் கடலின் அலைபோலச் சொல் முழங்கும்.<br/>அமிழ்தமெனும் தமிழ்மொழியின் பண் முழங்கும்.<br/>கமழ்கின்ற கவிதை மலர்த் தோட்டம் - அன்பு<br/>தவழ்கின்ற நெஞ்சத்தின் கூட்டம்.<br/><br/>முத்திரைத் தமிழால் கவிதை யாத்திடும் புலவர் - பெருஞ்<br/>சித்திரனார் என்றொருவர் - பேரறிஞர்<br/>பத்தரை மாற்றுத் தங்கம்போல் பத்தினி -<br/>புத்திரச் செல்வம் - புலவரையீன்ற வயிறு - அவர்<br/>இத்தரையில் கண்ட சுகம் வறுமையே என்று<br/>மெத்த இளைத்திட்டார் - நித்த நித்தம் பட்டினியால்!<br/>பூப்போல வாடி வதங்கிடுவாள் துணைவி;<br/>புழுப்போலச் சுருண்டு படுத்திடுவாள் அன்னை;<br/>பூனைபோலப் பானை உருட்டிடுவான் பசியாலே பிள்ளை;<br/>கைபிசைந்து நின்றிருந்த கவிஞருக்குக் கொடையாலே<br/>கை சிவந்த குமண வள்ளல் நினைவுவரக், கிளம்பிவிட்டார்!<br/><br/>\" பாரிக்கும் காரிக்கும் பின்னர் கொடை வழங்க<br/>யாரிருப்பார் என நினைத்த நினைப்பறுத்து,<br/>வாரிக்கொள் நான் வழங்குகின்ற செல்வத்தை!<br/>மாரிக்குப் பெயர்தான் குமணனென்று மார்தட்டும் கொங்கு மன்னா!<br/>கோரிக்கை யொன்றுமக்கு! என் குடும்பம் வாழ்வதற்குப் பொன் வேண்டும்\"<br/>எனக் கேட்டார்! அள்ளித் தந்தான்! \"நான்<br/>வருகின்ற பாதையிலே வாழ்கின்றான் வெளிமான் என்று - அவன்<br/>தருகின்ற பொருள் வாங்க நடந்து சென்றேன்; அந்தோ மன்னா!<br/>அவனிறந்து ஆண்டொன்று ஆனதாலே - அவன் தம்பி<br/>இளவெளிமான் இருக்கின்றான் - கொடை வழங்க மறுக்கின்றான்.<br/>கொடிய பதில் உரைக்கின்றான்.<br/>வளங் கொட்டும் முதிரமலை ஆள்கின்ற காவலனே!<br/>தரங் கெட்ட இளவெளிமான் தருக்கடக்க<br/>ஆனை ஒன்று தரவேண்டும் - அதனை அவன்றன்<br/>காவல் மரத்தில் கட்டி வைத்துக்<br/>கடையனவன் தலைகுனியச் செய்ய வேண்டும்; தருவீர் \" என்றார். தந்து விட்டான்.<br/>வீர வெளிமானுக்கொரு தம்பி இளவெளிமான் போல்<br/>வேந்தன் குமணனுக்குமுண்டு ; இளங்குமணன் அவன் வேர்.<br/>\"அணிமணிகள் ஆனை சேனை அரண்மனைகள் அத்தனையும்; அள்ளி அள்ளிக்<br/>கவிமணிகள் வாழ்வதற்கே தந்து விட்டால்...<br/>துணிமணியும் இல்லாத ஆண்டி மடம் ஆகிவிடும் நமது நாடு -<br/>இனியுமிதை அனுமதிக்க முடியாதண்ணா!<br/>இப்போதே பிரித்திடுக என் பாகம்\" என்றான்.<br/><br/>\"கனிகள் உதிர் முதிரமலைத் தருக்கள் கண்டேன் - செங்<br/>காய்கள் உதிர் தம்பியுந்தன் வாயுங் கண்டேன்!<br/>அணிமணிகள் எவை வேண்டும் உனக்கு? அரண்மனையில் எது வேண்டும்?<br/>ஆனையென்ன, சேனையென்ன? அனைத்தையுமே எடுத்துக் கொள்!<br/>அண்ணனுக்குத் தம்பியென்ற அன்பை மட்டும் கொடுத்துவிடு\" எனச் சொல்லி<br/>அழுதுவிட்டான் குமண வள்ளல் ... தம்பி நெஞ்சில்<br/>விழுது விட்டு வளர்கின்ற பகை உணர்வோ அடங்கவில்லை!<br/>பழுதுபட்டு போய்விட்ட உள்ளங் கொண்டோன் - \"வானில்<br/>உழுது விட்டுப் போன செங்கதிரோன் திரும்புமுன்னே - என் நாட்டைத்<br/>தொழுது விட்டுக் காடேகு\" என ஆணையிட்டான்; குமணன் சென்று விட்டான்!<br/>மானினமும் தேன்சிட்டும் மயில் குயில் மாடப்புறாவும்<br/>மனித இனம் காட்டுகின்ற அன்பைவிடத் தூய அன்பை<br/>மன்னனிடம் காட்டியதால் மகிழ்ந்திருந்தான் - காட்டில்<br/>கன்னல் மொழித் தமிழ் வழங்கப் புலவரில்லை; நீங்கா<br/>இன்னல் அஃதொன்றே - வேறில்லை!<br/><br/>கொழுந்து விட்ட துரோகத்தின் விளைவாகக்<br/>கவர்ந்து விட்டான் செங்கோலைத் தன் கையில் - தம்பி!<br/>மகிழ்ந்தானா அந்த மட்டில்? மனத்தில்<br/>அழுந்திவிட்ட பகையுணர்வைக் கிளறிவிட்டார் கொடிய நண்பர்.<br/><br/>\"பொறுத்திருந்தேன் இதுநாள் மட்டும் - என்மேல்<br/>போர்தொடுக்க முனைந்து விட்டான் அண்ணன்.<br/>தடுத்திடாதீர்; தம்பியென்னை; அண்ணன் தலை<br/>அறுத்தெடுத்து வருபவர்க்குப் பத்தாயிரம் பொன் பரிசே!\" என்றான்.<br/><br/>\"தலை யறுப்பதோ? என்ன<br/>குலை யறுக்கும் செய்தி இது!<br/>அலை யடிக்கும் கடல்வெல்லும் பரந்த உள்ளம் - தமிழ்க்<br/>கலை வடிக்கும் புலவர்க்கெல்லாம் கொடை நீருற்று;<br/>முதிரமலை கரைந்து விடும் - முடிமன்னன் தலைகொய்தால் - எம்<br/>உதிரமெலாம் உறைந்து விடும்\" எனச் சொல்லிக்<br/>காரி உழிந்தனர் காவலன் காட்டிய பொற்கிழியை!<br/><br/>கோத்திடும் தமிழ்மலர் கொற்றவன் தோளில்<br/>சேர்த்திடும் நினைவுடன் புலவர் வந்தார் -<br/>சாத்தனார் அவர் பெயர் - குமணன்<br/>காட்டினில் இருப்பதை அறிந்து கவன்றார்<br/>பாலையில் சோலையைக் கண்டது போலும்<br/>மாலையில் தென்றலை மணந்தது போலும்<br/>காலையில் கவிஞரைக் கண்டான் குமணன்,<br/>ஓலையில் தமிழை உண்டான் மிகவே!<br/><br/>\"அந்தநாள் வந்திலை அருங்கவிப் புலவோய்!<br/>இந்தநாள் வந்து நீ, நொந்தெனை அடைந்தாய்.<br/>தலைதனைக் கொண்டு போய்த் தம்பிகைக் கொடுத்து<br/>விலைதனைப் பெற்றுன் வறுமை நோய் களையே!\"<br/>என்றான் குமணன் எடுத்தான் வாளை!<br/><br/>தடுத்தார் புலவர் விடுத்தார் அடவி!<br/>\"அடுத்தார் பேச்சால் அழிந்த தம்பி<br/>எங்கே?\" என்றார் தொடுத்தார் கணைகள்!<br/><br/>\"தம்பியுடையான் படைக்கஞ்சான் என்றிருந்தான் அண்ணன்<br/>நம்பியவன் கழுத்தறுத்தாய்; தலையறுத்துத் தருபவர்க்குப்<br/>பொன்பரிசு தருவதாக வாக்களித்தாய்; அவன்<br/>தலையறுத்த வாள் இதுதான்<br/>தந்திடுவாய் பரிசை\" என்றார். அண்ணனின்<br/>வாள் கண்டான்; வாள்முனையில் செங்குருதி தனைக் கண்டான்.<br/>\"வீழ்ந்தாயோ அண்ணா!\" என்று வீறிட்டு அலறிவிட்டான்,<br/>விஷஉள்ளம் - வெறி உள்ளம் - அண்ணன் உயிர்தந்த செய்தியாலே<br/>வெடிவைத்த மலைபோலச் சிதறிற்று!<br/>வெள்ளையுள்ளம் தம்பியுள்ளம் வாய்விட்டுக் கதறிற்று!<br/>\"ஆவி தர நேரிடினும் அருந்தமிழ்க்கே தருவேனென்று<br/>காவியுடைக் குமணனவன் கானகத்தில் உரைத்திட்ட பொன்மொழியைப்<br/>பாவி நீ புரிந்திட்ட பாதகத்தால் யான் கற்றேன்,<br/>தாவி நீ ஓடிச் சென்று அன்னவனின் தாள்பணிந்தால்<br/>தமையன் உயிரல்ல; தமிழுக்கே உயிர் வந்து விடும்!<br/>தயங்காமல் வந்து விடு; தடவிப்பார் வாள்முனையை<br/>இரத்தமல்ல; செவ்வண்ணம்\" என்றுரைத்தார்.<br/><br/>ஏன் நிற்பான் இளங்குமணன்?<br/>தேன் விற்பார் தமைச் சுற்றும் ஈக்கள் போல்<br/>வான் தமிழால் கவிதை பின்னும் புலவர் கூட்டம்<br/>வட்டமிடும் குமண மன்னன் காலடியில்<br/>வணங்கி வீழ்ந்தான் - வள்ளல் தழுவிக் கொண்டான்!<br/>தமிழ்ச் சாத்தனார்க்குத் தலையைக் கொடையாய்த் தரச் சம்மதித்தோன்;<br/><br/>தம்பிக்கும் ஒரு மன்னிப்பைக் கொடையாய்த் தந்தான்.<br/>தம்பி, குடையைத் தந்தான்<br/>தனக்கினி வேண்டாமென்று ; அதனை அண்ணன்<br/>தமிழுக் கீந்தான்!"))
        
        databank.append(DataModel(title: "நீலமலை நீரினும் குளிர்ந்த நெஞ்சம்!", data: "பத்துப் பத்தாகப் பாவாணர்கள் வடித்த<br/>பாடல்களின் தொகுப்பாம்<br/>\"பதிற்றுப் பத்து\" எனும்<br/>பழமுதிர் சோலையில்,<br/>காலமெனும் கள்வன் புகுந்து<br/>கைவரிசை காட்டியதால்<br/>இரு பத்துக்கள் குறைந்து;<br/>எட்டு பத்துக்களே மிஞ்சியதாம்! அந்த<br/>எண்பது பாடல்கள் இயம்புவதென்னவாம்?<br/>உண்பதும் உறங்குவதுமே வாழ்க்கையல்ல;<br/>புண்படும் போர் முனை போந்து,<br/>வெற்றி அல்லது வீர மரணமெனப்<br/>புகழ்பட நிற்பதே வாழ்க்கையாகும்!<br/>இல்லார்க்கும் - தமிழில் வல்ல<br/>நல்லார்க்கும்; இல்லையெனாது<br/>எடுத்துச் செல்வீர் என,<br/>ஈவதே வாழ்க்கையாகும்!<br/>துணையாக வந்து கொஞ்சி,<br/>தோளில் தொத்திய கிளியுடனே<br/>இல்லறம் போற்றுவதே வாழ்க்கையாகும்!<br/><br/>இவ்வாறு<br/>செந்தமிழர் திருநாட்டுப் பெருமை சொல்லி,<br/>சேரர், சோழர், பாண்டியர் - அவரனைய<br/>தீரார் மிகு தமிழ்க் குலத்தார்தம்<br/>செப்பரிய வீரம் பாடும் இலக்கியங்கள்;<br/>ஒப்பற்ற நம் மண்ணின் சொத்து - அவற்றிலோர்<br/>ஒளியுமிழும் முத்துத்தான் பதிற்றுப் பத்து!<br/>சேர மன்னர் திறம் போற்றும்<br/>வீர வரலாற்றுப் பேழையிது!<br/>தொலைவிலிருந்து நுகர்ந்தாலும்<br/>தூய தமிழ் மணக்கும் தாழையிது!<br/><br/>இதில்,<br/>ஒன்பதாம் பத்தென்னும் மலர்க் கொத்து;<br/>ஓங்கு புகழ்ப் \"பெருங்குன்றூர்க் கிழார்\" எனும்<br/>ஒலி முரசக் கவியரசர் கட்டியது - அதன்<br/>உயர்மணத்தில், உயிர்த்தமிழின் உணர்வைக் காண்போம்!<br/><br/>\"குட்டுவன் இரும்பொறை\" யென்பானுக்கும் - அவன்<br/>கோப்பெருந்தேவி \"அந்துவஞ் செள்ளை\"க்கும்<br/>மட்டில்லாப் புகழ் விளக்கொன்று<br/>மகனாகப் பிறந்த தம்மா!<br/><br/>\"இளஞ்சேரல் இரும்பொறை\" எனப் பெயர்<br/>இட்டு அழைத்தனர்! இன்ப மகிழ்வில் திளைத்தனர்!<br/>அவனோ;<br/>வலிமையினைத் தோளில் ஏந்தி<br/>வாய்மையினை நெஞ்சில் ஏந்தி<br/>வஞ்சி மூதூர்க் கொடியி€க் கையில் ஏந்தி<br/>வஞ்சினங்கள் பலவற்றை நிறைவேற்றி முடித்தான்!<br/>\"விச்சிக்கோன்\" என்பானின் மலைநாட்டை வீழ்த்திப் பின்<br/>விற்கொடியின் நிழல் நின்று பழையன்மாறனையும் சோழனையும் தோற்கடித்தான்!<br/><br/>போர்க்களத்தில் புலிப் போத்தாய் - சிங்க ஏறாய் -<br/>புயல் காற்றாய்ச் சிறுகின்றான் சேரன்;<br/>பூப்பந்தாய் எதிரிகளின் தலைகளையே; இரு<br/>கரங் கொண்டு விளையாடும் அவன் நெஞ்ச<br/>உரங் கண்டு பெருங் குன்றூரார் பெரிதும் வியந்து<br/>பாசறை யொன்றில் அவனைச் சந்தித்தார்!<br/>மாசறு பொன்னாம் அவன் மனையாள்<br/>மன்னன் பிரிவால் மனமிக நொந்து<br/>இன்னலுறும் செய்தியினைச் சொன்னார்!<br/><br/>\"இருவாட்சியும் செண்பகமும் சூடி - முகத்தில்<br/>திருஆட்சி புரிகின்ற ஆய மகளிர் முன்னே<br/>தருநிழலின்றித் தவிக்கின்ற மான்போல் - உன்<br/>பருவமயில் படுகின்ற துயரால்; மேனியில்<br/>படர்கின்ற பசலை நிறம் மறைத்து<br/>சுடர்கின்ற பொன்னொளி போல் திகழும் - அவள்<br/>அடர்புருவ நெற்றியினைப் பற்றி<br/>இடர்தீர்ப்பாய் மன்னவனே\" என்றார்!<br/><br/>\"போர் முனை விட்டகன்று சேரமாதேவியினை<br/>மார்புறத் தழுவிமகிழ; நின்<br/>தேர் இன்றே புறப்படுதற்குப்<br/>புரவிகளைப் பூட்டிடுக\" என்றார்!<br/>\"நகை முகத்தழகியாம் நின்துணையை<br/>நாடி நீ நகர்வதாலே - இங்குள்ள<br/>பகை முகத்தில் எல்லாம் எல்லையில்லாப்<br/>பரவசத்தைக் காணலாமே!<br/>இளஞ்சேரல் இரும்பொறையின்<br/>கள முரசின் ஓசையின்றி<br/>சில நாட்களேனும் - தம்<br/>செவிப்பறைகள் ஓய்வு பெறுமே எனச்<br/>செப்பிடுவர் - அதனாலே நீ<br/>வஞ்சி மூதூர் சென்று, உன்<br/>வஞ்சியினைக் கண்டுவர - ஒருசேர<br/>ஒப்பிடுவர்!\" எனச் சொன்னார்!<br/><br/>\"ஆங்குநீ அரசியுடன் இருக்கும் நேரம்தான்<br/>ஈங்குள்ள பகையரசர்க்குத்<br/>தூங்கும் நேரம் என்பதாலே - அருள்கூர்ந்து<br/>தூங்கவிடு அவர்களைச் சற்று!\" என்றுரைத்தார்.<br/><br/>சேரனும், புலவர் உரை கேட்டுச்<br/>செருமுனையில் சிதறியோடும் எதிரிகட்குச்<br/>சிறிதளவு ஓய்வு தரச் சிரமசைத்தான்!<br/>ஆவிதனை இவன்பால் விடுத்துக்<br/>காவியுகு நீருடனே கலங்குகின்ற<br/>தேவியின் தாபம் தீர்க்கப் புறப்பட்டான்!<br/>மாவீரர் அனைவருமே அவன் ஆணை கேட்டுத்<br/>தாவியோடித் தழுவிக் கொண்டார் தங்கள்<br/>தங்கம் நிகர் காதலியர் தளிருடலை!<br/><br/>\"தமிழ்ப் புலவர் வாழ்க\" என்று அனைவருமே<br/>இமிழ்கடல் அலையென முழங்கலுற்றார்!<br/>சேரமா தேவியுடன் ஒன்றிப்போய் - \"இரவு<br/>நேரமா அதற்குள்ளே கழிந்த\"தென்று<br/>வீரமா மன்னன் இளஞ்சேரல் வியந்தெழுந்து<br/>ஆரம் புரளும் அழகி மார்பில்<br/>ஆயிரம் முத்தங்கள் பதித்தபோது - அவளும்<br/>பாயிரம் பாடி வாழ்த்துகின்றாள் புலவரையே!<br/>அவரால் தானே அவனும் வந்தான்<br/>அதனால் அவர்க்கே வாழ்த்து என்றாள்!<br/>ஆளன் முகத்தில் முகம் பதித்து - அவன்<br/>தோளை விரலால் மீட்டியசைத்து<br/>\"அன்பே ஒரு வரம் வேண்டு\"மென்றாள்.<br/><br/>\"என்பு தோல் கிழித்து எதிரியை விரட்டும்<br/>களத்திற்கினி மேல் போகாமல்<br/>காரிகை உன்னருகே இருத்தல் வேண்டுமெனும்<br/>அந்த வரம் தவிர - கண்ணே; நீ -<br/>எந்த வரம் கேட்டாலும் தருவேன்\" என்றான்!<br/><br/>\"இந்த ஒரு நாள் என்னுயிர் பிழைக்க நீவிர்<br/>வந்தது எதனால் என்பதை நினைத்தேன்<br/>பெருங்குன்றூர்க் கிழார் இல்லையேல் - இன்று<br/>அருந்தேனாய் இனித்த இன்பம் கிட்டாதன்றோ?<br/>அள்ளி வழங்கிடுக அவர்க்குப் பரிசை!\" என்றாள்.<br/><br/>\"இன்பமே! இதோ உனக்கோர் பரிசெ\"ன்று - அவள்<br/>இதழோடு இதழ் பதித்து; விடுவித்துக்கொண்டு<br/>அத்தாணி மண்டபத்திலமர்ந்து<br/>அழைத்து வரச் சொன்னான் புலவர் கிழாரை!<br/>வந்தவுடன் அவரை வணங்கி நின்று<br/>வழங்குகின்றேன் பரிசிலாகப் பெற்றுக் கொள்க;<br/>முப்பத்து ஈராயிரம் பொற்காசென்று<br/>முறுவலுடன் மொழிந்திட்டான் - உடன் பொழிந்திட்டான்!<br/>அவர் அறிந்து கொடுத்தது இப்பரிசென்றால் - அவர்<br/>அறியாமலே கொடுத்தது பல ஊரும் மனைகளுமாம்!<br/><br/>அதுகண்ட புலவர்;<br/>\"வெட்டுண்ட உடல்கள் வீழ்ந்தும் சாய்ந்தும்<br/>கொட்டிய குருதியால் போர்க்களம் முழுதும்<br/>புலால் நாற்றமெடுக்க;<br/>பொல்லாப் பகையை நொறுக்கும் கையில்<br/>கொடித்தேர் மீதமர்ந்து கொடும்பகை தீர்க்க மட்ம்<br/>படித்தான் சேரன் என்றிருந்தேன் - இல்லை! இல்லை*!<br/>படையெடுத்துப் பகைப்புலத்தின்மீது செல்லும் ஆர்வம் போலே<br/>கொடை கொடுத்துப் புலவர்தமை அரவணைக்கும் ஆர்வமுள்ளான்!<br/><br/>ஓடக்கோலுக்கும் எட்டாத ஆழமிகு நீரினிலே<br/>ஓடிக் குதித்தாடும் மகளிர் தம் காதணிகள்<br/>கழன்று வீழ்ந்து கைதவறிப் போனாலும் - மேலிருந்து<br/>காண்போர்க்குத் தெளிவாகத் தெரியு மளவு<br/>சந்தன மரங்கள்தமைச் சுமந்து கொண்டு<br/>சலசல வெனப் பாய்ந்து வரும்<br/>நீலமலைவானியாற்று தெள்ளிய நீரை விடக்<br/>கோலமிகு சேரனது குளிர்ந்த உள்ளம்; இனிது என்பேன்!\"<br/><br/>என்று பாடி ஏற்றிப் புகழ்ந்தார் - சேரனும்<br/>சென்று வருகின்றேன் மீண்டும் செருக்களமென்று<br/>விடைபெற்றுக் கிளம்பி விட்டான்! அவன்<br/>படைமுரசின் ஒலிகேட்டுப் பகைவர்களோ மீண்டும் பதைக்கலுற்றார்!"))
        
        databank.append(DataModel(title: "குக்கூ! என்றது கோழி", data: "\"மலர்ந்த தாமரைப் பூக்கள் இரண்டு<br/>மார்பினில் தலைகீழாய்க் கவிழ்ந்ததுவோ? அவற்றின்<br/>தண்டுகள், அடிவரை வெட்டுண்டு<br/>வண்டுகள் போலக் குந்தியதோ? - கருங்<br/>காம்பிரண்டு கொண்டுருண்ட கனிகுலுங்க<br/>கவர்ச்சிக்கோர் கொடுமுடியாய் நிற்கின்றாளே!<br/>சிலந்திவலை இழையெடுத்து நெய்திட்ட சேலை எழில்<br/>சிந்த அவளுடுத்தி எறிகின்றாள் விழிவேலை!<br/>வாழைத் தண்டெடுத்து வடிவமைத்த தொடையினிலே<br/>தாழை மலர் நிறமெடுத்துத் தடவியது யாரோ?<br/>சந்தன வண்ணத்து மெழுகு கொண்டு - இந்தச்<br/>சுந்தரச் சிற்பத்தைப் படைத்திட்ட இயற்கைக்கு,<br/>கோடிப்பொன் சிற்பத்தைப் பரிசாகக் கொட்டிக் கொடுத்தாலும் அப்பரிசு;<br/>மூடித்திறக்கும் அவள் இமையிரண்டின் அழகுக்கு ஈடாமோ?\"<br/><br/>ஆடிப் போனான் காதலன் வேட்கையினால் அவ்விளைஞன் - அவளோ,<br/>ஓடிப்போனாள்; ஒளிந்தொருவன் தன்னையே பார்த்தல் கண்டு!<br/>விண்மீன் வானத்தில் வீழ்கின்ற வேகத்தைக் கண்டுள்ளான்;<br/>தண்ணிலவு, தடால் எனப் பாய்ந்து மறைகின்ற காட்சியினைக்<br/>குன்றருவிப் பக்கமுள்ள பாறையின் பின்னிருந்து<br/>இன்றவன் கண்டான் நேரில்!<br/>உயிரையும் நினைவையும் அங்கு விடுத்துத் தன்<br/>உடலை மட்டும் ஏற்றிக்கொண்டான் தேரில்!<br/><br/>மறுநாளும் அவன் வந்தான் அருயோரம்;<br/>மறதியினால் விட்டுச் சென்ற தன்னுயிரைத் தேடி!<br/>உயிர் மட்டுமா கிடைத்ததங்கே - அந்த<br/>உயிர் போன்றாள் தரிசனமும் கிட்டியது!<br/>முதல்நாள் அனுபவத்தால் அந்த அழகு ரோஜா, தோழியெனும்<br/>முள்ஒன்றைப் பாதுகாப்புக்கு அழைத்து வந்திருந்தாள்!<br/>பக்கத்தில் காவலாக நிற்கின்ற தோழிதனைப்<br/>பார்த்த வீரன் முதலில் பயந்தான் எனினும்<br/>பக்குவமாய் அந்தப் பாங்கி மூலம்<br/>பாவையினைப் பெறலாமெனக் கணக்கிட்டான்.<br/><br/>தோழி வாழும் இடமறிந்து<br/>கோழி கூவுதற்கு முன் அவள் வீடு சென்றான்.<br/><br/>\"எதற்காக வந்தீர்?\" எனச் சற்று<br/>இடக்காகத் தோழி கேட்க; \"யாரும்<br/>பறிக்காத மலராம் உன் தலைவிதனை<br/>மறுக்காமல் அவள் பெற்றோர் எனக்களிக்க<br/>உன் துணையை வேண்டி வந்தேன்<br/>ஊண் உறக்கமின்றி அலைகின்றேன்\" என்றான்.<br/><br/>\"தாய்தந்தை விருப்பம் பின்னர் - முதலில்<br/>தளிர்க்கொடியாம் என் தலைவி விரும்பவில்லை உம்மை!<br/>தடம்பார்த்து நீர் திரும்பிச் செல்வதுதான் நன்மை! - வேறு<br/>இடம்பார்த்துக் கொள்வீர், காதல் வலைவீச!\" என்றாள்.<br/><br/>\"ஏன் என்னை வெறுக்கின்றாள் என்று<br/>கூன்பிறையை நெற்றியாகக் கொண்டவளைக் கேட்டறிய<br/>நான் சற்று அவளருகே செல்வதற்கு அனுமதிப்பாய் தோழி!<br/>மான் விழுங்கும் புலியல்ல நான்! நீ வாழியவே வாழி!\" என்றான் அவன்.<br/><br/>\"தனித்தொரு பெண் நீராடுமிடத்தில் குறும்பு செய்தல்<br/>தக்கதோர் பண்பாடன்று - இது தலைவியின் கருத்து\" என்றாள்.<br/><br/>\"பனித்துளியில் குளிக்கின்ற பூ ஒன்றைப் பார்ப்பது போல் பார்த்து விட்டேன்<br/>இனித் தவறு செய்யமாட்டேன் இரக்கம் கொள்ளச் சொல்க!\" என்றான்.<br/><br/>\"என்னடி பேச்சு அவரோடு\" எனச் சிறப்<br/>பொன்னடி சிவப்பேறப் பூமேல் நடந்து<br/>சின்ன இடை மின்னல் ஒளிப்பாவை;<br/>மன்னர் மகள் போல மமதையுடன் சென்றுவிட்டாள்!<br/><br/>கன்னியொருத்தியின் கடுமொழியும்<br/>காதல் வயப்பட்ட காளையர்க்குக்<br/>கற்கண்டாய் இனிக்குமன்றோ? - \"என்<br/>ஆவிதனைத் திருகிக்கொண்டு போகின்றாள்;<br/>பாவியெனக்காக உருகி அருள்பாலிக்கச் செய்\" என்றான்!<br/><br/>\"தலைவியொரு முடிவெடுத்தால்<br/>தலைபோக நேரிடினும் மாற்றமாட்டாள் - உமது<br/>தவம் பலிக்குமா - பார்ப்போம்\" என்று<br/>தாவிப் பாய்ந்தோடித் தலைவியுடன் சேர்ந்துகொண்டாள்!<br/><br/>அந்திமாலை வருமட்டும் அங்கேயே தனியாக நின்றான்;<br/>அதன்பின்னர் தோழி இல்லம் நாடிச் சென்றான்.<br/><br/>\"கொய்யாப்பழம் தின்ன வரும் அணிலே கேள்!<br/>மெய்யாகத்தான் கூறுகின்றேன், என் தலைவி மெய்தீண்டல்<br/>முயற்கொம்பே!\" என முடிவுரைத்தாள்!<br/><br/>கயல்நிகர்த்த விழியாளின் காலைத்தான் பிடிக்கவில்லை அவன்;<br/>\"தோழிப்பெண்ணே! தொல்புவியின் இன்பமெலாம்<br/>தொகுத்தளித்தாலும் தொடமாட்டேன்!<br/>ஆழிசூழ் உலகனைத்தும் ஆள்க என<br/>அனுமதித்தாலும் ஏற்கமாட்டேன்!<br/>இனி அவளில்லா வாழ்வெனக்கு,<br/>இலுப்பைப்பூ! எட்டிக்காய்! அறிக!\" என்றான்.<br/><br/>\"காலையென்றும் மாலையென்றும்<br/>காரிருள் என்றும் கடும்பகல் என்றும்<br/>பாராமல் எனை வருத்திப்<br/>\"பாங்கி விடுதூது\" நடத்தப் பார்க்கின்றீர்!<br/>வேறுமக்கு வேலையேதும் கிடையாதோ? இறுதியாகக்<br/>கூறுகின்றேன், போய்விடுக!\" என்றாள்."))
        databank.append(DataModel(title: "தகடூரான் தந்த கனி!", data: "அணிலின் வால் போல மீசை கொண்ட மன்னவன்<br/>அதியமான் நெடுமான் அஞ்சி யெனும் தென்னவன்<br/>ஆதிநாளில் தமிழ் மண்ணில் முதல் முதலாய்<br/>அருங் கரும்பைக் கொண்டுவந்து விளைச்சல் செய்தார்<br/>அதன் இனிப்பைத் தமிழில் கண்ட அவனின் முன்னோர்<br/>அதியர் குடிப்பிறந்ததாலே அதியமான் ஆனான்.<br/>தகடூரைத் தலைநகராய்க் கொண்டு - அந்தத்<br/>தமிழூரில் தலைநிமிர்ந்து ஆண்டான்.<br/>திண்தோளில் வலிமைதனைத் தேக்கிக்<br/>கண் அசைவில் பகைப்புலத்தைப் போக்கி<br/>மண்மீது புகழ் நிலைக்க மார்பில்<br/>புண் ஏந்தி வீழ்வதையே விரும்பி<br/>அஞ்சியெனப் பெயர் பூண்ட போதும்<br/>அஞ்சாத வரிப்புலியாய் மானமுடன் வாழ்ந்தான்<br/>அவ்வையாம் கவியரசி அவனுக்காக<br/>அணிமணிகள் தமிழால் செய்து<br/>அகமாரப் பூட்டி மகிழ்ந்தாள் - அவன்<br/>அறம் வியந்து திறம் புகழ்ந்தாள்.<br/>\"களம் புக எண்ணுகின்ற பகைவர் அறிக - எம்<br/>உளம் நிறை வீரன் ஒருவன் இருக்கின்றானய்யா!<br/>ஒரு நாளில் எட்டுத்தேர் செய்கின்ற தச்சன்<br/>ஒரு திங்கள் உழைத்தமைத்த ஒரு தேர்க்காலுக்கொப்ப<br/>உடல்வலியும் உரமும் பெற்றோன்<br/>அடலேறு அதியமான்; தெளிக!\" என்றே,<br/>அவ்வை பாடிய செய்யுள் இஃதே:-<br/><br/>\"களம்புக ஓம்புமின் தெவ்விர் போர் எதிர்ந்து<br/>எம்முளும் உளன்ஒரு பொருநன் வைகல்<br/>எண் தேர் செய்யும் தச்சன்<br/>திங்கள் வலித்த கால்அன் னோனே\"<br/><br/>அவ்வீரன் அதியமான் ஒரு நாட் காலை<br/>அடவியிற் சென்று அலைந்து திரிந்து<br/>விலங்குகள் மீது வேல்களைப் பொழிந்து<br/>வேட்டையை நிகழ்த்தி விரைந்து திரும்பினான்<br/>வழியில் மலையிடைப் பிளவொன்று கண்டு<br/>விழியினைச் செலுத்தி வியப்பாய் நோக்கினான்<br/>தனிமையிலே நெல்லி மரமொன்றுயர்ந்து<br/>தன் கிளையினில் சிறு இலைகளுக்கிடையே<br/>கனியொன்றே யொன்றைத் தாங்கி நின்றது.<br/>இனிப்பாக இருக்குமென்றும் அதனையுண்டால்<br/>இறப்பேதும் அணுகாதென்றும் இஃதுண்மையென்றும்<br/>தலைப்பாகைத் துறவியொருவர் புதுமையாகப் புகன்றவுடன்<br/>இமைப் போதும் தயங்காமல் அக்கனியைப் பறித்துக் கொண்டான்<br/><br/>தனைக் காக்க மன்னவனும் அக்கனியை உண்பான் என்று,<br/>தகடூர் வீரரெல்லாம் எண்ணிக் கொண்டு<br/>தடந்தோள் உயர்ந்திடவே தலைவன் பின் நடைபோட்டார்.<br/>மாளிகைக்கு வந்தவுடன் நெடுமானஞ்சி<br/>மாணிக்கத் தமிழ்பாடும் அவ்வையை அழைக்கலுற்றான்.<br/>\"மன்னவனே ஏன் அழைத்தாய் எனத் தெரியும்;<br/>மாற்றானாம் தொண்டைமானிடம் எனைத் தூதாக அனுப்பத்தானே?\"<br/><br/>என்று கேட்ட அவ்வை அம்மை<br/>சென்று வரத் தயார்தான் என்றாள் - தூதில்<br/>வென்று வருவதற்கு முன்னே - இதைத்<br/>தின்று சுவைத்திடுக என வேண்டி,<br/>நெல்லிக்கனியை நீட்டினான் வேந்தன் - நிலவில்<br/>அல்லி மலர்வது போல் முகமலர்ந்தாள் அவ்வை!<br/>சுவையான கனிதான் என்று சுவைத்துப் பார்த்து; அரசு<br/>அவைக் கவியான அவ்வை சொன்னாள்.<br/>\"அருமையாய்க் கிடைத்திட்ட இக்கனியுண்டால்<br/>ஆயுள் நீளுமென்று அடவியில் துறவி சொன்னார்<br/>அடியேனின் உயிர் அதிக நாள் இருந்தென்ன பயன்?<br/>அவ்வையின் ஆயுள் வளர்ந்தால் தமிழ் வளரும்<br/>தகடூரின் பெயர் உள்ளளவும் நான் வாழ்வேன்<br/>தமிழ் உள்ளளவும் வாழ்க அவ்வையே\" என்றான்.<br/>உண்மையை முனூகூட்டி உரைத்திட்டால்<br/>உண்ணாமல் மறுத்திடுவாள் அவ்வையென்று<br/>தான் வாழக் கருதாமல் தமிழ்த்தாய் வாழத்<br/>தகடூரான் தந்தகனி நெல்லிக்கனி;<br/>அதியமான் தமிழ் ஆர்வம் போற்றி<br/>அவ்வை தந்த நன்றிக்கனி; இந்த செய்யுட்கனி!"))
        databank.append(DataModel(title: "காடைப்போர் கண்டுவந்த கணவன்!", data: "கல்லொன்று நெற்றிப்பொட்டில் பட்டதுபோல்<br/>\"நில்\" என்று நேரிழையாள் எதிர்நின்று<br/>சொல் ஒன்றால் தாக்கியதால் திடுக்கிட்டுப்போய்<br/>வில்லம்பு விழியாளே; ஏனிந்த வெறுப்பென்று கேட்டான்!<br/>தோட்டத்து முல்லை; அவன் தேர்த்தட்டு மார்பில் படர்வதற்குத் தவித்திருக்க;<br/>வாட்டத்தின் உருவமாக அவளை மாற்றிப் பரத்தையர் இல்லம் ஏகி<br/>கொஞ்சு மொழிக் கோலவிழிக் கோகிலங்கள் கற்பித்த கலையனைத்தும் அவர்தம்<br/>பஞ்சணையில் படர்ந்தன்றோ பயின்று வருகின்றான்.<br/>நீருக்குள்ளிருந்த தாமரைக் கொடியெடுத்துக்<br/>கோடை வெயில் தகிக்கின்ற பாறையின்மேல் படுக்கவைத்தால்<br/>துள்ளி மகிழ்ந்து ஆடுமோ; அன்றித் துவண்டு வீழ்ந்து வாடுமோ?<br/>நெருப்புப்பூ எடுத்துக் குடும்ப மெழுகுவத்திக்குச் சூட்டிவிட்டு<br/>நித்த நித்தம் அவள் செத்துருகிச் சிர்குலையும்போது;<br/>மறுப்புக்கே இடமின்றி மஞ்சத்து விருந்தளித்த பரத்தையரின்<br/>சிரிப்புப் பூ அழகு கண்டு நன்றாகச் சிக்கிக்கொண்டான்!<br/>காலை முதல் மாலை வரை மனைபுதுக்கி, உணவு சமைத்து, உடை துவைத்துக்<br/>கண்மணியாம் மழலைதனைக் கல்வி பயில்வதற்கனுப்புகின்ற பணி முடித்துக்<br/>கால் கடுக்கக் காத்திருந்து கண்பூத்துப் போகுமட்டும்<br/>கணவனது வருகைக்காகக் காத்திருப்பாள் இங்கே!<br/>அவனோ;<br/>பலரிதழ் பட்டுப் பவளம் போல் சிவந்திருக்கும் பரத்தையர் இதழை,<br/>மலரிதழ் இதுதான் என்று மாறி மாறிச் சுவைத்திருப்பான்!<br/>அலர் தூற்றும் ஊர் என்றும் கவலைப்படாமல்<br/>புலர் பொழுதும் போம்பொழுதும் தெரியாமல் அவளைப் புசித்திருப்பான்!<br/>இல்லத்துக்கிளியோ சோகக் கூண்டினிலே - தன்<br/>இறைவன் இன்னும் வரவில்லையே என ஏங்கிக் கிடக்கும்;<br/>வெல்லத்தைப் பேச்சில் கலக்கும் பரத்தையரின் தூண்டிலிலே சிக்கி<br/>விருந்துண்ட மயக்கத்தில் அவன் தூங்கிக் கிடப்பான்!<br/>பாலும் தெளிதேனும் கூடத் தெவிட்டுமென்பான்; ஆனால் அந்தப்<br/>பரத்தையரின் பட்டு மேனிக் கட்டவிழா மொட்டுகளைத் தெவிட்டாத தேமாங்கனி யென்பான்!<br/>கன்னல் கசக்கும் என்பான்; கணிகையரின்<br/>கன்னம்தான் கனியிடையேறிய சுளையென்பான்!<br/>இன்னலுற்றே இல்லத்தில் குலமங்கையவள் விழிநீர் பெருக்கும்போது; அவன்<br/>மின்னல் கீற்றே எனப் பரத்தையரின் மெல்லிடை வீணையினை மீட்டி மகிழ்வான்!<br/>வாயிற்புறம் வந்து வந்து வருகின்றானா என<br/>வாழவந்த பத்தினிப் பெண் பார்த்துப் போவாள்; அந்நேரம் அவனோ-<br/>வாடகைப் பத்தினியின் வாயில் உள்ள தாம்பூலத்தைத்<br/>தன வாய்க்கு மாற்றச் சொல்லித் தவமிருப்பான்!<br/>எல்லையொன்றைக் கடந்துவிட்டால் - அது<br/>எதுவாயினும் கசந்துவிடும்!<br/>அஃதேபோல் அமுதம் என அவன் கருதி<br/>\"இஃதே என் பிறவிப் பயன்\" எனக் கொண்டிருந்த<br/>பரத்தையரின் பாசம் நேசமெல்லாம் - தன்<br/>கரத்தினிலே காசுள்ளவரையில்தான் எனக் கண்டவுடன்;<br/>மரங்கொத்திப் பறவைகளை விட்டகன்று;<br/>மாங்குயில் கூவும் தனது மனைநோக்கித் திரும்பி வந்தான்!<br/><br/>வந்தான் கணவன் எனத் தெரிந்தவுடன்<br/>நொந்த உள்ளம் ஒரு நொடியில் வேலாக மாற<br/>இந்த வழி; தவறி வந்தாயோ எனக் கேலி பேசிச்<br/>சொந்தமென்ன இனி வந்ததென \"நில்\" என்றாள்; சுடுசொல் வீசிவிட்டாள்!<br/><br/>பசுப்போலச் சாந்தமிகு பத்தினியாள்<br/>பாயும் புலி போலத் தோன்றியதால்<br/>பதைத்தான், திகைத்தான்;<br/>பக்குவமாய் அவளை வயப்படுத்தப்<br/>பாகுமொழி மிக உதிர்த்தான்!<br/><br/>கற்பனையில் மிதக்கின்றாய் கண்மணியே; அதனால்தான்<br/>கண்ணில் கனல் அள்ளிப் பொழிகின்றாய்! - நான்<br/>ஆணாக இருப்பதாலே ஆயிரம் ஐயம் உனக்கு<br/>வீணாகத் தேவையில்லை; இத்தனை நாளும் நான்<br/>காடைப் பறவைகளின் சண்டைக் காட்சிகளைத்தான் கண்ட வந்தேன் எனக்<br/>கற்கண்டப் பொடி தூவி அவளைக் கவிழ்க்கப் பார்த்தான்.<br/><br/>ஆனால் அவளோ;<br/>செந்தணலை இறைப்பது போல் சிரிப்பினூடே<br/>சினத்தைக் கொட்டி ஏளனமாய்;<br/><br/>காடைப் பறவைகளின் சண்டைதனை நீ<br/>கண்டவந்த விதமெல்லாம் கேள்வியுற்றேன்.<br/>ஆடை களைந்த அப்பறவைகள் உன்னை<br/>ஆரத்தழுவி அணைத்துப் போர் நடத்துதற்கு;<br/>மேடையிலே வீசுகின்ற மெல்லிய பூங்காற்றெனவே உன்<br/>மெய்த்தோழன் பாணன்; யாழ்கூட இசைத்திருப்பானே!<br/>அய்யோ பாவம்; காடைகளின் சண்டையினால்<br/>ஆறாத புண்களன்றோ உன் அங்கமெலாம் காணுகின்றேன்!<br/>புதுப் புதுக் காடைகள் உன்மீது மோதி<br/>போர் புரிந்த காரணத்தால் திரண்டுருண்ட நின்<br/>தோள்களெல்லாம்; உலராத காயங்களைத்<br/>தூக்கிச் சுமப்பதையும் தோகை நான் காணுகின்றேன்!<br/>வென்றிடுக என வேண்டினாலும்<br/>வெல்லாது அப்பறவையுடன்<br/>நெடுநேரம் பஞ்சணைப் போர் புரிந்ததாலே<br/>நின் முகத்திலுள்ள நினைவுச் சின்னமெல்லாம்<br/>நிலவின் முகத்தில் பரவிய களங்கம் போல்<br/>நிலைத்துவிட்ட காட்சியினைக் காணுகின்றேன்!<br/>இப்படி<br/>வெடித்துக் கிளம்பிய வார்த்தைகள் கேட்டு; அவன்<br/>வடித்துச் சொன்ன பொய்யுரை விடுத்து,<br/>அன்பே; அத்தனை உண்மையும் நீ எப்படியோ தெரிந்து கொண்டாய்;<br/>அறியாமல் செய்த குற்றத்தை மறந்திடுக பெண்ணே!<br/>அருள் பாலித்து என்னை ஏற்றிடுக கண்ணே - என<br/>அவளடி தொழாக் குறையாய் அழுது புலம்பினான்.<br/><br/>அவளோ;<br/>அணுவளவும் கோபத்தைக் குறைத்துக் கொள்ளாமல்<br/>அருள் செய்வதற்கு நான் யார் அய்யா? அன்பு பொங்கிட நீ<br/>அருள் செய்து பின்னர் அருளாது கைவிட்ட<br/>அழகுமிகு காடைகள் எல்லாம் - நீ மீண்டும்<br/>அருள்வதற்கு வருவாய் எனக் காத்திருக்கும்.<br/>அவற்றைக் கைவிடாமல் அங்கேயே செல்க என்றாள்;<br/>அவளும் உடன் உள்ளே சென்று விட்டாள்,<br/>அவன் பாவம்; அங்கே சிலையாக நின்றுவிட்டான்!"))
        
            databank.append(DataModel(title: "பதிலை நெருப்பாக உமிழ்ந்த பாவாணர்!", data: "வாள் தூங்கும் உறையைப் போல் மீசையுள்ள<br/>வேள் ஆய் அண்டிரன் எனும் குறுநில வேந்தன்<br/>பொதிய மலைப்பகுதி ஆய்குடியைத் தலைநகராய்க் கொண்டு<br/>போரென்று வருவோரை எலியாக்கி விரட்டுகின்ற புலியாவான்!<br/><br/>உறையூர் ஏணிச்சேரி முடிமோசியார், குட்டுவன் கீரனார்,<br/>துறையூர் ஓடைக்கிழார், பரணர், காரிக்கண்ணனார்,<br/>உமட்டூர் கிழார் மகனார் பரங்கொற்றனார் என்றெல்லாம்<br/>ஒரு புலவர் கூட்டமே இவன் புகழ் பாடிப் போற்றிற்று எனினும்<br/><br/>உடனிருந்து அவன் உளமுணர்ந்து உவகை பொங்கத்<br/>திடங் கொண்ட அவன் கைகளாலே தினந்தோறும்<br/>அள்ளிக் கொடுத்த வள்ளல் தன்மையினைப்<br/>புள்ளி மயிலின் இறகெடுத்துக் குழந்தையின் பூங்குழலில்<br/>செருகியது போல்<br/><br/>புறப்பாட்டாய் எழுதியெடுத்து தமிழ் முடியில் சொருகியவர்<br/>புலவர் பிரான் உறையூர் ஏணிச்சேரி முடிமோசியார்<br/>ஒருவர்தானே!<br/>ஆய் அண்டிரன் மலை வளம் குறித்து ஏணிச்சேரி<br/>முடமோசியார்<br/>அரியதோர் நிகழ்ச்சியினை அழகுறப் பாடுதல் காண்போம்.<br/>மலையோரம் ஊர்ப் பொதுவிடத்தில் இரவலர் கட்டியிருந்த<br/>மத்தளத்தை<br/>மந்தியொன்று பலாக்கனியென மயங்கித் தட்டிப்பார்க்க<br/>மரத்தின் கிளைகளில் வாழ் அன்னச் சேவல்;<br/>மருண்டு போய்ச் சிறகடித்து ஒலியெழுப்பிப் பறக்கின்ற;<br/>புகை போலும் முகில் தவழும் பொதிய மலை நோக்கி<br/>நகை முகத்து ஆரணங்குகள் ஆடச் செல்வரேயன்றி<br/>படை யெடுத்துப் பகைநாட்டார்; அண்டிரனைச் சூழ்வதற்குத்<br/>தலை வைத்தும் படுக்க மாட்டார் அவன் ஆளுகின்ற மலையிருக்கும் திக்கு நோக்கி!<br/>கொங்கரை எதிர்த்து அவர் கொடும் பகை அழித்துக்<br/>குடமலைக் கடற்கரை வரையில் விரட்டிய வீரனவன்!<br/><br/>ஏணிச்சேரியார் இயற்றிய பாடலில் - அவன் ஆண்ட நிலத்தின்<br/>எழிலையும் காணலாம்; எதிரிகள் பட்ட பாட்டையும் காணலாம்!<br/>தோற்றோடிய பகைவராம் கொங்கர் புறங்காட்டி<br/>மேற்றிசைக் கடலோர மலைகளிலே ஒளிந்தபோது<br/>எறிந்துவீட்டுப் போன வேல்களின் தொகையையும் மிஞ்சுமாம்;<br/>பரிசிலர்க்கு ஆய் அண்டிரன் பரிந்தளித்த யானைகளின் தொகை!<br/><br/>வியப்புற்ற, தமிழ் வித்தகராம் ஏணிச்சேரியார் கேட்கின்றார்;<br/>வேந்தே! உனது நாட்டு யானைகள் மட்டும் ஒரு தடவைக்குப்<br/>பத்து கன்று போடுமோ என்று!<br/>பாடி வந்த பாணர்க்கும், பாவலர்க்கும், பரிசிலர்க்கும்<br/>பரிசளித்த எப்படித்தான் இருந்ததோ அத்தனை யானையென<br/>அதிசயத்தால் மெய்மறந்து கவிதை தீட்டி<br/>ஆய் அண்டிரனைப் புகழ்ந்த பாவேந்தர் முடிமோசியார்;<br/>நாட்டு வளம் பாடுவதில் நற்புலமை நாவலராய்த் திகழ்ந்தார் எனினும்<br/><br/>காட்டு வளம் காண்பதற்கு ஒரு நாள் சென்றாராம்!<br/>வழி நெடுக அவர் கண்ட காட்சி; அவர்<br/>விழிகளையே அவர் நம்ப முடியாமற் செய்ததுவாம்!<br/>கூட்டங் கூட்டமாய் யானைகள் பெரிதும் சிறிதும்<br/>வாட்டமின்றி வளர்ந்தோங்கிய மரங்களின் நிழலில்<br/>நூற்றுக்கணக்கில் நின்றதைப் பார்த்து<br/>ஆற்றுப் பெருக்கென அருந்தமிழ்க் கவிதை யாத்தார் ஆங்கே!<br/><br/>பாடுவோர்தான் ஆய் அண்டிரனை நாடிப்<br/>பரிசாகப் பன்னூறு யானைகளைப் பெற்றிடுவர்; இந்தக்<br/>காடுகளும் ஒருவேளை; கவிதை கற்றுப்பரிசு பெற<br/>நீடுபுகழ் ஆய் அண்டிரனைத் தேடி வந்து - இந்தக்<br/>களிறுகளைப் பரிசாகப் பெற்று வந்து, நல்ல<br/>குளிர் தருக்கள் நிழல் தனிலே குவித்து வைத்தனவோ?<br/><br/>கவிநயம், கவின் தமிழ் நயம் கற்கண்டாம் - அதிலும்<br/>புவியாளும் ஆய் அண்டிரனைப் புகழ்கின்ற பொருள் நயம்<br/>புதிய மணப் பூச்செண்டாம்!<br/>பனி சூழ் மலையாம் இமயம் வடதிசையில் நிற்கும்போது,<br/>கனி சூழ் மலையாம் ஆய்குடி தென்திசையில் நில்லாதாயின்<br/>கீழ் மேலாகி இப்பெரும் உலகம் தலை சாய்ந்து<br/>பாழ்பட்டுப் போகுமென முடமோசியார் பாடுகின்றார் எனின்;<br/>அது, ஆய் அண்டிரனின் கொடைச் சிறப்பை எண்ணி<br/>மதுவுண்ட வண்டு போல் மாக்கவிஞர் ஆனதினால்தானே!<br/><br/>வள்ளலுக் கெல்லாம் வள்ளலாய்த் திகழ்ந்தவனும்<br/>வான்மழை யொத்தவனுமான வேள் ஆய் அண்டிரன் குறித்து<br/>உள்ளத்தில் அழுக்கு கொண்ட ஒரு சிலரோ - புகழ்<br/>உயரத்தில் வீற்றிருந்த ஆய்குடி வேந்தன் தன்னை<br/>பள்ளத்தில் உருட்டுவதாய்க் கருதிக்கொண்டு - அவன்<br/>அள்ளித் தருவதெல்லாம் ஆதாயம் எதிர்பார்த்துத்தான் என்றும்<br/>இப்பிறப்பில் செய்யும் உதவியெலாம் மறுபிறப்பில் உதவுமென்ற<br/><br/>தன்னலத்தின் அடிப்படையில்தான் தருகின்றான் கொடையென்றும்<br/>நாக்கிருக்கும் காரணத்தால் நாலு பேர் பத்த பேர் கூடி<br/>நோக்கம் கற்பித்தார் ஆய் அண்டிரனின் அரவணைக்கும் போக்கிற்கு!<br/>இதுகேட்டுச் சினம் மிகக் கொண்ட முடமோசியார்,<br/>இழிதகை நோக்கம் கற்பித்தல் தீதென உரைத்து;<br/>இம்மையிற் செய்வது மறுமைக்கு ஆகுமெனும்<br/>அறவிலை வணிகனல்ல ஆய் அண்டிரன் என்று,<br/>திட்டவட்டமாய்த் தீட்டினார் பாடல் ஒன்று!<br/><br/>கொட்டிக் கொடுக்கும் பொருளை விலையாக்கி<br/>தட்டிப் பறித்திடலாம் மறுமையில் அறத்தையென்று<br/>தரந் தாழ்ந்த சிந்தை அணுவளவும் கொள்ளாத<br/>வேள் ஆய் அண்டிரனை வெறுப்பின் விளைவாலே<br/>தேள் போலக் கொட்ட நினையாதீர்!<br/>பயன் கருதி பரிசிலர்க்கும் இரவலர்க்கும்<br/>பசும்பொன்னும் அணியும் மணியும் யானைகளும்<br/>பரிசாகத் தருகின்ற எத்தனல்ல எமது மன்னனென்று<br/>பதிலை நெருப்பாக உமிழ்ந்த பாவாணர் முடமோசியார்<br/>வாழ்க! வாழ்க!"))
         databank.append(DataModel(title: "காடைப்போர் கண்டுவந்த கணவன்!", data: " கல்லொன்று நெற்றிப்பொட்டில் பட்டதுபோல்<br/>\"நில்\" என்று நேரிழையாள் எதிர்நின்று<br/>சொல் ஒன்றால் தாக்கியதால் திடுக்கிட்டுப்போய்<br/>வில்லம்பு விழியாளே; ஏனிந்த வெறுப்பென்று கேட்டான்!<br/>தோட்டத்து முல்லை; அவன் தேர்த்தட்டு மார்பில் படர்வதற்குத் தவித்திருக்க;<br/>வாட்டத்தின் உருவமாக அவளை மாற்றிப் பரத்தையர் இல்லம் ஏகி<br/>கொஞ்சு மொழிக் கோலவிழிக் கோகிலங்கள் கற்பித்த கலையனைத்தும் அவர்தம்<br/>பஞ்சணையில் படர்ந்தன்றோ பயின்று வருகின்றான்.<br/>நீருக்குள்ளிருந்த தாமரைக் கொடியெடுத்துக்<br/>கோடை வெயில் தகிக்கின்ற பாறையின்மேல் படுக்கவைத்தால்<br/>துள்ளி மகிழ்ந்து ஆடுமோ; அன்றித் துவண்டு வீழ்ந்து வாடுமோ?<br/>நெருப்புப்பூ எடுத்துக் குடும்ப மெழுகுவத்திக்குச் சூட்டிவிட்டு<br/>நித்த நித்தம் அவள் செத்துருகிச் சிர்குலையும்போது;<br/>மறுப்புக்கே இடமின்றி மஞ்சத்து விருந்தளித்த பரத்தையரின்<br/>சிரிப்புப் பூ அழகு கண்டு நன்றாகச் சிக்கிக்கொண்டான்!<br/>காலை முதல் மாலை வரை மனைபுதுக்கி, உணவு சமைத்து, உடை துவைத்துக்<br/>கண்மணியாம் மழலைதனைக் கல்வி பயில்வதற்கனுப்புகின்ற பணி முடித்துக்<br/>கால் கடுக்கக் காத்திருந்து கண்பூத்துப் போகுமட்டும்<br/>கணவனது வருகைக்காகக் காத்திருப்பாள் இங்கே!<br/>அவனோ;<br/>பலரிதழ் பட்டுப் பவளம் போல் சிவந்திருக்கும் பரத்தையர் இதழை,<br/>மலரிதழ் இதுதான் என்று மாறி மாறிச் சுவைத்திருப்பான்!<br/>அலர் தூற்றும் ஊர் என்றும் கவலைப்படாமல்<br/>புலர் பொழுதும் போம்பொழுதும் தெரியாமல் அவளைப் புசித்திருப்பான்!<br/>இல்லத்துக்கிளியோ சோகக் கூண்டினிலே - தன்<br/>இறைவன் இன்னும் வரவில்லையே என ஏங்கிக் கிடக்கும்;<br/>வெல்லத்தைப் பேச்சில் கலக்கும் பரத்தையரின் தூண்டிலிலே சிக்கி<br/>விருந்துண்ட மயக்கத்தில் அவன் தூங்கிக் கிடப்பான்!<br/>பாலும் தெளிதேனும் கூடத் தெவிட்டுமென்பான்; ஆனால் அந்தப்<br/>பரத்தையரின் பட்டு மேனிக் கட்டவிழா மொட்டுகளைத் தெவிட்டாத தேமாங்கனி யென்பான்!<br/>கன்னல் கசக்கும் என்பான்; கணிகையரின்<br/>கன்னம்தான் கனியிடையேறிய சுளையென்பான்!<br/>இன்னலுற்றே இல்லத்தில் குலமங்கையவள் விழிநீர் பெருக்கும்போது; அவன்<br/>மின்னல் கீற்றே எனப் பரத்தையரின் மெல்லிடை வீணையினை மீட்டி மகிழ்வான்!<br/>வாயிற்புறம் வந்து வந்து வருகின்றானா என<br/>வாழவந்த பத்தினிப் பெண் பார்த்துப் போவாள்; அந்நேரம் அவனோ-<br/>வாடகைப் பத்தினியின் வாயில் உள்ள தாம்பூலத்தைத்<br/>தன வாய்க்கு மாற்றச் சொல்லித் தவமிருப்பான்!<br/>எல்லையொன்றைக் கடந்துவிட்டால் - அது<br/>எதுவாயினும் கசந்துவிடும்!<br/>அஃதேபோல் அமுதம் என அவன் கருதி<br/>\"இஃதே என் பிறவிப் பயன்\" எனக் கொண்டிருந்த<br/>பரத்தையரின் பாசம் நேசமெல்லாம் - தன்<br/>கரத்தினிலே காசுள்ளவரையில்தான் எனக் கண்டவுடன்;<br/>மரங்கொத்திப் பறவைகளை விட்டகன்று;<br/>மாங்குயில் கூவும் தனது மனைநோக்கித் திரும்பி வந்தான்!<br/><br/>வந்தான் கணவன் எனத் தெரிந்தவுடன்<br/>நொந்த உள்ளம் ஒரு நொடியில் வேலாக மாற<br/>இந்த வழி; தவறி வந்தாயோ எனக் கேலி பேசிச்<br/>சொந்தமென்ன இனி வந்ததென \"நில்\" என்றாள்; சுடுசொல் வீசிவிட்டாள்!<br/><br/>பசுப்போலச் சாந்தமிகு பத்தினியாள்<br/>பாயும் புலி போலத் தோன்றியதால்<br/>பதைத்தான், திகைத்தான்;<br/>பக்குவமாய் அவளை வயப்படுத்தப்<br/>பாகுமொழி மிக உதிர்த்தான்!<br/><br/>கற்பனையில் மிதக்கின்றாய் கண்மணியே; அதனால்தான்<br/>கண்ணில் கனல் அள்ளிப் பொழிகின்றாய்! - நான்<br/>ஆணாக இருப்பதாலே ஆயிரம் ஐயம் உனக்கு<br/>வீணாகத் தேவையில்லை; இத்தனை நாளும் நான்<br/>காடைப் பறவைகளின் சண்டைக் காட்சிகளைத்தான் கண்ட வந்தேன் எனக்<br/>கற்கண்டப் பொடி தூவி அவளைக் கவிழ்க்கப் பார்த்தான்.<br/><br/>ஆனால் அவளோ;<br/>செந்தணலை இறைப்பது போல் சிரிப்பினூடே<br/>சினத்தைக் கொட்டி ஏளனமாய்;<br/><br/>காடைப் பறவைகளின் சண்டைதனை நீ<br/>கண்டவந்த விதமெல்லாம் கேள்வியுற்றேன்.<br/>ஆடை களைந்த அப்பறவைகள் உன்னை<br/>ஆரத்தழுவி அணைத்துப் போர் நடத்துதற்கு;<br/>மேடையிலே வீசுகின்ற மெல்லிய பூங்காற்றெனவே உன்<br/>மெய்த்தோழன் பாணன்; யாழ்கூட இசைத்திருப்பானே!<br/>அய்யோ பாவம்; காடைகளின் சண்டையினால்<br/>ஆறாத புண்களன்றோ உன் அங்கமெலாம் காணுகின்றேன்!<br/>புதுப் புதுக் காடைகள் உன்மீது மோதி<br/>போர் புரிந்த காரணத்தால் திரண்டுருண்ட நின்<br/>தோள்களெல்லாம்; உலராத காயங்களைத்<br/>தூக்கிச் சுமப்பதையும் தோகை நான் காணுகின்றேன்!<br/>வென்றிடுக என வேண்டினாலும்<br/>வெல்லாது அப்பறவையுடன்<br/>நெடுநேரம் பஞ்சணைப் போர் புரிந்ததாலே<br/>நின் முகத்திலுள்ள நினைவுச் சின்னமெல்லாம்<br/>நிலவின் முகத்தில் பரவிய களங்கம் போல்<br/>நிலைத்துவிட்ட காட்சியினைக் காணுகின்றேன்!<br/>இப்படி<br/>வெடித்துக் கிளம்பிய வார்த்தைகள் கேட்டு; அவன்<br/>வடித்துச் சொன்ன பொய்யுரை விடுத்து,<br/>அன்பே; அத்தனை உண்மையும் நீ எப்படியோ தெரிந்து கொண்டாய்;<br/>அறியாமல் செய்த குற்றத்தை மறந்திடுக பெண்ணே!<br/>அருள் பாலித்து என்னை ஏற்றிடுக கண்ணே - என<br/>அவளடி தொழாக் குறையாய் அழுது புலம்பினான்.<br/><br/>அவளோ;<br/>அணுவளவும் கோபத்தைக் குறைத்துக் கொள்ளாமல்<br/>அருள் செய்வதற்கு நான் யார் அய்யா? அன்பு பொங்கிட நீ<br/>அருள் செய்து பின்னர் அருளாது கைவிட்ட<br/>அழகுமிகு காடைகள் எல்லாம் - நீ மீண்டும்<br/>அருள்வதற்கு வருவாய் எனக் காத்திருக்கும்.<br/>அவற்றைக் கைவிடாமல் அங்கேயே செல்க என்றாள்;<br/>அவளும் உடன் உள்ளே சென்று விட்டாள்,<br/>அவன் பாவம்; அங்கே சிலையாக நின்றுவிட்டான்!<br/><br/>"))
        
        databank.append(DataModel(title: "பதிலை நெருப்பாக உமிழ்ந்த பாவாணர்!", data: "வாள் தூங்கும் உறையைப் போல் மீசையுள்ள<br/>வேள் ஆய் அண்டிரன் எனும் குறுநில வேந்தன்<br/>பொதிய மலைப்பகுதி ஆய்குடியைத் தலைநகராய்க் கொண்டு<br/>போரென்று வருவோரை எலியாக்கி விரட்டுகின்ற புலியாவான்!<br/><br/>உறையூர் ஏணிச்சேரி முடிமோசியார், குட்டுவன் கீரனார்,<br/>துறையூர் ஓடைக்கிழார், பரணர், காரிக்கண்ணனார்,<br/>உமட்டூர் கிழார் மகனார் பரங்கொற்றனார் என்றெல்லாம்<br/>ஒரு புலவர் கூட்டமே இவன் புகழ் பாடிப் போற்றிற்று எனினும்<br/><br/>உடனிருந்து அவன் உளமுணர்ந்து உவகை பொங்கத்<br/>திடங் கொண்ட அவன் கைகளாலே தினந்தோறும்<br/>அள்ளிக் கொடுத்த வள்ளல் தன்மையினைப்<br/>புள்ளி மயிலின் இறகெடுத்துக் குழந்தையின் பூங்குழலில்<br/>செருகியது போல்<br/><br/>புறப்பாட்டாய் எழுதியெடுத்து தமிழ் முடியில் சொருகியவர்<br/>புலவர் பிரான் உறையூர் ஏணிச்சேரி முடிமோசியார்<br/>ஒருவர்தானே!<br/>ஆய் அண்டிரன் மலை வளம் குறித்து ஏணிச்சேரி<br/>முடமோசியார்<br/>அரியதோர் நிகழ்ச்சியினை அழகுறப் பாடுதல் காண்போம்.<br/>மலையோரம் ஊர்ப் பொதுவிடத்தில் இரவலர் கட்டியிருந்த<br/>மத்தளத்தை<br/>மந்தியொன்று பலாக்கனியென மயங்கித் தட்டிப்பார்க்க<br/>மரத்தின் கிளைகளில் வாழ் அன்னச் சேவல்;<br/>மருண்டு போய்ச் சிறகடித்து ஒலியெழுப்பிப் பறக்கின்ற;<br/>புகை போலும் முகில் தவழும் பொதிய மலை நோக்கி<br/>நகை முகத்து ஆரணங்குகள் ஆடச் செல்வரேயன்றி<br/>படை யெடுத்துப் பகைநாட்டார்; அண்டிரனைச் சூழ்வதற்குத்<br/>தலை வைத்தும் படுக்க மாட்டார் அவன் ஆளுகின்ற மலையிருக்கும் திக்கு நோக்கி!<br/>கொங்கரை எதிர்த்து அவர் கொடும் பகை அழித்துக்<br/>குடமலைக் கடற்கரை வரையில் விரட்டிய வீரனவன்!<br/><br/>ஏணிச்சேரியார் இயற்றிய பாடலில் - அவன் ஆண்ட நிலத்தின்<br/>எழிலையும் காணலாம்; எதிரிகள் பட்ட பாட்டையும் காணலாம்!<br/>தோற்றோடிய பகைவராம் கொங்கர் புறங்காட்டி<br/>மேற்றிசைக் கடலோர மலைகளிலே ஒளிந்தபோது<br/>எறிந்துவீட்டுப் போன வேல்களின் தொகையையும் மிஞ்சுமாம்;<br/>பரிசிலர்க்கு ஆய் அண்டிரன் பரிந்தளித்த யானைகளின் தொகை!<br/><br/>வியப்புற்ற, தமிழ் வித்தகராம் ஏணிச்சேரியார் கேட்கின்றார்;<br/>வேந்தே! உனது நாட்டு யானைகள் மட்டும் ஒரு தடவைக்குப்<br/>பத்து கன்று போடுமோ என்று!<br/>பாடி வந்த பாணர்க்கும், பாவலர்க்கும், பரிசிலர்க்கும்<br/>பரிசளித்த எப்படித்தான் இருந்ததோ அத்தனை யானையென<br/>அதிசயத்தால் மெய்மறந்து கவிதை தீட்டி<br/>ஆய் அண்டிரனைப் புகழ்ந்த பாவேந்தர் முடிமோசியார்;<br/>நாட்டு வளம் பாடுவதில் நற்புலமை நாவலராய்த் திகழ்ந்தார் எனினும்<br/><br/>காட்டு வளம் காண்பதற்கு ஒரு நாள் சென்றாராம்!<br/>வழி நெடுக அவர் கண்ட காட்சி; அவர்<br/>விழிகளையே அவர் நம்ப முடியாமற் செய்ததுவாம்!<br/>கூட்டங் கூட்டமாய் யானைகள் பெரிதும் சிறிதும்<br/>வாட்டமின்றி வளர்ந்தோங்கிய மரங்களின் நிழலில்<br/>நூற்றுக்கணக்கில் நின்றதைப் பார்த்து<br/>ஆற்றுப் பெருக்கென அருந்தமிழ்க் கவிதை யாத்தார் ஆங்கே!<br/><br/>பாடுவோர்தான் ஆய் அண்டிரனை நாடிப்<br/>பரிசாகப் பன்னூறு யானைகளைப் பெற்றிடுவர்; இந்தக்<br/>காடுகளும் ஒருவேளை; கவிதை கற்றுப்பரிசு பெற<br/>நீடுபுகழ் ஆய் அண்டிரனைத் தேடி வந்து - இந்தக்<br/>களிறுகளைப் பரிசாகப் பெற்று வந்து, நல்ல<br/>குளிர் தருக்கள் நிழல் தனிலே குவித்து வைத்தனவோ?<br/><br/>கவிநயம், கவின் தமிழ் நயம் கற்கண்டாம் - அதிலும்<br/>புவியாளும் ஆய் அண்டிரனைப் புகழ்கின்ற பொருள் நயம்<br/>புதிய மணப் பூச்செண்டாம்!<br/>பனி சூழ் மலையாம் இமயம் வடதிசையில் நிற்கும்போது,<br/>கனி சூழ் மலையாம் ஆய்குடி தென்திசையில் நில்லாதாயின்<br/>கீழ் மேலாகி இப்பெரும் உலகம் தலை சாய்ந்து<br/>பாழ்பட்டுப் போகுமென முடமோசியார் பாடுகின்றார் எனின்;<br/>அது, ஆய் அண்டிரனின் கொடைச் சிறப்பை எண்ணி<br/>மதுவுண்ட வண்டு போல் மாக்கவிஞர் ஆனதினால்தானே!<br/><br/>வள்ளலுக் கெல்லாம் வள்ளலாய்த் திகழ்ந்தவனும்<br/>வான்மழை யொத்தவனுமான வேள் ஆய் அண்டிரன் குறித்து<br/>உள்ளத்தில் அழுக்கு கொண்ட ஒரு சிலரோ - புகழ்<br/>உயரத்தில் வீற்றிருந்த ஆய்குடி வேந்தன் தன்னை<br/>பள்ளத்தில் உருட்டுவதாய்க் கருதிக்கொண்டு - அவன்<br/>அள்ளித் தருவதெல்லாம் ஆதாயம் எதிர்பார்த்துத்தான் என்றும்<br/>இப்பிறப்பில் செய்யும் உதவியெலாம் மறுபிறப்பில் உதவுமென்ற<br/><br/>தன்னலத்தின் அடிப்படையில்தான் தருகின்றான் கொடையென்றும்<br/>நாக்கிருக்கும் காரணத்தால் நாலு பேர் பத்த பேர் கூடி<br/>நோக்கம் கற்பித்தார் ஆய் அண்டிரனின் அரவணைக்கும் போக்கிற்கு!<br/>இதுகேட்டுச் சினம் மிகக் கொண்ட முடமோசியார்,<br/>இழிதகை நோக்கம் கற்பித்தல் தீதென உரைத்து;<br/>இம்மையிற் செய்வது மறுமைக்கு ஆகுமெனும்<br/>அறவிலை வணிகனல்ல ஆய் அண்டிரன் என்று,<br/>திட்டவட்டமாய்த் தீட்டினார் பாடல் ஒன்று!<br/><br/>கொட்டிக் கொடுக்கும் பொருளை விலையாக்கி<br/>தட்டிப் பறித்திடலாம் மறுமையில் அறத்தையென்று<br/>தரந் தாழ்ந்த சிந்தை அணுவளவும் கொள்ளாத<br/>வேள் ஆய் அண்டிரனை வெறுப்பின் விளைவாலே<br/>தேள் போலக் கொட்ட நினையாதீர்!<br/>பயன் கருதி பரிசிலர்க்கும் இரவலர்க்கும்<br/>பசும்பொன்னும் அணியும் மணியும் யானைகளும்<br/>பரிசாகத் தருகின்ற எத்தனல்ல எமது மன்னனென்று<br/>பதிலை நெருப்பாக உமிழ்ந்த பாவாணர் முடமோசியார்<br/>வாழ்க! வாழ்க!"))
       
        databank.append(DataModel(title: "செவிலி தேடிய சித்திரப் பாவை!", data: "பால் நிலவு பாலையிலே காய்வது போல்<br/>பருவக் கிளிநான் பயனற்றுப் போய்விடவோ?<br/>வேல் தூக்கி விண்ணதிர வீசுகின்ற கண்ணாளா - சற்று<br/>விழிதூக்கி என்நிலையை நோக்கிடுக!<br/><br/>கண்ணெல்லாம் குளமாக நின்கின்றேன் - ஒரு<br/>பெண்ணென்றால் பேய்கூட இரங்குமாமே!<br/><br/>என்னதான் எனக்கு விடை அளிக்கின்றாய்? - அன்றனக்கு<br/>கன்னலாகச் சுவைத்தநான் இன்று கசந்ததுதான் - ஏன்; சொல்வாய்!\"<br/><br/>சேல் நிகர்த்த விழியிரண்டைக் கெஞ்சவிட்டு - அவன்<br/>கால் பிடித்துக்கொண்டவாறு கதறியழுத<br/><br/>நூலிடைப் பெண்ணாளைத் தழுவி எடுத்து - அவன்<br/>ஆலிலை வயிற்றினிலே தடவிக் கொடுத்து<br/><br/>\"அடி பேதாய்; கலங்காதே - ஆருயிரே! அணிரதமே!<br/>நொடிப்போதும் உனைப்பிரிவதற்கு முடியாதென்னால்!<br/><br/>இடி இடித்தாற்போல் உன் பெற்றோர் கூச்சலிட்டு<br/>தடை விதிக்கின்றரே நம் உறவுக்கு - அதனால்தான்<br/><br/>தயங்குகின்றேன் தளிர்க்கொடியே! தங்கமே!<br/>மயங்குகின்றேன் என் செய்வது என்றறியாமல்!\"<br/><br/>காதலன் சொற்கேட்டுக் காரிகையும் - அவனைக்<br/>கட்டிப்பிடித்து மார்பகத்தால் முட்டித்தழுவி; - \" என் இதழ்தூ<br/><br/>தேன் கேட்டுத் தருவதற்குள் எடுத்துக்கொள்வாயே; என் அன்பே!<br/>ஏன் கேட்டு வேண்டும் இனி என் பெற்றோரை?<br/><br/>ஆலையிற் கரும்பென அகப்பட்ட சிறை விடுத்து,<br/>பாலைவெளியில் உன்னோடு பறந்திடுவேன்\" என மொழிந்தாள்.<br/><br/>நாளைப் பயணமெனக் காளை நவின்றவுடன் - அவன்<br/>தோளை வளைத்துத் தொத்திக்கொண்டு - புறப்பட்டாள்.<br/><br/>செய்தி அறிந்து செந்தீ மிதித்தார்கள் பெற்றோர்கள்!<br/>செவிலித்தாள் கிளம்பிட்டாள் அந்தச் சித்திரத்தைத் தேடுதற்கு!<br/><br/>முல்லையும் குறிஞ்சியும் திரிந்த நிலம்<br/>தொல்லைகள் குவிந்த பாலை நிலம் - அதனை<br/><br/>வில்லையொத்த புருவத்தினளும் - கருங்<br/>கல்லையொத்த உருவத்தினலும்<br/><br/>கடந்திருக்க வேண்டுமெனக் கருதியதால்<br/>கடுகி நடந்தாள் கவலையுடன் செவிலித்தாய்.<br/><br/>அங்கே,<br/><br/>தோளில் தாங்கும் தண்டுகளில்<br/>தொங்குகின்ற உறிகளுடன்<br/><br/>தூய நெஞ்சப் பெரியோர்கள்<br/>தொலையாப் பயணம் நடத்துகின்றார்.<br/><br/>இறை நினைவகலா இரவலோர் அவரை<br/>இடை வழிமறித்து செவிலி நின்றாள்.<br/><br/>\" அந்தணர் எனப்படும் அறநெறியாளரே! இந்தச்<br/>செந்தழற் பாலையில் எம் செல்வத்திருமகள்;<br/><br/>கன்றெனத் துள்ளும் கட்டழகு இளைஞனுடன்<br/>அன்றில் புள்ளெனச் சென்றது கண்டீரோ?<br/><br/>களவியல் வாழ்க்கை நடத்திக் களித்தனர் - இன்றோ<br/>உலகியல் அனைத்தும் துய்க்கத் துணிந்தனர்.<br/><br/>வளமிகு நெஞ்சப் பெரியீர்! நல்லீர்!<br/>வழியினில் அவர்களைக் கண்டீரோ சொல்வீர்!\"<br/><br/>ஆவல் பீறிடக் கேட்ட அவளிடம் - \"உமது<br/>காவல் மீறியக் கன்னியைக் கண்டோம் அம்மா!<br/><br/>சேவல் நிகர்த்த செந்தமிழ் வீரனை - அந்தச்<br/>செந்தாமரை அழகி சேர்ந்தது சிறப்பே!<br/><br/>சென்றவரை உளமார வாழ்த்தி அனுப்பாமல் - நீயும்<br/>குன்று, மலை ஏறிவந்து தேடுதல் வியப்பே!<br/><br/>ஒன்று கேள்!<br/><br/>மலையிடைப் பிறந்த சந்தனமெனினும் - அது<br/>மணத்தை அளிப்பது, பூசி மகிழ்பவர்க்கன்றோ!<br/><br/>மலைக்குத்தான் அது என்றுமே உரிமையெனில் - அதன்<br/>மணத்திற்குத்தான் மதிப்பேது தாயே!<br/><br/>முத்து பிறப்பது கடலில் எனினும் - அதன்<br/>புத்தொளி சிறப்பது அணிவோர் உடலில்!<br/><br/>ஆழ்கடலுக்கே அஃது உரிமை எனில்<br/>அந்தக் கடலுக்கும் முத்துக்கும் பெருமைதான் என்னே?<br/><br/>இசை பிறப்பது யாழிலே எனினும் - அதனை<br/>இசைத்திடும்போதே இன்பம் பொங்கிடும்!<br/><br/>யாழுக்கு மட்டும் இசை உரிமையெனில்<br/>யாழுக்கு அதனால் விளையும் பயன்தான் யாது?<br/><br/>பெற்றோர்க்குத்தான் பெண் உரிமையெனில்<br/>கற்றறிந்தவளே! ஒன்றறிந்திடுக!<br/><br/>மணந்திடாத சந்தனம் போல்<br/>அணிந்திடாத முத்தைப் போல்<br/><br/>இசைத்திடாத யாழைப் போல்<br/>இருந்திடவோ உமது பெண்ணும்?<br/><br/>எனவே;<br/><br/>கருத்தொருமித்த காதலனுடன்<br/>கற்புடை மங்கை வாழ்ந்திடவே - நீ<br/><br/>இதய வாழ்த்தை ஒலித்துவிட்டு<br/>இக்கணமே திரும்பிடுக!\"<br/><br/>வாய்மை நிறை வழிப்போக்கர் சொற்கேட்டு<br/>தாய்மை உளம் மிகத் தெளிந்ததாலே<br/><br/>கோலமகள் தேர்ந்தெடுத்த இல்வாழ்க்கை<br/>சோலைமலராய்ச் செழித்திடுக என வாழ்த்திவிட்டு<br/><br/>பாலை நிலப் பயணம்செய் பெரியோர்க்கும்<br/>பண்புடனே நன்றிகூறி வணங்கிச் சென்றாள்.<br/><br/>இதனைப்<br/>பாலைக்கலியில்<br/><br/>பெருங்கடுங்கோ எனும் பாவலர்<br/>பழச்சாறுத் தமிழில் தரும் பாங்கினைக் காண்க"))
        
        databank.append(DataModel(title: "மலர்மாரி பொழிகின்றேன்!", data: "உள்ளகம் சிவந்த கண்ணேம் வள்இதழ்<br/>ஒண்செங் காந்தள், ஆம்பல், அனிச்சம்,<br/>தண்கயக் குவளை, குறிஞ்சி, வெட்சி,<br/>செங்கொடு வேரி, தேமா, மணிச்சிகை<br/>உரிதுநாறு அவிழ்தொத்து உந்தூழ், கூவிளம்,<br/>எரிபுரை எறுழம், சுள்ளி, கூவிரம்,<br/>வடவனம், வாகை, வான்பூங் குடசம்,<br/>எருவை, செருவிளை, மணிப்பூங் கருவிளை,<br/>பயினி, வானி, பல் இணர்க்காயா,<br/>விரிமலர் ஆவிரை, வேரல், சூரல்,<br/>குறீஇப்பூளை, குறுநறுங்கண்ணி,<br/>குருகிலை, மருதம், விரி பூங்கோங்கம்,<br/>போங்கம், திலகம், தேங் கமழ் பாதிரி,<br/>செருந்தி, அதிரல், பெருந்தண் சண்பகம்,<br/>கரந்தை, குளவி, கடிகமழ் கலிமா,<br/>தில்லை, பாலை, கல் இவர் முல்லை,<br/>குல்லை, பிடவம், சிறுமா ரோடம்,<br/>வாழை, தளவம், முள்தாட்தாமரை,<br/>ஞாழல், மௌவல், நறுந்தண் கொகுடி,<br/>சேடல், செம்மல், சிறுசெங்குரலி,<br/>கோடல், கைதை, கொங்கு முதிர் நறுவழை,<br/>காஞ்சி, மணிக்குலைக் கள்கமழ் நெய்தல்,<br/>பாங்கர், மராஅம், பல்பூந் தணக்கம்,<br/>ஈங்கை, இலவம், தூங்கு இணர்க்கொன்றை,<br/>அரும்பு, அமர்ஆத்தி, நெடுங்கொடி அவரை,<br/>பகன்றை, பலாசம், பல்பூம் பிண்டி,<br/>வஞ்சி, பித்திகம், சிந்துவாரம்,<br/>தும்பை, துழாஅய், சுடர்ப்பூந் தோன்றி,<br/>நந்தி, நறவம், நறும் புன்னாகம்,<br/>பாரம், பீரம், பைங்குருக்கத்தி,<br/>ஆரம், காழ்வை, கடி இரும் புன்னை,<br/>நரந்தம், நாகம், நள்ளிருள் நாறி,<br/>மா இருங்குருந்தும், வேங்கையும், பிறவும்\"<br/><br/>(பத்துப்பாட்டு (குறிஞ்சிப் பாட்டு)<br/>(61 முதல் 95 முடிய)<br/>(பாடியவர் : கபிலர்)<br/><br/>பத்துப்பாட்டாம் குறிஞ்சிப் பாட்டில் - மலர்களைப்<br/>பட்டியல் போடும் பாங்கே; பாவையர்<br/><br/>சரம் தொடுத்ததுபோல் அமைந்திட்டதம்மா; இந்தச்<br/>சங்கத்தமிழின் பெருமையைச் சாற்றுதல் எளிதோ?<br/><br/><br/>சங்கப்புலவர்கள் பாடிய தமிழை<br/>குங்குமம் இதழில் நூறுகிழமை<br/><br/>எளிய நடையில் எழுதிட முனைந்து<br/>என்னாலியன்ற பணியினை முடித்தேன்<br/><br/>கடல் குளித்து முத்தெடுக்கும் போதும்,<br/>கனிகள் வெட்டிப் பொன் குவிக்கும்போதும்,<br/><br/>களிப்பு துள்ளும் உள்ளத்தைப் போலே - இந்தக்<br/>கலைஞனது உள்ளமுமே துள்ளுவது உண்மை!<br/><br/>கருவூலப்பொருள்கூட அள்ள அள்ளக் குறையும்; - சங்கக்<br/>கவிப்பெரியோர்கள் கற்பனையோ அகழ்கின்ற ஊற்றாக நிறையும்!<br/><br/>பழம்புலவர்பெருமக்கள்; தமிழர் வீரம், காதல்,<br/>பண்பாடு போற்றுகின்ற வாழ்க்கை நெறிமுறைகள்;<br/><br/>பாட்டாலே தொகுத்தளித்து; நம்மைப் பிற<br/>நாட்டாரும் போற்றுகின்ற செயல் புரிந்தார்!<br/><br/>ஏடுகளாய் எழுத்துக்களாய் வாழ்கின்ற - அந்தப்<br/>பீடுநிறைப் பெரியோர்க்கு வணக்கம் சொல்லி;<br/><br/>காடுகளில் மலைச்சோலைகளில் கபிலப்புலவன்<br/>கண்டறிந்து குறிஞ்சிப்பாட்டில் கோடிட்டுக்காட்டியுள்ள;<br/><br/>மலரையெல்லாம் பறித்தெடுத்து என் மனம் பறித்த<br/>மாத்தமிழ்ச் சுவடிகள் மேல் மாரியெனப் பொழிந்திடுவேன்!"))
        
        
        databank.append(DataModel(title: "நடுக்கத்தில் கண்ட நயம்!", data: "வெல்லம்போல் பரத்தையின் உடலைச் சுவைத்து;<br/>இல்லம் ஒன்றிருப்பதையும்; அங்கே தனக்காகக்<br/><br/>கள்ளமில்லாப் பெண்டொருத்தி கண்ணீர் உகுத்துக்<br/>கவலை மிகக் கொண்டிளைத்துத் தேய்வதையும்;<br/><br/>எண்ணிப்பார்த்திடவே இமைப்பொழுது நேரமின்றி<br/>கண்ணிக்குள் அகப்பட்ட பறவைபோல் காளையவன்<br/><br/>கலவியின்பக் கலையனைத்தும் கண்டு கண்டு;<br/>காமத்துப்பால் பருகிக்கிடந்தான் மொண்டு மொண்டு!<br/><br/>இன்பத்துப்பாலுக்கென இன்னும் சில குறள் எழுத<br/>இனியொரு முறை வள்ளுவனும் பிறந்து வரவேண்டுமென;<br/><br/>இனிதினிதாய்ப் புதிது புதிதாய் இருவரும் புதையல் தோண்டி எடுத்தனர்!<br/>ஈருடல் ஓருடலாய் இணைந்து புரண்டனர்; இதழால் நனைந்து; பிணைந்து உருண்டனர்!<br/><br/>தெவிட்டியதோ தேன்? அல்லால்; தெளிந்ததுவோ இதயவான்?<br/>தேரேறிச் சென்றுவிட்டான் தன் தேவியினைத் தேற்றி வாழ!<br/><br/>இழந்துவிட்ட பொருளென்று வலியவந்து;<br/>இருகையில் ஏறிக்கொண்ட விந்தைபோல,<br/><br/>மறந்துவிட்டான் என்றிருந்த மணவாளன் மீண்டும்<br/>திறந்துவிட்ட கூண்டுகிளிபோல திரும்பி வந்தாலே;<br/><br/>பறந்துவிட்டதடி கவலையென்று தோழியின் தோள் பற்றிப்<br/>பத்தினியாள் மகிழ்ச்சியினல் விழுந்திடாமல் சாய்ந்து நின்றாள்!<br/><br/>தலைவியின் வீடு சேர்ந்த தலைவனை மீண்டும் காணத்<br/>தவித்திட்ட பரத்தையோ; தந்திரமொன்று செய்தாள்!<br/><br/>தலைவியின் தோழியினை வீட்டுக்க்ழைத்துத்<br/>தலைவனுடன் ஒட்டியுறவாடித் தான் வாழ்ந்தபோது;<br/><br/>நடந்த கதையொன்றைச் சொல்லி அந்த<br/>நகைச்சுவைத் துணுக்காகத் தனக்குள்ளே சிரித்துக் கொண்டாள்!<br/><br/>\"தோழி நீயொன்று அறிவாயா? தோகையெனது<br/>தோள்தொத்திக் காளையவன் சுகித்திருந்தபோது;<br/><br/>கொக்கின் மூக்கைப்போல் நீண்டிருக்கும்<br/>குளத்து ஆம்பல் மலர் குலுங்கும் ஊரான்;<br/><br/>கோபத்தில் ஒருநாள் கோதையென் மீது பாய்ந்து<br/>கூந்தல்தனைப் பற்றியிழுத்ததோடமையாமல்;<br/><br/>கைவளையலையும் கழற்றுவதற்கு முனைந்து;<br/>கடுகடுத்த முகத்துடனே குதித்தோடி வந்தான்!<br/><br/>ஆத்திரம் கொள்ளாதே தலைவா! பொறுத்திடுக!<br/>அமைதி நீ கொள்ளாவிடில்; இக்கணமே இச்செய்தியினை;<br/><br/>உன் அருமைமிகு மனைவிக்கு அறிவிப்பேன் என நான்<br/>உரைத்ததுதான் தாமதம்; உடன் என்ன ஆனான் தலைவன் என்று;<br/><br/>உன்னிடம் சொல்வதற்கே இயலாமல் நான்<br/>ஒருத்தி மட்டும் தனியாகச் சிரிக்கின்றேன் ஓயாமல்!\" என்றாள்!<br/><br/>\"அப்படியென்னதான் நடந்தது? அதை நானறியச்<br/>செப்புக பெண்ணே\"யென்றாள், செந்தாழம்பூத் தோழி!<br/><br/>\"ஆநிரை கவர்வதற்கு வில்லேந்திப் போர்புரிந்து<br/>ஆர்த்தெழுந்த பகைவர்களை வென்றுகாட்டி,<br/><br/>மானமுரசு கொட்டி வாழுகின்ற நமது மன்னன்<br/>மலையமானின் அத்தாணி மண்டபத்தில்;<br/><br/>வேற்று நாட்டினின்றும் பரிசில்பெற வந்துள்ள - இசைக்<br/>கூத்தர்கள் கொட்டுகின்ற மத்தளத்தில்<br/><br/>அவர்தம் கைவிரல்கள் பட்டு \"மார்ச்சனை\" வைத்தபக்கம்<br/>அதிர்வது போல் அவன் உடல் முழுதும் நடுங்கியது கண்டேன்\" என்றாள்!<br/><br/>இச் செய்தியினைத் தோழி சென்று அவன் இல்லத்தரசியன் காதில் போட்டுவிட்டால்;<br/>\"சிச்சி! இவரும் ஒரு மனிதரா?\" என அவன் துணைவி வெறுப்பாளென்றும்<br/><br/>காய்ச்சிய பால்மீண்டும் காராம் பசுவின் மடி புகுந்தது போல்;<br/>கைவிட்டுச் சென்ற தலைவன்; துணைவியைக் கைவிட்டுத் தன்<br/>கட்டிலறை நாடி ஓடி வருவான் என்றும்;<br/><br/>கணக்கிட்டுக் கொண்டிருந்தாள் அக்கலையழகி; அவள்<br/>கனவு பலித்ததா இல்லையா என்பது பற்றி;<br/><br/>நற்றிணையில் பாடல் இயற்றிய பரணராம்<br/>நற்கவிஞர் சொல்லவேயில்லை; பிறகு நமக்கேன் கவலை?<br/><br/>எனினும்;<br/>\"மனைவிக்கு உரைத்திடவா?\" எனக் கேட்டவுடன்<br/>மத்தளத்தைக் கொட்டி முழக்குகையில் - அதன்<br/><br/>\"மார்ச்சனை\" வைத்த பக்கம் அதிர்வது போல்<br/>மாவீரன் நடுக்கமுற்றான் என்பதில்தான் என்ன நயம்! என்ன நயம்!<br/><br/>"))
        databank.append(DataModel(title: "உலைக் களத்து இரும்பும் ஒரு துளி நீரும்!", data: " சின்ன மருது, பெரிய மருது என சிவகெங்கைச் சிமை ஆட்;<br/>சிவந்தமண்ணின் வரலாற்றில் செங்குருதி மலராய்ப் பூத்த<br/>சிங்கநெஞ்ச மாவீரர் இருவருக்கும் சிலைகள் இருக்கின்ற,<br/>காளையார்கோயிலுக்குப் பழங்காலத் தமிழகத்தில்;<br/>\"கானப்பேர்\" என்று பெயர் உண்டு; இன்றும் அது,<br/>மானப்போர் தொடுக்கின்ற மறவர்பூமி - அங்கு;<br/>வேங்கை மார்பன் எனும் குறுநில வேல்வேந்தன்<br/>ஓங்குபுகழ் கொண்ட அரண் ஒன்று அமைத்து - அதனை<br/>வெற்றி கொள்ள யாரால் முடியுமென அறைகூவல் விடுத்திருந்தான்.<br/>அணுவைத் துளைத்து அதற்குள் நுழையவும் கூடும் - ஆனால் அந்த<br/>அரணுக்குள் நுழைதல் இயலாதென்று<br/>அடலேறு வீரரெல்லாம் திகைத்து நின்றார்!<br/>அப்போது பாண்டி நாட்டை ஆண்ட மன்னன் உக்கிரப்பெருவழுதி;<br/>அரிமாதான் போர்க்களத்தில்!<br/>ஆனைகளைத் தூக்கிப் பந்தாடும் பலமுடையான்!<br/>அவன் வாளுக்குத் தப்பிப் பகைவர் உயிர் வாழ்தல் அரிதரிது!<br/>ஆற்றல் பெருக்குடனே அறிவார்ந்த கலையுள்ளம் கொண்டதாலே;<br/>அகநானூறு பாடல்களைத் தொகுத்தளித்துப் பெருமை பெற்றேன்.<br/>அணையாச் சுடர் விளக்காம் திருக்குறளின் நலம்பாடும்<br/>அரங்கேற்றுவிழா அவன் முன்னிலையில்தான் நடந்ததென்பார்!<br/>ஒளவை ஒருநாள்; அவனும், சோழன் பெருநற்கிள்ளியும்<br/>அவர்களுடன் சேரமான் மாரிவெண்கோவும் வீற்றிருந்த காட்சி கண்டு,<br/><br/>அருந்தமிழ்க் கவியாரம் தொடுத்துச் சூட்டி,<br/>அங்கு மூவேந்தர் ஒற்றுமைக்கு வாழ்த்துரைத்தாள்.<br/><br/>கயற்கொடி கொண்ட மன்னன் உக்கிரப்பெருவழுதி<br/>கயல்விழி மங்கையொருத்திக்கும் காதலனும் - அந்தக்<br/>காதற்கிழத்தியுடன் ஒருநாள் கட்டிலறைக் காவியம் படித்தபோது;<br/>அவள்<br/>கடைவிழியோரம் வினாக்குறியைத் தொங்கவிட்டு,<br/>\"கண்ணாளா! எனக்கோர் ஐயம் போக்கிடுக\" என்றாள்.<br/><br/>\"என்ன கண்ணே; உனக்கு ஐயம்? அதோ நீந்துகின்ற<br/>அன்னத்தின் தூவிபோல் மென்மை உனக்கு<br/>எப்படி வந்ததென்று என்னைக் கேட்கின்றாயா? அல்லது<br/>வண்டுகள் கொண்டுவந்து கொட்டாமலே உன் வாயிதழில்<br/>மொண்டு குடிக்கின்றேனே தேன்; அது<br/>வந்தவிதம் தெரிந்துகொள்ள விரும்புகின்றாயா?<br/><br/>செந்தாமரையைப் பார்த்துச் செய்யப்பட்டது உன் முகமா? அல்லது<br/>செல்வமே! உன் முகம் பார்த்துத்தான் செந்தாமரை உருவானதோ என்ற ஐயமா?<br/><br/>செண்டு இரண்டும் தாங்காமல் - குளத்தில்<br/>கெண்டைக்குப் போட்ட தூண்டிலில் நெளிகின்ற புழுப்போல<br/>நெளியும் இடை நிலைக்குமா? அல்லது<br/>ஒளியுமிழும் உன் தந்தத் தொடைகள் அதனைத் தூக்கி நிறுத்துமா?<br/>உன் மனத்தில்<br/>விளைந்த ஐயமெல்லாம் இதுதானே?\" எனக் கேட்ட வழுதியின்முன்;<br/>வளைந்த கதிர்போல தலை நாணிக்கொண்டு அவள்;<br/>\"என்மீது கவிதையெழுதிப் பரிசுபெறக் கேள்வி எழுப்பவில்லை நான்!<br/>\"எயில்\" என்றால் இன்பத்தமிழில் பொருள் என்ன? சொல்லுங்கள்\" என்றாள்!<br/><br/>\"அட, மடமயிலே! இது தெரியாதா?<br/>அயலார் படை தடுக்க;<br/>அரண்கட்டுகின்றோமே தலைநகரைச் சுற்றி -<br/>அதற்குப் பெயர்தான் \"எயில்\" என்றான் மன்னன்!<br/><br/>\"எனக்கும் தெரியும் அது; ஆனாலும் ஒன்று!<br/>எவராலும் உடைக்க முடியாத \"எயில்\" ஒன்று இருக்கிறதாம்; - அதனை<br/><br/>இவராலும் நெருங்கொணாது என ஒரு வேந்தன்,<br/>இறுமாப்பு துள்ளப் பேசுவதை அறியீரோ?\" எனக் கேட்டு<br/>\"இவராலும்\" என்றபோது; வழுதியைச் சுட்டிக்காட்டிக் கட்டிக்கொண்டாள்!<br/><br/>\"காதுவழி எனக்கும் எட்டியது அச்செய்தி<br/>கானப்பேர் எனும் நகரில் கட்டியதாம் அந்த அரண்! - நமது<br/>அவைப்புலவர் ஐயூர் மூலங்கிழார் அது குறித்து<br/>அரியதோர் வர்ணனையை செய்துள்ளார்!<br/>அதை நான் உனக்குச் சொல்லுகின்றேன் கேள்;<br/>அரண்களின் வரிசையில் அதற்கு முதலிடமே பொருந்தும்!<br/>நிலத்தின் ஆழத்தையும் கடந்த நெடியதோர் அகழியும்,<br/>நீலவானை நிமிர்ந்து தொடுகின்ற நிலையில் உயர்மதிலும், அவ்வானில்<br/><br/>நிறைந்து கிடக்கும் விண்மீன்கள் போன்று<br/>விரைந்து செயலாற்றும் ஏவறை உச்சியும்,<br/>கதிரவனின் ஒளிபுகவும் இடமின்றி மரம் செறிந்த காவற்காடும்,<br/>கணக்கிலாச் சிறுசிறு துணை அரண்களும் சூழ்ந்ததே<br/>களிறுகள் ஆயிரம் முட்டினும் அசைந்திடாத கானப்பேர் எயில்!<br/><br/>அதனை நம் புலவர்;<br/>கவித் தமிழால் கூறக் கேட்டு நான் பலமுறையும்<br/>களிப்புற்றிருக்கின்றேன்\" என்றான் மன்னன்!<br/>\"பாண்டியர்க்கு நெடுந்தூரம் இருக்கின்ற பகையெல்லாம் தெரிகிறது;<br/>கூண்டிலிட்ட புலிபோல எதிரிகளைக் கொண்டு வருகின்றீர்; ஆயினும்<br/><br/>பக்கத்தில் கானப்பேர் எனும் நகரில் அரண் அமைத்து<br/>படைகொண்டு எவராலும் நெருங்கமுடியாதென்று<br/>உடைவாளை ஓங்கிக்கொண்டு<br/>ஊர் அதிர ஒலிக்கின்றான், வேங்கைமார்பன்!<br/>நமது வீரத்தை மறைமுகமாய்ப் பழித்தபின்னும்<br/>நமக்கென்ன என்றிருத்தல் நாணத்தகும் செயலன்றோ? - என் இதய<br/><br/>ஆழத்தின் அடியிற் கிடந்த பலநாள்<br/>ஐயத்தை இன்றிழுத்துப் போட்டுவிட்டேன்; மதங்கொண்ட<br/>வேழத்தைக்கூட எதிர்த்து மத்தகத்தைப் பிளந்தெறியும் மன்னருக்கு;<br/>வேங்கைமார்பன்ன கட்டியுள்ள மதில் சுவரென்ன; விந்திய மலைத் தொடரா? அவன்<br/>தோண்டியுள்ள அகழியோர் ஆழ்க்கடலாயினும்; என்<br/>பாண்டியர்க்குத் தாண்டும் தொலைவுதானே!<br/><br/>பின் எதற்காக<br/>அறைகூவல் விடுக்கின்ற வேங்கைமார்பன்<br/>ஆணவங்கண்டும் அடங்கிக் கிடக்கவேண்டும்?<br/>அரசர் இந்த ஐயத்தைப் போக்கிடுக - அதன்பிறகு<br/>சரசமாடிடலாம்\" என ஊடினாள்! பாண்டியனோ,<br/>எட்டிச் செல்ல முயன்றவளைக்<br/>கட்டியண€த்துக் கால்பின்னி நெய்தவாறு,<br/>\"காலைமலரட்டும் கானப்பேர் எயில் நோக்கிக்<br/>காலாட் களிறு புரவியுடன் தேர்ப்படை புறப்படக்<br/>கட்டளையிடுகின்றேன்; கவலையைவிடு! இப்போது தடுக்கின்ற<br/>கையினை எடு\" என்றான் - எடுத்துவிட்டாள்!<br/><br/>கண்ணிமைக்கும் பொழுதுக்குள் ஊடல் பறந்தது -<br/>கதிர்விரியும் நேரம் வரை கூடல் தொடர்ந்தது!<br/><br/>மறுநாள்<br/>வாக்குறுதி தந்தவாறு பாண்டிமாதேவியின்<br/>நோக்கு நிறைவேறக் கிளம்பியது படைவரிசை!<br/>கானப்பேர்எயில் என்னம் காவல்நிறை அரணை மோதி,<br/>கடுமைப்போர் நடத்திப்பின்னர் முற்றுகையை முடித்துவிட்டான்.<br/><br/>உக்கிரப்பெருவழுதியின் உறுதிமிகு தாக்குதலால்<br/>உருக்குலைந்து போயிற்று வேங்கைமார்பன் கோட்டைக்காவல்!<br/><br/>நெற்றித் திலகமிட்டு வழியனுப்பிவைத்த அரசிக்கு,<br/>வெற்றிச்சேதி சொல்வதற்குப் பாண்டிமன்னன் திரும்பிவந்தான்.<br/>குத்துண்டு புண்பட்ட கோவேந்தன் மார்பகத்தில்<br/>ஒத்தடங்கள் ஓய்வின்றிக் கொடுத்திட்டாள்; உடல் மேட்டால்!<br/>ரத்தம் கசிந்துருகும் விழுப்புண்கள் எல்லாம் - அவள்<br/>முத்தங்கள் பெற்று முழுமையாக ஆறினவே!<br/><br/>சிலநாள் சென்று<br/><br/>ஐயூர் மூலங்கிழார் பாண்டியன் அவைக்கு வந்தார்.<br/>அரசே! மதியாத பகைவரெல்லாம் உமை மதித்துப் புகழ்கின்ற பெருமை என்னே!<br/>களம் சென்றால் வெற்றியன்றி வேறொன்று அறியாத வேந்தே! இன்றுமுதல் உம்மை;<br/>\"கானப்பேர்எயில் கடந்த உக்கிரப் பெருவழுதி\" என<br/>காவியங்கள் பாடட்டும்! கவியரங்கம் புகழட்டும்!<br/>கதிர் உள்ளளவும் அப்பெயரே நிலைக்கட்டும்,\" எனப்புகன்றார்.<br/>\"நன்றி புலவரே!\" எனப் பணிந்த வேந்தன்; அவரைப்பார்த்து - இப்போரில்<br/>குன்றிவிட்ட வேங்கைமார்பன், கோபக்கனல் உமிழ<br/>கானப்பேர்எயிலை மீட்பதற்கு மீண்டும்<br/>களம் அமைக்கப்போகின்றானா? எனக்கேட்டான்.<br/>அதற்கு ஐயூர் மூலங்கிழார் சொன்ன பதில்<br/>அரியதோர் உவமையினை அன்னைத்தமிழுக்கு வழங்கியது!<br/><br/>அஃதென்ன?<br/>உலைத்தீயில் கொல்லன் காய்ச்சிடும் இரும்பில்<br/>ஒரு துளி நீர் பட்டால் அந்நீரை மீட்டிடல் முடியுமா?<br/><br/>(அவ்வாறே)<br/><br/>உக்கிரப் பெருவழுதி வென்றுவிட்ட கானப்பேர்எயிலும்<br/>உலைக்களத்து இரும்பு உறிஞ்சிய நீர்த்துளியாய் ஆனது என்று<br/>உமது பெருமையைப் பேசி - வேங்கைமார்பன்<br/>உளம் நொந்து நிற்கின்றான்\" என்றார்! - அந்தத்<br/><br/>தங்கப் புலவர் தந்த உவமை அழகுக்குத்<br/>தக்க பரிசு என்ன தருவதென்று புரியாமல்<br/>தவித்திருப்பான் பாண்டியன் என்கின்றேன் நான்;<br/>தவறுண்டா என் கருத்தில்?<br/>தமிழின் சுவை அறிந்தோரே! கூறிடுவீர்!"))
        
        databank.append(DataModel(title: "ஒரு பொது மகளின் புலம்பல்!", data: " வயலின் ஓரம் வரப்புகள் மீது<br/>வளர்ந்து நிற்கும் மாமரக் கொப்பில்<br/><br/>உண்ணத் தெவிட்டா ஒட்டுமாங்கனிகள்<br/>வண்ண மாதர்கள் பெருந்தனம் போல<br/><br/>காய்த்துக் குலுங்கி கடைசியில் அவகள்<br/>கயல்கள் ஆடும் கழனியின் பக்கம்<br/><br/>வாவியின் நீரில் வளைந்து துள்ளும்<br/>வாளைமீன் விழுங்கி மகிழ்தல் இயல்பே!<br/><br/>அவ்வாறே,<br/><br/>வாணன் என்னும் இவ்வூர்த்தலைவன் - காம<br/>பாணன் தைத்தது என்று; உள்ளம்<br/><br/>கருகி வந்தான்! கருணை செய்க என்றே - என்முன்<br/>உருகி நின்றான்!<br/><br/>வாளைமீன் வாய்க்குள் மாம்பழமொன்று<br/>வலிய விழுந்தால் சும்மா விடுமா?<br/><br/>காளை அவன்; என் மடியில் வீழ்ந்த<br/>கதையும் இதுதான் தோழி!<br/><br/>பிறகென்ன; நான் அவனை<br/>உறவென்று உரைத்தேனா? அல்லால்<br/><br/>வரிசை வைத்து வா என்று அழைத்தேனா? விழி<br/>வலையை வீசி வஞ்சித்துப் பிடித்தேனா? - என்<br/><br/>பொலிவைக் காட்டிப் பொடிபோட்டு இழுத்தேனா? ஒரு<br/>புன்னகை வீசி அந்தப் \"புனிதனைக்\" கவிழ்த்தேனா?<br/><br/>வசைபாடி எனைத்தாற்றும்<br/>வாணன் மனைவியிடம் கேள்! - இன்ப<br/><br/>இசைபாடிக் களிப்போம் எடனறு;<br/>இரவலன் போல் கெஞ்சியது யார்?<br/><br/>வந்த வாணன் எனைநோக்கி<br/>நொந்த மனத்துடன் சொன்னது என்ன?<br/><br/>\"கட்டிலுக்குத் துணையாய் வாய்த்த மங்கை - பிள்ளைத்<br/>தொட்டிலுக்கு முழுநேரக் காவல் ஆனாள்!<br/><br/>தொடர்ந்து பலநாட்கள் பட்டினியால் வாடி - இன்று<br/>துணிந்து உனைத்தேடி இன்பம் பருகவந்தேன்!<br/><br/>கட்டியணைத்துக் காய்ச்சல் போக்கி - மார்புக்<br/>கச்சை அவிழ்த்துக் காமநோய் தீர்த்திடு\" என்றான்.<br/><br/>ஒப்பினேன் நானும் - பொன்னால்<br/>உப்பிய பையை உதறினான் என்மேல்!<br/><br/>பரத்தை நான் எனினும் உன்பால் பற்றுவைத்த பின்னர் - என்<br/>தரத்தை உணர்வாய்; தாகம் தணிக முதலில் - என்றேன்!<br/><br/>தட்டிலே உள்ள சோற்றைப் பசியால் உண்ணப் பறந்து<br/>கொட்டிக் கவிழ்த்த ஒரு குழந்தையைப் போலே<br/><br/>பரபரப்புணர்வுடன் பாய்ந்தான் என்மேல் - நான்<br/>பயத்தால் நடுநடுங்கிப் போனேன் எனினும் - எனக்குப்<br/><br/>பழகிய கலைதான் என்ற துணிவில்<br/>படுக்கைப் போட்டியில் சமமாய் நின்றேன்!<br/><br/>பலாச்சுளை - தேன்கதலி - தினைமா - என்று<br/>பலநாள் இன்பம் பகிர்ந்து மகிழ்ந்தோம்!<br/><br/>திடீரென்று ஒருநாள்<br/><br/>தெவிட்டியதாலோ அல்லது அவன் தேவியாம் திருமகள்<br/>தேடி அலைகிறாள் என்ற செய்தி தெரிந்ததனாலோ;<br/><br/>தேர்ஏறிப் போன வாணன்,<br/>திரும்பவே இல்லையடி!<br/><br/>பொருள் மட்டும் தரவில்லை; என்னைப் பொதுமகள் என்னும்<br/>இருள் விரட்டும் ஒளிப்பிழம்பாய் அருகிருந்தான்.<br/><br/>உருள்கின்ற காலத்தின் கோலம் பார் -<br/>சுருள்கின்ற கத்திபோல நாக்குச் சுழலும்.<br/><br/>ஆடவனின் பாராட்டெல்லாம் - பஞ்சணையில்<br/>கூடுகின்ற நேரத்தில்தான்!<br/><br/>அதன்பின்னர்ப் புதன் வருவேன்; வியாழன், வெள்ளி வருவேன் என்று<br/>அடுக்கடுக்காய் நாள்குறித்து \"சனி\" காணாமற்போய் விடுவான்.<br/>குறைசொல்லி அவனைநான் குமுறுவதால் பயனில்லை தோழி!<br/>பறைமுழக்கி ஊர்கூட்டி என்மீது பழியுரைக்கும்<br/><br/>கல்நெஞ்சக்காரி இருக்கின்றாளே; அவன்துணைவி<br/>கண்ணீரையே கயிறாய்த் திரித்து அவனைக் கட்டிப்போடுகின்றாளாம்!<br/><br/>கண்ணாடி முன்நின்று ஒருவர்<br/>கண்டபடி அங்கங்கள் அசைத்திட்டால்<br/><br/>கண்ணாடியில் தோன்றும் உருவும்<br/>கச்சிதமாய் அவ்வாறே அசைத்திடுமே!<br/><br/>அதுபோலத்தான்<br/><br/>அவளோ கண்ணாடிக்குமுன் அசைகின்ற அம்மை!<br/>அவனோ அந்த அசைவுஅனைத்தையும் பிரதிபலிக்கும் பொம்மை!<br/><br/>காத்திருந்து இனி என்ன பயன்?<br/>பூத்திருக்கும் முல்லை நான் - இதழ்<br/><br/>மதுவுண்டு செல்வதற்குப்<br/>புதுவண்டு வராமலா இருந்து விடும்!<br/><br/>கலைமகளாய் மாற்றம் பெறக் கனவுகண்டேன்; ஆனால்<br/>விலைமகளாகவே வாழ்க என்று<br/><br/>வாணன் வாழ்த்திவிட்டான்; அந்த<br/>வீணன் வாழ்க நன்று!\"<br/><br/>இவ்விதம் ஒரு காதல் பரத்தை<br/>இதயத்துயர் எடுத்துச் சொல்லி<br/><br/>ஆருயிர்த் தோழியிடம் அலறிப் புலம்பியதை<br/>ஆலங்குடி வங்கனார் எனும் புலவர்<br/><br/>தங்கத் தமிழ் உவமை கூறி<br/>சங்கக் கவியாய்த் தருகிறார் இங்கே!"))
        
        databank.append(DataModel(title: "வாணன் மணந்த வண்ணத் திருமகள்", data: "குலவும் கிள்ளை - குடும்பப்பாவை -<br/>உலவும் தென்றல் - உதய தாரகை - அந்த<br/><br/>மலரை மணந்தான் ஒரு மருதத்தலைவன் - இந்த<br/>உலகை மறந்தனர் இருவரும் இரவில்!<br/><br/>இலவம் பஞ்சுப் படுக்கைப் பள்ளியில் - கலைகள்<br/>பலவும் கற்றார், கற்றார், நாளும் கற்றார்!<br/><br/>கட்டிலில் கற்றது கைம்மண் அளவே - இன்னும்<br/>கல்லாதன கடல்மண் அளவே - இதனைப்<br/><br/>பட்டுமேனிப் பத்தினிக்குரைத்து<br/>பற்பல கோடி இன்பம் தந்தான்.<br/><br/>கொஞ்சிப் பிணைந்த குடும்பக் கொடியில்<br/>பிஞ்சு போன்று ஒரு பிள்ளை உதித்தது.<br/><br/>மஞ்சம் சிலநாள் ஓய்வு பெற்றதால்<br/>மணாளன் தவித்தான் தனிமைத் துயரில்!<br/><br/>மரத்தை வெட்டும் கோடரி போல - அவன்<br/>மனத்தை வெட்டும் காமக்கிளர்ச்சி!<br/><br/>அறத்தை மறவா அவன் இல்லக்கிழத்தி,<br/>சிறுத்தைப் புலியாய் மகனை வளர்க்க -<br/><br/>கனத்த தனங்களில் பெருகும் பாலை,<br/>கருத்த காம்பின் வழியாய்க் கொடுத்தாள் - அதனை<br/><br/>நினைத்த போதெல்லாம் எச்சிலால் நனைத்து<br/>இனித்த சுகத்தை இழந்த கணவன்<br/><br/>தணலாய்க் காமம் தகித்தது கண்டு -<br/>தாகம் தணிக்கப் பெரும் மோகம் கொண்டு;<br/><br/>பரத்தை ஒருத்தியின் படுக்கையில் விழுந்தான் - அவன்<br/>கரத்தைப்பற்றி அவளும் காதலைப் பொழிந்தாள்.<br/><br/>காலை, மாலை, இரவு, பகல் என்ற கணக்கேயின்றி<br/>காம லீலையில் கழிந்தன நாட்கள்!<br/><br/>மூலையில் அமர்ந்து வாடுகின்றாள் - அவன்துணைவி<br/>பாலையில் வெண்ணெய்போல் உருகுகின்றாள்!<br/><br/>அவளிருக்கும் நிலைமைதனை அறிந்ததாலே<br/>அவசரமாய்ப் புறப்பட்டான் தேர்ஏறி!<br/><br/>பாணன் ஒருவனைப் பக்குவப்படுத்தி<br/>பாவையின் கோபம் தணிக்கச் சொன்னான்.<br/><br/>இசைப்பாணன் அவள் இல்லம் சென்றான்.<br/>இங்கிதமாய் எவ்வளவோ எடுத்துரைத்தான்.<br/><br/>இல்லக்கிழத்தியோ இறவாச் சினமுடன்<br/>இலைமறைகாய் என எதுவுமின்றி<br/><br/>எல்லா விபரமும் எடுத்தியம்பிப்<br/>பொல்லாக் கணவனைப் புறக்கணிக்கின்றாள்.<br/><br/>பல்கண்ணனார் என்னும் புலவர்,<br/>பாடிய சங்கப் பாடலில் இப்படிப்<br/><br/>பாணன் ஒருவனிடம் தனது நிலையைப்<br/>பட்டவர்த்தனமாய்த் தலைவி சொன்னதாய்ச் சொன்னதை மாற்றி<br/><br/>கற்பனையொன்றைத் தீட்டிட முனைந்தேன்<br/>கருப்பொருள் எதுவும் கெடாதவாறு!<br/><br/>பரத்தையின் இருளை விட்டகன்ற வீரன்,<br/>பத்தினி விளக்கைப் பார்க்கத் துடித்தான்.<br/><br/>பாணனை அனுப்பினால் தூது பலிக்காமற் போகலாம்; அந்தப்<br/>பயத்தால் திகைத்துப் பற்பல சிந்தனை செய்தான் - முடிவில்,<br/><br/>ஒரு பாணன் போல் உருமாற்றங் கொண்டு<br/>திருமகள் என்ற தன் தேவிமுன் சென்றான்.<br/><br/>\"யார் நீ? யாது வேண்டும் உனக்கு?\" என்று<br/>பார்வையொன்றால் அந்தப் பாவையும் கேட்டாள்.<br/><br/>\"குலவிளக்கே! குங்குமச் சிமிழே! உனைக்<br/>கொண்ட கணவன் அனுப்பினான் என்னை!<br/><br/>விலைமகளோடு போன துணைவன் வருவான் மீண்டும்! நீ;<br/>நிலமகள் போலப் பொறுமையைப் பூண்டு,<br/><br/>வளைக்கரம் நீட்டி வரவேற்றிடுக என்று -<br/>களைத்தமிழ்ப் பாட்டை யாழுடன் இசைத்தான்.<br/><br/>பொதுமகளை நாடி அவன் ஓடியதைப் பொறுத்திடுக!<br/>புதுமனிதனாய்த் திரும்பி வருகின்றான்; அவனை<br/><br/>மன்னித்தேன் என்றுநீ உரைத்துவிட்டால் - உனை<br/>அள்ளித்தேன் பருகுதற்குத் தயங்கமாட்டான்.\"<br/><br/>இசைமீட்டி இவ்வாறு<br/>பாணன் வேடத்தில் பரிந்துரைக்கும் சாகசத்தை<br/>வாணன் எனும் தன் கணவன்தான் நடத்துகின்றான் எனத் தெளிந்தாள்.<br/>இசைப்பாணன் அல்ல அவன் எனத் தெரிந்தபின்னர்,<br/>மணவாளன் முன்பேச என்ன அச்சம்! - அதனாலே<br/><br/>மனத்தில் உள்ள கொதிப்பை எல்லாம்<br/>மாதரசு கொட்டித் தீர்த்தாள்.<br/><br/>\"சிறியாழ் மீட்டுகின்ற பாணரே!<br/>சிர்குலைந்த என் நிலையைப் பாரும்!<br/><br/>காய்ச்சுகின்ற நெய்யும்<br/>கரும்புகையும் பட்டதாலும்,<br/><br/>கற்கண்டாம் என் பிள்ளைக்குக்<br/>கண்ணோரம் மைஎழுதி விட்டதாலும்<br/><br/>அழுக்குப் படிந்து ஆடையெலாம்<br/>அழகற்றுப் போனதய்யா!<br/><br/>தேமதுரக் குழந்தையின் பசிதீரக் கொடுத்தபிறகும்கூட<br/>தேமலழகுக் கொங்கையினில் சுரக்கின்ற பால்பெருகி,<br/><br/>தோகையென் தோள்மீதும் மார்பினிலும் வழிவதாலே - உம்<br/>தோழருக்குப் பிடிக்காத முடைநாற்றம் வீசுதய்யா!<br/><br/>எனவே எனைவிடுத்து,<br/>அணிமணிகள் ஒளிவழங்க<br/>ஆடிப்பாடிக் களிப்பூட்டித் - தும்பைப்பூத்<br/><br/>துணிமணிகள் காற்றில் பறக்க<br/>ஆள்மயக்கும் விழிகாட்டும்<br/><br/>விலைமகளிர் வீதிக்கே - அவரை<br/>விரைந்து திரும்பச் செய்திடுவீர் - இம்மனையின்<br/><br/>தலைமகனும் என் கணவர் உம்கையில்<br/>தரும் பொன்னைக் கூலியெனப் பெற்றுக் குதித்திடுவீர்!\"<br/><br/>இல்லாள் வெகுண்டெழுந்து இதைச் சொல்லி முடித்தவுடன்<br/>பொல்லாச் செயல் புரிந்த துணைவனவன் நடுங்கிப் போய்<br/><br/>\"நில்லாது இனி என் உயிர் கண்ணே!\" என்று<br/>நெடுமரமாய் அவள் தோளில் சாய்ந்தபடி,<br/><br/>அணிந்துவந்த பாணன் வேடம்<br/>அரைநொடியில் கலைத்து விட்டான்.<br/><br/>துணிந்து சென்று தொகை வழங்கி இன்பம் பெற்று - இன்று<br/>பணிந்து வந்து பாணன் உருவில் கெஞ்சுகின்ற<br/><br/>வாணன் மீது வஞ்சம் தீர்க்க வழியே இன்றி,<br/>\"வாழ்க\" என்று திருமகளும் தழுவிக் கொண்டாள்.<br/><br/>குழந்தையொன்று குறுகுறுவெனப் பார்ப்பதையும் மறந்து - உடல்கள்<br/>அழுந்த அணைத்தனர் - அதைத் தொடர்ந்து அகல் விளக்கையும் அணைத்தனர்."))
        
        databank.append(DataModel(title: "ஊரின் பெருமை உணர்த்தினாள் ஒருத்தி!", data: "வீராம்பட்டினமென்பது புதுவைக்கருகில்<br/>விளங்கிடும் ஊராம் - புலவர் பிரான்<br/>வீரைவெளியனார் தோன்றிய இடமென, இதனை<br/>விளக்குவர் அறிஞர்!<br/>தித்திக்கும் கவிதை தீட்டி அகத்துறையில் புகழ்பெற்ற<br/>தித்தனார் எனும் கவிஞர் இவர் பெற்ற மகனேயாவார்.<br/>திறன்மிக்க மகனை ஈன்ற வெளியனாரும்<br/>புறப்பாட்டில் ஒரு புதுமைதனைக் காட்சியாக்கி,<br/>வேட்டுவர் இல்லத்துப் பெண்விளக்கின் மாண்புதனைக்<br/>காட்டுகின்றார் கவிதை ஒளிக் காவியமாய்!<br/><br/>வேந்தனிடம் விடைபெற்றுக் களம் சென்ற தளபதி<br/>வெற்றியினை மாலையாக்கிச் சூடிக்கொண்டு<br/>விருதுபல பெற்று வீரக்கொடி விண்தவழ<br/>வீடு திரும்பினான் என்ற செய்தி கேட்டு - அவனைப்<br/>பாடிப் பரிசில் பெற பாணன் ஒருவன் சென்றபோது - அவன்<br/>நாடி வந்த தளபதியின் நல்நெஞ்சக் கொடை இயல்பை<br/>நற்றமிழில் பாடுகின்றார், வீரை வெளியனார்.<br/><br/>வெற்றி பெற்ற நாட்டுப் பொருள்களையும் - நமது<br/>வேந்தர் வழங்கியுள்ள விருதுப் பொருள்களையும்<br/>ஈந்து சிவந்திட இரு கரமும் கொண்டவன்தான்<br/>பாய்ந்து பகைவீழ்த்தி வந்துள்ள வீரன் எனப் புகழ்ந்து - அன்பு<br/>தோய்ந்து இதயமுடன் அள்ளித் தருவான் பரிசுகளை என்று,<br/>ஆழ்ந்து அறிவுசால் வீரைவெளியனார், பாணர்க்கு அறிவித்து மகிழ்ந்து,<br/><br/>அவ்வூரின்கண் அரும்பிமலர்கின்ற<br/>அழகு மகளிர் பண்பு நலமும் பாடுகின்றார், கேளீர்!<br/><br/>முன்னைக் கொடியும் முசுண்டைக் கொடியும்<br/>பின்னிப் படர்ந்து விரிந்து பரந்ததால்<br/>பந்தல் என ஒன்று வேண்டாம் வெயில் மறைக்க என<br/>பலாக்கனிகள் தொங்குகின்ற அடர் மர நிழலில் கீழே<br/><br/>வேடனொருவன் உறக்கத்தைத் தழுவிக்கொண்டு,<br/>வேல்பிடித்த கரத்தைக்கூடத் தளர்த்திக் கிடந்தான்.<br/>வேலுக்குப் பதிலாக அவனருகே படுத்துக்கொள்ள<br/>சேலுக்கு இணையான விழிபடைத்த அவன் துணைவி, விருப்பங்கொண்டு;<br/><br/>மெல்ல நடந்து இல்லத்துக்குள்ளிருந்து - அந்த<br/>வெல்லப்பாகு இதழாள்; கொடிப்பந்தல்தனைக் கூர்ந்து நோக்குகின்றான்.<br/><br/>களைப்புற்ற கணவனருகில் ஒரு மலர் போல ஒலியின்றி பட்கார வேண்டுமென்றும்,<br/>கண்களின் பசி தீர அவன் கட்டழகைப் பார்த்துக் களித்திட வேண்டுமென்றும்,<br/><br/>தாமரை மொட்டிரண்டைச் சற்றுத் தாமதாய் அவன்<br/>தடந்தோளின் பக்கம் திருப்பினால் போதுமென்றும்,<br/>அவசரப்பட்டு ஒரு முத்தம் அரும்பு மீசை உதட்டின்மேல்<br/>அளித்துவிடின், அவன் விழித்துக்கொள்ள நேருமென்றும்,<br/>ஆனைகளை வேட்டையாடி அயர்ந்து தூங்கும்<br/>ஆளனது ஓய்வு கலைத்தல் அவன் உடல் நலனுக்கு ஆகாதென்றும்,<br/><br/>முழுத் தூக்கம் முடியட்டும்; பின்னர் காலைக்கதிர்<br/>எழுமட்டும் முடியாமல் தொடரலாம் இன்பமென்றும்,<br/>தொலைவில் நின்று அத்தோகை நல்லாள் - காற்றில்<br/>அலை அலையாய் எழுகின்ற கருங் கூந்தல் கோதி,<br/>எண்ணமிட்டு நின்றிருந்தாள் - அவள்<br/>வண்ணமிட்ட மார்பகமோ உயர்ந்துயர்ந்து உயிரை வாங்கித்<br/>தணிந்து, பின்னும் உயர்ந்தெழுந்து மேன்மேலும் அவள்<br/>தணியாத தாபத்தை வெளிப்படுத்தும்!<br/>துணிந்து சென்று சென்று அவன்மேல் வீழ்ந்துவிட்டால் - நல்ல<br/>தூக்கத்தைக் கலைத்த குறை, அவள் மனதைக் குடையும்!<br/><br/>அதனாலே<br/><br/>அவனருகிற் சென்று அசையாமல் படுத்துக்கொண்டு<br/>அயர்வு நீங்கி அவன் விழிப்புற்றுப் புரளும்போது;<br/>அத்தான் என அழகு விரல்களாலே முகம் தடவி<br/>முத்தாக ஒரு முதூதம் கொடுப்பதென முடிவு கட்டி,<br/>காட்டு வழியில் தேய்ந்து காய்த்துப்போன கால்களையும்<br/>காய்ந்த சருகுகளின் மேல் பூப்போல ஓசையின்றி ஊன்றி,<br/>விழிமூடித் துயில் கொள்ளும் வேடனிடம்; தொப்புள்<br/>சுழியின் எழில் மூடாமல் சுகந்தமிகு வசந்தமென நடக்கலுற்றாள்!<br/><br/>அப்போது,<br/><br/>பலாக்கனிகள் குலுங்குகின்ற மரநிழலில்<br/>படர்ந்திருக்கும் கொடிகள் நிறை இலைமறைவில்<br/>படுத்திருக்கும் வேடனுக்கு மிக அருகாமையில்<br/>தடுத்துரைத்த பெண்மான் ஒன்றைத் தழுவிக்கொண்டு<br/>ஆண்மான் தந்த அருஞ்சுகத்தை, இழப்பதற்கும் மனமின்றி, - அந்தப் பெண்மான்<br/>வீண் முயற்சி எடுத்து மீண்டும் தடையொன்றும் சொல்லாமல்,<br/>இருமானும் இன்பத்தின் உச்சியிலே மிதந்து<br/>தருநிழலில், தவமிருந்து வரம்பெற முனைவோர் போல - தலைமறந்த நிலையுற்ற,<br/>இயற்கையின் திருக்கூத்தை அந்த இல்லத்தரசி<br/>இருவிழி கொட்டாமல் பார்த்தயர்ந்தாள் !<br/><br/>உடனே<br/><br/>அவளுக்கோர் அச்சம் உள்ளத்தில் வெளிச்சமிட<br/>அப்பால் ஓர் அடிதனை எடுத்து வைக்கவும் தயக்கமுற்றாள்!<br/><br/>பலாமரத்து இலைச்சருகுகளில்<br/>படர்ந்துள்ள கொடியுதிர் மலர்களில் - தனது<br/>பாதம் படுகின்ற சிறிய ஒலி கூட;<br/>இசை மூடி இவ்வுலகை மறந்து துயில்கின்ற<br/>இளமை மிகு வேடனையே எழுப்பிவிட்டால்,<br/>ஆர்த்தெழுகின்ற அவன் தோற்றம் கண்டு - காதல்<br/>போர்க்களத்து வெற்றிக்குப் போட்டியிடும்<br/>ஆண்மானும் பெண்மானும் அடைய இருந்த இன்பமெலாம்<br/>ஊண் பொங்கும் நேரத்தில் உலைப்பானை கவிழ்ந்தது போலாகி<br/>ஓடி உயிர் காத்துக் கொண்டாலே போதுமென இருமானும்<br/>வாடிப் பிரிய நேரிடுமே எனவும் எண்ணினாள்; அல்லது<br/>வேடன் எழுந்தவுடன் அருகிருக்கும், தனது<br/>வேலை எடுத்து வீசிவிட்டால் - காதல்<br/>பாடந்தனைச் செயல் மூலம் பயிலுகின்ற ஈருயிரும்<br/>கலவி நடுப்பாதையிலே பலியாக வேண்டும் என்றும்<br/>கவலை மிகக் கொண்டுவிட்டாள்!<br/><br/>இவையிரண்டு தீங்கினையும் தவிர்ப்பதற்குத் தக்க வழி<br/>இங்கேயே நான் மறைந்து நிற்பதுதான், என்று<br/>அசையாத சிலைபோல இருந்துவிட்டாள் - தெவிட்டாத<br/>இசைபோல வீசும் தென்றல்; அவள் பண்பை வாழ்த்திப் பாடிற்றாங்கே!<br/>மானினத்தின் உணர்வுக்கு மதிப்பளித்துக் கருணைதனை<br/>மழையாகப் பொழிகின்ற மாதரசு வாழ்கின்ற இவ்வூரில்<br/>மானிடராம் பாணர்க்குப் பரிசு இல்லை என மறுப்போர் உண்டோ?<br/>பானை சோற்றுக்கு ஒரு சோறு பதம் என்ற பழமொழியைப்<br/>பாவை இவள்தான் மெய்ப்பிக்கின்றாள் என்று<br/>பாணர்க்கு அவ்வூரின் பண்பாட்டை வீரவெளியனார் எடுத்துக்காட்டி<br/>பரிசுபெறச் சென்றிடுவீர் பகைமுடித்த தலைவனிடம் எனக்கூற<br/>விரைந்திட்டார் வேகமாகப் பாணர்; வீர<br/>வெளியனார்க்கு நன்றியினைக் குவித்தவாறு!"))
        databank.append(DataModel(title: "நெல்லும் உயிர் அன்றே! நீரும் உயிர் அன்றே!", data: " அரசனது அத்தாணி மண்டபத்தில் அன்றொரு நாள்<br/>ஆன்றவிந் தடங்கிய மேலோர் புலவர் அவை கூடிற்றாம்!<br/><br/>பல்பொருள் குறித்து விவாதம் எழுவதும் முடிவதும்<br/>பலமுறை நடக்கும்; அங்கே பைந்தமிழ் மணக்கும்.<br/><br/>இம்முறையோ அரியதோர் கேள்வி பிறந்தது அவையில்<br/>இன்னுயிர்க்கிணை எதுவென்றும் என்புதோல் போர்த்த உடலுக்கு<br/><br/>இணைதான் எதுவென்றும் இணையிலாப் புலவர் கூட்டம்<br/>கணைகளாய்த் தொடுத்திட கருத்துக்கள் மோதின அக்களத்தில்<br/><br/>நெல்லும் நீரும் உயிராய் விளங்கிப் பல்லோர் உடலை வளர்த்து<br/>நில்லா நெடுஞ்சுவர் உயிரையும் நிலைக்க வைத்திடுமென்றும்<br/><br/>நிலமாளும் வேந்தர்க்கும் அதுவே உயிராக விளங்குமென்றும்<br/>நீருக்கும் நெல்லுக்குமுள்ள சிறப்பைச் செப்பினர் சிலபுலவர்<br/><br/>முரசு கட்டிலில் படுத்துறங்கி முடிமன்னன் கைகளாலே<br/>அரசு மரியாதையாக வெண்சாமரம் வீசப்பெற்ற<br/><br/>மோசிகீரனார் எனும்புலவர் முற்றிய விவாதத்துக்கிடையே<br/>மாசு மருவிலாக் கருத்தொன்றை வெளியிட்டார்!<br/><br/>\" என்னருமைப் புலவர் பெருமக்காள்! இனிய<br/>கன்னலெனத் தமிழ் வடித்துத் தருகின்ற பெரியீர்!<br/><br/>பொன்னின் நிறம் வாய்ந்த நெல்விளையும் கழனிகளில்<br/>புனல்பாய்ந்து விதை முளைத்து, பயிரின் வேர் நனைத்து<br/><br/>உழவனது வியர்வையெலாம் அறுவடைக் குவியலாகி<br/>உண்பதற்கு வெண்சோறு பொங்குதற்கும்;<br/><br/>உலையிலிட நீர் வேண்டும்; உண்மையது மறுக்கவில்லை!<br/>உடல் வளர உயிர் வாழ நெல்லும் நீரும் தேவையென்பதிலும் மாற்றமில்லை!<br/><br/>சிந்தனைக்கு ஒரு வாதம் வைக்கின்றேன்;<br/>சிறிதளவும் நடுமுள் நடுங்காமல் தீர்ப்பளிப்பீர்!<br/><br/>நாடாளும் அரசொன்று இருக்கும்போது - அவ்வரசு;<br/>கேடான செயல்களுக்கும் கீழான குணங்களுக்கும் அடிமையாகி,<br/><br/>நீரும் நெல்லும் இல்லா நிலையில்<br/>ஊரும் உலகும் தவித்திடும் வகையில்<br/><br/>சிரும் சிறப்பும் இல்லாச் \"செங்கோல்\"<br/>செலுத்திடும் கொடுமை படைத்தடுமானால்<br/><br/>உடலெங்கே? உயிரெங்கே? இவ்விரண்டும்<br/>ஒன்றைவிட்டு ஒன்றகன்று பிணமாக மாறிவிடும்!<br/><br/>அதனாலே;<br/><br/>மன்பதை ஆளும் ஆட்சியின் மாட்சியே - அந்த<br/>மக்களுக்குயிராம் ; அவர்கள் வாழும் மண்ணுக்கும் உயிராம்!<br/><br/>வேற்படை விற்படையென விதவிதப் படைகளிருப்பினும்<br/>விழிப்புடன் வேந்தன் விரைந்து செயல்படல் வேண்டுமன்றோ?<br/><br/>வெளிநாட்டுப் பகைப்புலத்தை விரட்டுகின்ற வீரமுடன்<br/>ஒளிநாடாய்த் தன்னாட்டை உருவாக்கி வைப்பதற்கு<br/><br/>தெளிவுள்ள ஆட்சித் திறன் தேவையன்றோ? சோம்பலினால்<br/>நெளிகின்ற போக்கிருந்தால்; நெல் எங்கே? நீர் எங்கே?<br/><br/>அறிவார்ந்த அரசொன்றே நாட்டுக்குயிராய் அமைந்துவிடின்<br/>அதன்பின்னர் நீர்வளமும் நிலவளமும் பிறவளமும்<br/>நிறைந்து குலுங்கிவிடும் அறிவீர்!\" என்றார்.<br/><br/>மோசிகீரனார் மொழி கேட்டுப் புலவரெலாம் தமது வாதம்<br/>தூசியென ஒப்புக்கொண்டு தோல்வியினை ஏற்றுக்கொண்டார்!<br/><br/>நெல்லும் உயிரன்றே! நீரும் உயிரன்றே! எனக்<br/>கல்லில் செதுக்கிய எழுத்தாகப்<br/><br/>புறப்பாட்டு பூத்ததம்மா! அது என்றும்<br/>புதுப்பாட்டாய்ப் பொலியும் அம்மா!"))
        
        
        
        databank.append(DataModel(title: "மாவீரன் கண்ட மலர்கள்", data: " பூக்காட்ன் சிறப்பு குறித்துப்<br/>பாராட்டு மொழியொன்று கூறுதற்கு - ஒரு<br/>பொற்கொல்லன் முன்வந்தால்;<br/>பொன்னிறப் பூக்கள் நிறை சோலையென்று<br/>பொதுவாகக் கூறிவிட்டு<br/>முத்துமாலை கோத்தது போல்<br/>முல்லையரும்பு வரிசை என்பான்!<br/>செம்பருத்திப் பூக்கள் கண்டால்<br/>சிவப்புக்கல் தொங்கல் என்பான்!<br/>மாணிக்கம் நிகர் பூக்கள் பல<br/>மலர்த் தோட்டத்தின்<br/>மகிமைதனை உயர்த்தல் காண்பீர் என்பான்!<br/><br/>இதனையே ஓர் உழவன் சிறப்பித்தால்<br/>ஏர் இழுக்கும் மாடுகளின் வால்நுனி போல்<br/>அல்லித் தண்டுகளின் மொட்டுக்கள்<br/>அழகுற விளங்குகின்ற<br/>அரியதோர் காட்சியென்பான்;<br/>அங்கிருக்கும் தடாகத்தைச் சுட்டிக்காட்டி!<br/>தும்பைப்பூ, மகிழமலர், நீலோற்பலமென<br/>தூரிகை கொண்டு பற்பல நிறங்களைத்<br/>திரைச்சிலையிலே அள்ளி அள்ளித்<br/>தெளித்தது போல் அமைந்ததிந்தப் பூங்கா என்று,<br/>ஓவியனாயின் அவன் நோக்கில்,<br/>உவமையொன்று உரைத்திடுவான்!<br/><br/>ஒரு கவிஞனிடம் சிக்கிவிட்டால் மலர்க்கூட்டம்; அப்பப்பா!<br/>ஒவ்வொன்றாய் எடுத்து வைத்து; மகளிர்<br/>உடலின் அங்கமெல்லாம் ஒப்பிட்டுத் தீர்த்தால்தான்<br/>உறக்கம் வரும் அவனுக்கு!<br/>தாழை மலர் நிறமன்றோ தளிர் மேனியென்பான்<br/>தாமரைதான் முகம் என்பான்.<br/>மல்லிகை, செண்பகம், சாமந்தி கொண்டு<br/>மலர்ச் செண்டமைத்து - அதன் முனையில்<br/>மனோரஞ்சிதத்தைக் கவிழ்த்து வைத்தது போல்<br/>மார்பகம் கொண்ட மாதர்க்கரசி யென்பான்!<br/>கையின் விரல்களது மென்மைக்கு<br/>காந்தள் மலரை உவமை காட்டிடுவான்!<br/>மகிழம்பூதான் அவள் தொப்புள் என்பான்;<br/>மஞ்சத்தில் ஊடும்போது அனிச்சமலர் ஆவாள் என்பான்!<br/>பஞ்சு மலர்ப் பாதமென்பான் - அந்த<br/>வஞ்சிக்கு இடை, அத்திப்பூ என்பான்!<br/><br/>பாவு போட்டுத் தறி நெய்யும்<br/>பாட்டாளியாம் நெசவாளி, பாராட்ட முற்பட்டால்;<br/>பட்டுப்போல் இருக்குதம்மா பூவின் இதழ் என்றும்,<br/>பளபளக்கும் சரிகைப் பூக்கள் கொண்ட முன்றானையால்<br/>தளதளக்கும் தையலர் தம் அழகென்பான்!<br/>ஏருழவன், பொற்கொல்லன்,<br/>ஏற்றமிகு ஓவியன்,<br/>இசைபட வாழும் கவிஞன்,<br/>நெசவுதனைத் தொழிலாய்க் கொண்டோன்<br/>இவரெல்லாம் இனிய கற்பனையால்,<br/>இயற்கையீன்ற பூக்குழந்தைகளைத்<br/>தமிழ்மொழியால் கொஞ்சுகின்றார்!<br/>இதோ ஒரு வீரன் எதிரிகளைக் களம் கண்டு<br/>இட்ட பணி முடித்து வெற்றியுடன் திரும்பி;<br/>இல்லத்தில் காத்திருக்கும்<br/>இனிய நறுந்தேனை<br/>இடையறாது பருகுதற்கு<br/>இடிக்குமுன் வெட்டுகின்ற மின்னலது வேகத்தில்<br/>இரதத்தைச் செலுத்த ஆணையிடுகின்றான் பாகனுக்கு!<br/>ஓடோடி வருகின்ற அவ்வீரன் - தன்<br/>உள்ளத்தில் குடியிருக்கும்<br/>உயிரனையாள் வாழ்கின்ற<br/>ஊரின் சிறப்புதனை நினைத்து மகிழ்கின்றான்.<br/>வாரி இறைத்தது போல் அவ்வூரின்<br/>வழியெங்கும் பூத்துக் கிடக்கும்<br/>வண்ண வண்ண மலர்கள் பற்றி - அவன்<br/>எண்ணம் பறக்கிறது சிறகடித்து!<br/><br/>இருமருங்கும் நிறைந்து மலர்ந்துள்ள<br/>இலுப்பைப் புக்கள் எதிர்வந்து;<br/>வில்லேந்தி வெற்றி விளைக்கின்ற<br/>வீரர்தம் தோள்களிலே<br/>கூர் மழுங்காதிருக்க குப்பிகளிட்டு மூடிய<br/>கூடுகளில் நிறைந்துள்ள அம்புகளின் நுனியைப் போல்<br/>செப்புத்தகடனைய சிவந்த தளிருடனே<br/>செழிப்பு தவழ அரும்பி நின்று<br/>வண்டுகள் தேன்பருக வந்து கூடிக்<br/>கிண்டிவிட்ட காரணத்தால் இதழ்கள் விரிந்து<br/>காற்றலையால் தாக்குண்டு காம்பு கழன்று<br/>கடுமையாய்ப் பெய்த ஆலங்கட்டி மழை போல<br/>பவளமென சிவந்துயர்ந்த பாதையெங்கும்,<br/>பரவிக் கிடக்கும் காட்சி கண்டால்; அது<br/>களத்தில் - கை, கால், தலைகள் அறுபட்ட<br/>காரணத்தால் வழிந்தோடும் குருதியிலே<br/>கொழுப்புத் துகள்கள் ஆங்காங்கு<br/>குவிந்து மிதப்பது போல் தோன்றுதம்மா!\"<br/>ஏர் உழவன், பொற்கொல்லன், நெசவாளி, ஓவியன்,<br/>இயற்கைதனைப் பாடுகின்ற இளம் நெஞ்சக் கவிஞன்<br/>இவரெல்லாம் மலர்கள் பற்றி செய்திட்ட கற்பனைக்கும்<br/>இந்த மாவீரன்; இலுப்பைப்பூ சிதறியுள்ள காட்சியினைக் காண்பதற்கும்;<br/>இருக்கின்ற தனிச் சிறப்பென்ன; புரிகின்றதா?<br/>இவன், இனிய காதலியை நாடி வரும்போது கூட<br/>களத்தில் பெருகிய ரத்தமும் நிணமும்தான் - அகக்<br/>கண்களின் முன்னே அரிய காட்சியாகி<br/>அந்தச் சிந்தனையில் வருகின்றான் என்று - இந்த<br/>அகப்பாட்டை இயற்றியுள்ள கல்லாடனார்,<br/>மறைமுகமாய் அந்த ஊரின் பெருமை கூறுவார் போல்<br/>மாவீரனின் மனோநிலையை வெளிக்காட்டும்<br/>வித்தகத்தை என்னென்று புகழ்வதென<br/>எனக்கொன்றும் புரியவில்லை! இப்படி<br/>எத்தனையோ புதையல்கள் இருக்குதம்மா; சங்கத் தமிழ் இலக்கியத்தில்!"))
        
        databank.append(DataModel(title: "காக்கைக்கு நன்றி காட்ட...", data: "போரில் கலந்திட பிரிந்து சென்றவன்<br/>தோளில் தவழ்ந்திட வரவில்லை யென்று<br/><br/>வேரில் பழுத்த பலாப் போன்ற பாவை<br/>விழிநீர் பெருக்கி வேதனை யுற்றாள்!<br/><br/>துன்பம் போக்கித் தலைவியைத் தேற்றிட<br/>தோழியும் முனைந்தாள்! முடியவே இல்லை!<br/><br/>வீட்டினில் அமர்ந்து காகம் கரைந்தால்<br/>விருந்து வருமெனும் நம்பிக்கை யொன்று<br/><br/>நாட்டினில் உண்டு அறிவீர் அன்றோ!<br/>நல்லவேளை; அப்படியொரு காகம் கரைந்திற்றாங்கே!<br/><br/>\"கவலை விடுக கண்ணே; காகம் கரைந்ததாலே, - உன்<br/>பவள இதழ் சுவைக்கப் பருவக் காளை பாய்ந்து வருவான்\" என்று<br/><br/>பகர்ந்தாள் தோழி; தலைவியின் துயரமும்<br/>நகர்ந்தது சிறிதே!<br/><br/>திரும்பிய காதலன் மேனி தழுவிட<br/>கரும்பின் சுவையை இருவரும் பருகினர்!<br/><br/>\"பிரிந்திருந்த நாளெல்லாம் தலைவியின்<br/>பெரும் இன்னல் தீர்ப்பதற்குத் துணையிருந்தாய் தோழி!<br/><br/>நீ வாழியவே; வாழி!\" என்றுரைத்த தலைவனிடம்;<br/>நெடும் புருவத் தோழிப் பெண் கூறிடுவாள் -<br/><br/>\"தாகத்தால் தவித்திட்ட தலைவிக்கு ஆறுதலைக்<br/>காகம் தான் வழங்கிற்று! அதனாலே;<br/><br/>வள்ளல் நள்ளியின் வளநாட்டு ஆயர்<br/>அள்ளி வழங்கும் நெய்யுடன்<br/><br/>தொண்டி மூதூர் நெல்லரிசிச் சோறு பிசைந்து<br/>குண்டுப் பாத்திரம் ஏழில் நிறைத்துத் தரினும்<br/><br/>ஒளியழகுத் தலைவி துயர் போக்கிய காக்கைக்குத்<br/>துளியளவே ஆகுமென்றுணர்க!\" என்றாள்.<br/><br/>ஏழு பாத்திரம் நெய்ச்சோறு தரினும்<br/>இணையில்லை காகம் செய்த உதவிக்கு -<br/><br/>என்பது இதன் பொருளாம்; இதனை<br/>எடுத்தோதுவது எழிலார் தமிழ்க் குறுந்தொகையாம்!"))
         databank.append(DataModel(title: "பாவை புகழ்ந்த பன்றி", data: "தென்னை இளம்பாளைச் சிரிப்பழகி முன் எழிலைத்<br/>தென்றலுடன் வம்பு செய்து மூடிக் கொண்டே<br/>அன்னை என்ன சொன்னாள்? என்று தோழியிடம்<br/>அமுதூறும் வாய்திறந்து அவசரமாய்க் கேட்டாள்!<br/><br/>குறும்புக்காரத் தோழியவள் கோலவிழி காட்டி;<br/>குன்றையும் குடையாக்கும் திறமை எனக்குண்டு!<br/>எறும்பு ஊரக் கல்லும் தேயுமன்றோ? அதுபோல<br/>என்பணியை முடித்துவிட்டேன் உன் அன்னையிடம்!\"<br/>- என்றாள்.<br/><br/>\" எப்படித்தான் ஒப்பினாளோ என்தாய்?<br/>இப்படி உட்கார்! அந்த இனிமைச் செய்தியைக்<br/>கொஞ்சம் சொல்!\" என்று - அந்தக்<br/>கோதை கெஞ்சினாள்! தோழியோ;<br/><br/>\"நன்றி நீ எனக்கு ஆயிரம் சொல்ல வேண்டும்;<br/>நான் பட்டபாட்டுக்கு வெகுமதியே நாட்டில் இல்லை!<br/>குன்றிமணி அளவுகூடக் குறையில்லான் உன் காதலன் என்று<br/>பன்றி ஒன்றின் செயலுடன் அவனை ஒப்பிட்டு உரைத்தவுடன்<br/>கட்டிவெல்லம்போல் இனிக்கின்ற உவமை சொன்னாய்; இனி<br/>அட்டியில்லை என்மகளை அவன் மணப்பதற்கு என்று கூறித்<br/>தட்டிக் கொடுத்தாள் என் முதுகை! ஒரு<br/>சட்டிப் பலகாரத்தை என்வாயிலும் திணித்துவிட்டாள்<br/>உன்தாய்\" என்றாள்.<br/><br/>விளையாடுகின்றாயா தோழி? அவரைப் பிரிந்து;<br/>வளை கழலுமளவுக்கு மெலிந்துவிட்ட என் கைகள் கண்டும்<br/>விளையாடுகின்றாயா தோழி?<br/>தளையிட்டுத் தடுக்கின்ற தாயின் நெஞ்சை<br/>களையற்ற நஞ்சையாக மாற்றல் விடுத்து;<br/>அன்றில் பறவைகளாய் வாழ்வோமென்று அணைத்த ஆண் சிங்கத்தைப்<br/>பன்றியுடன் ஒப்பிட்டு என் ஆசைக்கனவுகளைக் கலைத்து<br/>விட்டாயே; அடுக்குமா உனக்கு?<br/><br/>முழுங்குகின்ற முரசடித்துப் பகை முடக்கிப் போடுகின்ற<br/>குறிஞ்சிநிலத்து இளங்களிறு போன்றவரை - பூமியில்<br/>கிழங்கு தோண்டும் பன்றிக்கு ஒப்பிட்டு உள்வாயைக்<br/>கிழிக்கவேண்டும்! கெடுத்துவிட்டாய் காரியத்தை! - என்<br/>கிட்டநெருங்காதே! போய்விடு என்று - அவள் போட்ட கூச்சலினால்,<br/><br/>கிறுகிறுத்துப்போன தோழி:<br/>\" பரபரப்பு அடையாதே! பதட்டத்தை அகற்றிவிடு!\"<br/>பன்றி என்றவுடன் பதறுவதால் பயனில்லை;<br/>முழுதும்கேள் கதையை! முடிவில் என்னைத்<br/>தொழுதுநீ போற்றிடுவாய்!<br/>பழுதொன்றுமில்லை நான் சொல்லும் பன்றிக்கு!<br/>உழுது, பயிர்காத்து அறுவடையும் செய்துவந்த எனைப் பார்த்து;<br/>கழனிதனைக் கெடுத்து வந்த கலகி எனக்<br/>கடுமையாகப் பேசிடுதல் முறையே அல்ல! உன்<br/>காதல் நாற்றுக்கு கரையெடுத்து நீர் பாய்ச்சி - இன்று;<br/>கதிர் சாயும் பக்குவத்தைக் கண்டு களிப்பவள் நான்!<br/><br/><br/>அதனாலே<br/>\"பண்பார்ந்த உன்தாய்கு நான் புகன்ற<br/>பன்றிக் கதையைச் சற்றுப் பகுத்தறிவைத்<br/>துணைக்கழைத்துச் செவிமடுத்துப் பின்னர் - உன்<br/>தோழியினை எடைபோடு\" எனக்கூறித் தொடங்கினாள் கதையை!<br/><br/>\"கதையென்று கருதாதே; நேரில் கண்டகாட்சி!<br/>காடு சூழ் மலைமீது கடும் வேட்டையாடுதற்கு<br/>நாய்களை முன் ஓடவிட்டு<br/>நமது வேட்டுவர்கள் விரைந்தபோது;<br/><br/>பாய்ந்து பறந்து பன்றிக் கூட்டம்<br/>பல்வேறு திசைகளிலே சிதறியோட<br/>தாய்ப்பன்றியொன்று தன் குட்டிகளுடன்<br/>தவியாய்த் தவித்துத் தலைதெறிக்கத் தடுமாறியது!<br/>தன் துணைவிக்கும் குட்டிகட்கும் நேர்ந்தகதி<br/>தாங்காமல் ஆண்பன்றி அவ்வேடர்களைத் தடுத்தெதிர்த்து,<br/>தலைநிமிர்ந்து உறுமியது!<br/>தாவித்தாண்டி சிறியது! சற்றுத்<br/>தயங்கினார்கள் வேடுவர்கள் என உணர்ந்து,<br/>தளர்ந்து தொங்கும் மார்புடைய<br/>தாய்ப்பன்றியையும் குட்டிகளையும் - ஆங்கு<br/>தழைத்திருந்த புதரோரம் ஒளித்துவிட்டுத் தான்மட்டும்<br/>ஆண்மையுடன் வழியில் ஒரு முடுக்கில் நின்று<br/>அடுத்துவரும் வேட்டுவரை அடித்துவீழ்த்த<br/>ஆயத்தமாய் அந்த ஆண்பன்றி;<br/>எதிர்பார்த்துக் கொண்டிருந்த ஆவேசத்தை - ஒரு<br/>புதிர் போலக் கண்டவேடன் - புல்லரித்துப் போனான்!<br/><br/>போர் முகத்தில் படைவீரர் - எதிரியிடம்<br/>புறங்காட்ட நேர்ந்த போதும்<br/>தான்மட்டும் தலைதாழாச் சிங்கமாய் நின்று<br/>வான்முட்டும் புகழைச் சேர்த்த - இந்த<br/>மலைநாடன் போன்றோ - இப்பன்றி<br/>மாவீரம் காட்டுதென்று மலைத்துப் போய்<br/>மனத்துக்குள் பாராட்டிப் போற்றி - அது<br/>மனத்துக்குள் நன்றாக வாழட்டுமென்று;<br/>வேலெறிந்து கொல்ல விரும்பாமல் - அந்த<br/>வேடன் வந்து சொன்ன கதையில்;<br/>வீழ்வதற்கும் அஞ்சாமல் வெகுண்டெழுந்த பன்றியின் உறுதியுடன்<br/>வீரநெஞ்சம் படைத்திட்ட நம் தலைவனின் உறுதியையும் ஒப்பிட்டான்!<br/><br/>எழுச்சியுடன் இதைநான் எடுத்துச் சொல்லி - உன்தலைவன்<br/>எஃகு உள்ளம் எவருக்கு வருமென்று கேட்டவுன்;<br/>மகிழ்ச்சியுடன் உன் அன்னை; என்னை,<br/>மார்புறத்தழுவி உச்சி மோந்து;<br/>நல்லவளடி நீ என்று நயந்துரைத்து<br/>வல்லவன் ஒருவனைத்தான் என் மகளுக்குத் தேர்ந்தெடுத்தாய்<br/>வாழி நீ என்று வாழ்த்தளித்தாள் - அப்போது<br/>வந்து நின்றான் உனைக்கவர்ந்த மன்னன்! - அந்தப்<br/>போர்மறவன் தோளில் சூடிய மாலையின் பூக்கள்<br/>புதர்களில்பட்டு உதிர்ந்திடும் நிலையில்; உன்<br/>இதழ்களில் ஊறும் தேனை நாடி - அவன்<br/>இளைப்பாற வருகின்றான்!<br/>இளையவளே நீ - அந்த<br/>இனியவன் களைப்பை உடனே ஆற்றிடுக!<br/>நன்றி உண்டா இல்லையா? நான்<br/>பன்றி கதை சொன்னதால் பழிவாங்கமாட்டாயே!<br/>வழி இதுதான் அவன் வருவதற்கு! இருவரும்<br/>விழிமூடாதீர் விடியுமட்டும்!<br/>நகருகின்றேன் நான் என்றாள்! தலைவி; தோழிக்கு,<br/>நன்றிகூற நா அசைப்பதற்குள் ஓடிவிட்டாள்!<br/><br/>"))
        databank.append(DataModel(title: "சுமந்தவள் சுமந்த சோகம்!", data: "பனிமுட்டை அடை காக்கும்<br/>பசும்புல்லின் தலைமீது<br/>மணித் தேர் அசைவது போல்<br/>மாதொருத்தி நடந்து வந்தாள்<br/>கனித் தமிழ்ச் சொல் உதிர்க்கும் புலமை மிக்காள்<br/>காக்கைப் பாடினி பொன்முடியார்க்குத் தமிழில் அவள் அக்காள்<br/>அதியமான் நெடுமானஞ்சி - வேள்பாரி வள்ளல் நாடன்<br/>சேரமான் மாவெண் கோவன்,<br/>கானப்பேர் தந்த வழுதி - மற்றும்<br/>சோழனாம் பெருநற்கிள்ளி<br/>ஆகியோர் புகழைப்பாடித் தமிழுக்கு<br/>அணி பல பூட்டி நின்ற<br/><br/>அறிவொளி ஒளவை நல்லாள்!<br/>காலைக் கதிரவன் போல் கை சிவக்கக் கொடை வழங்கும்<br/>காவலர்கள் வாழ்ந்திருந்த தமிழ்நாடு!<br/>ஓலை கொண்டொருவன் போர் என்று வந்துவிட்டால்<br/>மாலை சூடுகின்ற வேளையென்றும் பாராமல்<br/>தோளைத் தொத்துகின்ற பெண்கிளியைப் புறம் விடுத்து,<br/>வாளைக் கூரேற்றி வான் நோக்கி நிமிர்கின்ற வீரமறவர்களின் தாய்வீடு<br/><br/>பாலை, குறிஞ்சி, முல்லை, நெய்தல், மருதமென்று<br/>நிலம் வகுத்து வாழ்கின்ற தமிழரிடை<br/>சோலைப் பூவினம் போல் தமிழ்க் கவிதை மலரவைத்தாள்.<br/>தாயகத்துக் கற்புநிறை பிறழாத<br/>காதல் சொன்னாள் - களம் சென்றார் வீரம் சொன்னாள்.<br/>களத்தில்,<br/>சாதல் இன்னே வரட்டுமென்று சென்றுவிட்ட தலைமகனும்<br/>சந்தனத்து கட்டை போல மணக்கட்டும் இலக்கியத்தில் எனக் கூறிப்<br/>பாதம்முதல் உச்சிவரை குளிர்கின்ற தாய்க்குலத்தை<br/>எண்ணியெண்ணிப் பெருமை கொண்டாள், தன்<br/>பூவடிகள் புல்மீது அழுந்தியதால் பனித்துளிகள் அழிந்துவிடும்.<br/>போரடியில் நெல்மணி போல் பகைவர்களின் சிரம் வீழும்<br/>அமர்க்களத்தில்!<br/><br/>இதை நினைத்து,<br/>நடக்கின்றாள் ஒளவையம்மை!<br/>நாமணக்கும் தமிழம்மை! அறிவம்மை! அழகம்மை!<br/><br/>காலூன்றிப் பனியொற்றி வருகின்ற தாயின் முன்<br/>கோலூன்றிப் பிறிதொரு தாய் வந்து நின்றாள்.<br/><br/>\"அடி! தும்பை மலர் முடிதாங்கி!<br/>துவண்டு விட்ட காலிரண்டும் வாழைத் தண்டாய்க்<br/>குளிர்ந்து வரும் நேரத்தில நீ ஏன் வந்தாய்?<br/>விற்புருவம் என்றுன் ஆளன் அழைத்திட்ட காலம்போய்<br/>வில்லுருவம் முதுகில் பெற்ற இள நங்காய்!<br/>விழியிரண்டும் வேலென்றார் அன்று;<br/>வழி இரண்டு போனதென்று கோல்கொண்டாய் இன்று!<br/>தமிழ்நாட்டு முத்தெல்லாம் தீர்ந்ததென்று பல் கழற்றிப்<br/>பிற நாட்டு வாணிபத்தைச் செய்தவளே!<br/>எங்கெழுந்தாய்? ஏன் பதைப்பு?<br/>என்ன வாட்டம்?\" எனக் கேட்டுக்<br/>குறும்பு செய்தாள் ஒளவையம்மை!<br/><br/>\"வேலி போட முடியாது இளமைக்கென்று தெரிந்த பின்னும்<br/>கேலி பேசும் உன் போக்கு புரியவில்லை\"<br/>என மறித்து அந்தத் தாய் பேசலானாள்:-<br/><br/>\"புண்பட்ட என்னகத்தில் புலமைத் தாயே உந்தன் கேலி,<br/>பண்பட்ட தமிழ் மருந்தாய்ப் பாய்வதை நான் உணருகின்றேன்.<br/>கண்கெட்ட கிழவி எந்தன் கரம்தொட்டு வணங்கிச் சென்ற<br/>காளையை நான் மறக்கவில்லை.<br/>மண்முட்டி எழுந்து நிற்கும் மாங்கன்றின் தளிர்தான் மேனி.<br/>பொன்கொட்டிக் கொடுத்தாலும் வளையாத மனவலிமை<br/>புலிக்குட்டி விளையாடும் அவன் மடியில்!<br/>அந்த மகன் ஆசைமகன் அணிவகுத்தான்,<br/>சொந்த மண்ணின் நலங்காக்கப் பணி வகுத்தான்!\"<br/><br/>முடிக்கவில்லை முதியவளும்;<br/>பொறுக்கவில்லை ஒளவைக்குக்<br/>குறுக்கிட்டாள்.<br/>\"களம் சென்ற மகன் வீரம் புகழ்ந்து கூறிக்<br/>கண்கலங்காத் தாய் வீரம் காட்டுதலே தமிழர் மரபு!<br/>முதியவளே! மறவர் குல வழக்கிற்குப்<br/>புதியவளாய் இருக்கின்றாய்! - வியக்கின்றேன்\" என்றாள்.<br/><br/>\"முழுதும் கேள் பெருமாட்டி!<br/>பழுதொன்றும் காணமாட்டாய் என் நிலையில்!<br/>அழுதழுது என் விழிகள் வீங்ககியதை மறுக்கவில்லை<br/>பொழுதெல்லாம் கவலையினால் புலம்புவது பொய்யல்ல!<br/>ஏனென்று தெரிந்து கொள்வாய்<br/>நானொன்றும் மறைக்கவில்லை!\"<br/><br/>\"சொல்\" என்றாள் ஒளவை<br/>சொன்னாள் அவள்.<br/>\"கொற்றக் குடை சாயாத கொடித்தேர் மன்னர்<br/>கற்றறிந்த பாவாணர் கடமைமிகு மாவீரர்<br/>மற்றவர்க்கும் பெருந்தலைவர் அறிவாய் நன்று<br/>அவர் படையில் இருக்கின்றோர் பல்லோர் வீரர்<br/>ஆட்டுக்கடா அணிவகுத்துச் செல்வது போல்<br/>அரசன் பின் நடப்பதற்குத் தயங்க மாட்டார்.<br/>ஆயிரம் பேர் மன்னர்க்குப் பின்னால் இருந்தும்<br/>அவர் மட்டும் என் மகன்பால் அளவற்ற அன்பு கொண்டார்.<br/>போர்முனையில் ஓய்வெடுத்துக் கிடக்கும்போது<br/>\"தேறல்\" என்னும் உற்சாகத் சாறுதனைத் திறல்மறவர்<br/>அருந்துதற்குத் திரண்டு நிற்பார்.<br/>அப்போது மன்னரவர் என்மகனை அருகழைத்து<br/>அனைவரினும் அதிகபானம் அளித்து மகிழ்வார்.<br/>என் வாட்டம் இப்போது கேட்பாயம்மா!<br/>என்புகளைத் தெப்பமாக்கி நரி மிதக்கும் போர்க்களத்தில்<br/>தலையுருட்டிப் பந்தாடும் பறவையினம்!<br/>அத்தலையில ஒரு தலையாய் என்மகன் தலையும்<br/>இருந்தாலன்றோ இறைவனளித்த தேறலுக்கும் பெருமையுண்டு<br/>மன்னன் அன்பை மிகையாய்ப் பெற்றான்<br/>மண்டையில் தேறலும் அதிகம் பெற்றான்<br/>மாண்டனன் போரில்; புகழ்<br/>பூண்டனன் எனும் காட்சி<br/>காண்டிடல் அரிதோ எனக் கலங்கினேன் அம்மா!<br/><br/>தவறா?\" என்றாள்.<br/>அந்தத் தாயாக நான் இல்லையே என்று<br/>அகமிக நொந்தாள் ஒளவைப் பிராட்டி!"))
        
        databank.append(DataModel(title: "துணை நின்றாள் தோழி!", data: "தளிர் என்றால் கொழுந்தன்றோ!<br/>தடம் என்றால் வழியன்றோ!<br/>அஃதேபோல்<br/>தலைவன் என்று துணைவனையும்<br/>தலைவி என்று துணைவியையும் - பழந்<br/>தமிழ் நூல்கள் குறிப்பது வழக்கமன்றோ!<br/><br/>களவு வழியில் தலைவன் தலைவியின் இன்பக்<br/>காதல் அரும்பிடும் போதெல்லாம் - தக்க<br/>உளவுகள் கூறிஅதனை மலர்ந்திடச் செய்வர்<br/>உயிர்த் தோழனும் தோழியும் என்று;<br/>உரைத்திடும் இலக்கியம் தமிழனுக்குண்டு - தலை<br/>நரைத்திடு கிழவரும் நன்றாய் அதனைச் சுவைப்பதுமுண்டு!<br/><br/>ஐங்குறு நூறு எனும்<br/>பைந்தமிழ்ப்பனுவலில்<br/>நெய்தல், மருதம், குறிஞ்சி, முல்லையினும் - மழை<br/>பெய்தல் பொய்த்த பாலையிலும் - பிணைந்த காதலர்<br/>எய்திடும் இன்ப நிகழ்ச்சிகளை - மாலையில்<br/>கொய்திடும் மலர்களென வழங்குவர் புலவர்!<br/><br/>ஓரம்போகியார் எனும் ஒரு கவிஞர்,<br/>காரம்போகாக் கடுகெனத் தருவார் மருதப்பாடல்!<br/>குறிஞ்சி நிலத்தில் கூவுகின்ற - காதல்<br/>குயிலினத்தின் குன்றா அன்பைக்<br/>கவிக்கோமான் கபிலர் என்பார் - இப்<br/><br/>புவிக்கோர் விருந்தாய்ப் படைத்திடுவார்!<br/>ஆயனார் வாழும் முல்லை நிலத்தில்<br/>ஆவிநிகர்க் காதலர் ஆடி மகிழ்வதை<br/>பேயனார் எனும் கவிக்கோ - தமிழர்<br/>பெருமைக்கோர் சான்றாகப் பேசிடுவார்!<br/><br/>அம்மூவனார் எனும் புலவரோ<br/>அலை தழுவும் நெய்தல் நிலத்தில - காதல்<br/>கலைபயில்வோர் களித்திடும் காட்சி<br/>கவினுற வரைந்திடுவார்!<br/><br/>பாலை நிலத்திலும் - காதல்<br/>பண்பாடிடும் பறவைகள் உண்டென்று - அவர்தம்<br/>பிரிவின் வேதனை உரைத்திடுவார்;<br/>பரிவுடனே ஓதலாந்தையார் எனும் புலவர்!<br/><br/>ஐந்து வகை நிலம் வாழும் அறிவு ஆறறிவு மாந்தர்கள்,<br/>ஆய்ந்து தெளியும் அறிவிலா ஊர்வன, நடப்பன, பறப்பன,<br/>அடவிகள், அருவிகள், அழகுறு சோலைகள்,<br/>அடுக்கடுக்காய் அமைந்த மலைத்தொடர்கள்,<br/><br/>அலைக்கரத்தால் தாலாட்டும் கடற்பரப்பு,<br/>அங்கெல்லாம் உயிர் இனத்தின் வாழ்க்கை முறை,<br/>அத்தனையும் உற்று நோக்கி - தமிழில்<br/>முத்தனைய பாடல்கள் ஐநூறு தந்திட்டார்!<br/>சத்துநிறை அகவாழ்வின் அமுத ஊற்றுக்களாம் - அவற்றுள்;<br/>முதலில் ஒரு பாட்டுக்குள்<br/>மூழ்கித் திளைத்திடுவோம்!<br/><br/>நெய்தல் நிலத்தில்<br/><br/>ஓலமிடும் கடலோரம்<br/>கோலம் செய் பொன்மேனிக்<br/>கோதையொருத்தி;<br/>மணல் மேட்டில் சாய்ந்தவாறு - மேற்கின்<br/>தணல் வண்ணம் பார்த்திருந்தாள்!<br/><br/>அணிமலர்ப் புன்னையின் அருகேயிருந்து - அவள்<br/>பிணிதீர்க்கும் மருத்துவன் வந்து சேர்ந்தான்!<br/><br/>\"வந்தீரோ மருத்துவரே\" எனக்குதித்து<br/>\"தந்திடுவீர் மருந்தென்று\" தலைசாய்த்தாள் அவன் தோளில்!<br/><br/>\"காலை அரும்பிப் பகலெல்லாம் போதாகி<br/>மாலை மலரும் இந்நோய்க்கு<br/>இருவரும்தான் ஆட்பட்டுத் தவிக்கின்றோம் - அதனாலே;<br/>ஒருவருக்கொருவர் மருந்து உதவி தவிர்க்கலாம் நோய்,\" என்று<br/>தழுவினான் தங்கச் சிலையை!<br/><br/>விரிந்து பரந்த கடற்கரை மெத்தையில்<br/>விழுந்தனர், புரண்டனர், எழுந்தனர், மகிழ்ந்தனர்!<br/><br/>\"விழியில் என்ன நீர்? கடல் மணல் பட்டதாலா கண்ணே?\" - என்று<br/>மொழியில் ஒரு கொஞ்சல் கலந்து ஊதிட வந்தவனைத் தடுத்துவிட்டு<br/>\"ஊதுகின்றீர் என் கண்ணில்; நன்றி! நன்றி! ஆனால் நான்<br/>ஓதுகின்றேன் உமது காதில் பல நாளாய்!<br/>அதைக் கேட்காமல் நீர் இருப்பதால்தான்;<br/>அருவிபோல் நீர் என் விழியில் அறிக\" என்றாள்!<br/><br/>\"களவியல் விடுத்திட வேண்டும்<br/>கற்பியல் தொடங்கிட வேண்டும் - உன்<br/>உளமது மகிழ உடனே நம் மணம்<br/>உவகை பொங்கிட நட ந்திட வேண்டும்<br/>மதுவூறும் இதழ்ப்பூவே! மணியே! மானே!<br/>இதுதானே உன் ஆவல்?\" எனக் கேட்டான்.<br/><br/>குனிந்து தலையசைத்தாள் \"ஆம்\" என்று,<br/>குங்குமமாய்க் கன்னம் சிவக்க நாணமுற்று;<br/><br/>கோடு கிழித்துக் கால் பெருவிரலால்<br/>கோலம் போட்டாள் கொள்ளை இன்பம் நெஞ்சில் குவிய!<br/><br/>வீணையின் குடம்போல விம்மிப் புடைத்த அவள் பின்னழகை<br/>விரகதாப உணர்வோடு தட்டிக் கொடுத்துத் தலைவனும்;<br/>\"வீணாக வருந்தாதே கண்ணே!<br/>விண்ணு‘றும் சந்திரனின் சாட்சியாகச் சொல்வேன்;<br/>விரைவில் உனைக் கரம் பிடித்து மணப்பேன்<br/>விடுதுயரம்! இடையில் சில நாட்கள்;<br/>இவ்வுலகின் துன்பப் பகுதியை - இருவருமே<br/>எட்டிப்பாராமல் இன்பத்தில் கலந்திடுவோம்!<br/>தலையில் ஒரு பலாக்கனி சுமந்து கயிற்றின்மேல்<br/>நிலை தவறாமல் நடப்பதுதான் இல்வாழ்வு!<br/>உலையில் இட்ட சோறுபோல் கொதித்தல் நலமா? - அமைதி<br/>அலையில் ஆடும் படகுபோல் மிதத்தல் நலமா? - அதனாலே<br/>கலையழகே! என் கலாபமே! கண்மணியே!<br/>கணவன் மனைவி ஆவதற்குச் சற்றுக் காத்திருப்போம்! அதுவரையில்<br/>காதல் எல்லை எதுவென்று - அந்தக்<br/>கடலின் ஆழம் கண்டறிவோம்!\" என்றுரைத்தான்.<br/><br/>அவளோ;<br/>அன்பன் கரம்பட்டுச் சிலிர்த்துப்போய்,<br/>என்புதோல் நரம்பனைத்தும் சூடேறி;<br/>\"மன்பதைக்கழகா! உந்தன் மனம்போல் நடக்கட்டும்\" - என்று<br/>தன்புலன் அத்தனையும் அவன் தாளடி சேர்த்து நின்றாள்!<br/><br/>சுருண்டு நெளிந்த அவள் கூந்தலைக் கோதியவாறு - அவனும்;<br/>\"சுகமே! சுவையே! பழச்சுளையே! பாலமுதே! - அதோ<br/>இருண்டு கிடக்கும் மண்டபம் அழைக்குது! என்<br/>இதயமே வா; உடல் இன்பத்தைப் பருகலாம்\" என்றான்!<br/><br/>மருண்டது மானின் விழி அக்கணம் எனினும் - மறுகணம் அவன்<br/>உருண்டு திரண்ட தோளிலே, உரியவள் தொத்திக் கொண்டாள்!<br/><br/>நிலவின் ஒளிகூட நுழையாத<br/>மண்டபத்தில் நுழைவதற்குள்<br/>நில்லுங்கள் என்றோர் ஒலி! - அவர்களின்<br/>நேரம் தெரியாமல் தடுத்தது பாபம்!<br/><br/>கோழியொன்று குஞ்சுதனை - வல்லூறு<br/>கொல்லாமல் காப்பது போல்; அவளின்<br/>தோழி வந்தாள்! தொடாதே அந்தத்<br/>தோகையை என்று எதிர்நின்றாள்!<br/><br/>தேன் குடிக்கப் போகின்ற வண்டை - வீணே<br/>ஏன் தடுக்கின்றாய்? என்று<br/>விழியாலே கேட்டான் - \"இந்தப்<br/>பழி மிகப் பொல்லாது\" என்றான்!<br/><br/>\"குன்றெடுக்கும் நெடுந்தோள் கொண்டவனே! - பகையை<br/>வென்றெடுக்கும் ஆற்றல் பெற்றவனே!<br/>குறையொன்றும் காணவில்லை உமது அன்பில்!<br/>நிறை நெஞ்ச வாழ்த்து உண்டு ஏற்றிடுக! - எனினும்;<br/>மரபொன்று உண்டல்லவோ தமிழர்க்கு? அதை நீவிர்<br/>மறந்துவிடல் கண்டல்லவோ நினைவூட்டுகின்றேன்!<br/><br/>மணம் புரிந்து மனையறம் காணாமல் - கடற்கரை<br/>மணல்மீதும் மண்டபத்துள்ளம் காதல் கீதம்<br/>மறைவாக இசைத்து மகிழ்வதற்கு - இந்த<br/>மகளைப் பெற்றோர் நீண்ட நாள் ஒப்பமாட்டார்!<br/>இந்த ஊருக்குத் தலைவன்தானே நீர்-<br/>வெந்த உளத்தோடு ஒரு சேதி சொல்கின்றேன் - கேட்டிடுக!<br/><br/>இந்தத் தொண்டித்துறையில்<br/><br/>உடல் வியர்க்க உழைப்பைச் சிந்தி உயிரைப் பணயம் வைத்து<br/>கடல் மீது சென்று மீனவர் பிடித்த கொழுத்த மீனை<br/>பறப்பதற்குச் சிறகில் பலமில்லா கொக்கொன்று<br/>பறிப்பதற்குக் காத்திருக்கும் காட்சிதனைக் கண்டிலையோ<br/><br/>அதுபோல<br/><br/>பதுமைபோல் திகழ்கின்ற இத் தலைமகளை<br/>வதுவை நீ புரிவதற்கு நாள் கழித்ததால்<br/>முதுமை நிறைப் பெரியோர்கள் - கொக்கனையா\"<br/>முந்திக்கொண்டு வருகின்றார் இவளை மணமுடிக்க!<br/>இலவு காத்த கிளிபோலாக உனக்கு விருப்பமெனில்;<br/>இன்னும் பலநாள் தள்ளிப்போடு மணவிழாவை!<br/>இல்லையெனில் இன்றைக்கே - இந்த<br/>முல்லையினை மணப்பதற்கு நாள் குறித்திடுக!\"<br/><br/>தோழி இதைச் சொன்னவுடன் - திடுக்கிட்டு - அவன்<br/>\"ஆழிமுத்தை அணிவதற்கு அட்டியுண்டோ?<br/>வாழிநீ தோழி! வாழி! வாழியவே! இனி<br/>நாழிகை நேரம் பார்ப்பதற்கு எண்ணமில்லை;<br/>வாளைமீனை; வட்டமிடும் கிழப்பறவைக்கு இரையாக்க மாட்டேன்!<br/>நாளையே திருமணம்; நடக்கட்டும் ஏற்பாடெ\"ன்றான்!<br/><br/>உண்மையைச் சொல்லாமற் சொல்லி<br/>உவமையால் விளக்கம் தந்த<br/>வண்மை நிறை தோழியின் திறம் வியந்து<br/>வளமிகு பாடல் அம்மூவனார் வழங்கினார்!"))
        
        databank.append(DataModel(title: "மானங்காத்த மறவன்!", data: "மயிர் நீப்பின் வாழாத கவரிமான் போல்<br/>மானம் வரின் உயிர்நீப்பர் எனும் குறளுக்கு;<br/><br/>தயிர் கடைந்தால் தலைகாட்டும் வெண்ணெய் போன்று<br/>தமிழ்ச்சங்க காலத்து வீரன் வாழ்வே உண்மைச் சான்று.<br/><br/>தவழ்ந்து வரும் தன் மகவை<br/>முத்தமிடுவதற்கும் குனிந்திடாமல்<br/><br/>தலைக்குமேலே தூக்கச் சொல்லிக் கொஞ்சுகின்ற<br/>தன்மானத் தமிழ்ச்சிங்கம் அவன்!<br/><br/>தாமரை மொட்டிரண்டை ஏந்தி வரும்<br/>தன் பட்டத்து ராணியையும்<br/><br/>நெளியாமல் நிமிர்ந்து நின்றே<br/>நெஞ்சகத்தில் அணைத்துக் கொள்வான்.<br/><br/>நிலம் நோக்கும் வழக்கமின்றி<br/>நேர்ப் பார்வை செலுத்தித்தான்<br/><br/>நிலவு முகத்தரசி<br/>நெற்றியிலே இதழ் பதிப்பான்.<br/><br/>படுத்திருக்கும்போதும் பஞ்சணையில்<br/>வினாக்குறிபோல் வளைய மாட்டான் - வில்லில்<br/>தொடுத்திருக்கும் கணையைப் போல்<br/>கால்நீட்டித் துயில் கொள்வான்!<br/><br/>\"வளையாத முதுகெலும்பு பெற்றவனோ?<br/>வணக்கம் எனும் பொருள்<br/>விளையாத வயற்காடோ அவன் இதயம்?\" என வியந்து,<br/>வாய் பிளந்து மூக்கின்மேல் விரல் வைத்து - அவனை<br/>வாழ்த்தியவர் பலருண்டு!<br/><br/>\"இரும்பொறை\" மரபில் வந்த இணையிலா வீரனவன்.<br/>இரும்பொத்த கணையமரம் போல கால் படைத்த சேரனவன்; - அதனாலே<br/>சேரமான் கணைக்கால் இரும்பொறை யென்னும்<br/>செந்தமிழ்ப் பெயருடன் செங்கோல் ஏந்தினான்.<br/><br/>தொண்டித் துறைமுகம் அவன் ஆட்சித் தலைநகர் - அதனை<br/>\"டிண்டிஸ்\" என்று யவனர்கள் அழைத்தனர்!<br/><br/>மூவன் என்னும் முடியுடை வேந்தனை வீழ்த்தி - அவன்<br/>முகவாய் நொறுக்கிப் பற்களைப் பெயர்த்து - தனது<br/><br/>கோட்டை வாயிற் கதவின் பெரிய<br/>பூட்டின் மீது பொருத்தி வைத்தான் என்று<br/>\"களவழி நாற்பது\" பாடிய பொய்கையார் எனும்புலவர் - அவனது<br/>களப்புகழ் பலபடக் கூறிக் களித்திடுவாரே!<br/>தமிழரிடை ஒற்றுமை என்பது அத்திப்பூ - ஆடிப்பிறை - அதனாலே<br/>தமக்கிடையே போர் தொடுத்து தமிழ் இனம் பிளவுபட்ட சேதி கூறும்<br/>வரலாற்று ஏடுகளின் வரிகளாக ஆகிவிட்டார் - தமிழர்<br/>வாளாற்றல் காட்டுதற்கு வரிந்து கட்டி இனத்துக்குள் இரண்டுபட்டார்!<br/><br/>செங்கணான் எனும் சோழன்,<br/>செருமுனையில் சிறுத்தை போன்றான்!<br/>எங்கணும் வெற்றி கண்டு<br/>புகழ்வானில் பறக்க விட்டான் புலிக் கொடியை!<br/><br/>அது கேட்டுச் சேரமன்னன்<br/>ஆர்த்தெழுந்தான்!<br/>அணி தேர்ப்புரவி ஆட்பெரும்படை<br/>அவன் ஆணை கேட்டுப் பின்தொடர,<br/><br/>\"மங்குமோ எந்தன் வீரம்?<br/>என் மாவலித் தோளின் முன்னே<br/>தங்குமோ சோழன் பெற்ற வெற்றி\"யென்று<br/>கணைக்கால் இரும்பொறை கர்ச்சித்து<br/>கங்குல் கிழித்தெழும் ஒளியாக எதிர்ப்பட்டான்!<br/><br/>முரசுகள் போலே மத்தகம் படைத்த<br/>முரட்டு யானைகள் மோதிடவே<br/>அரசுத் தேர்களின் அச்சாணி கழன்று<br/>ஆயிரம் சுக்கல்கள் ஆயினவே!<br/><br/>குளத்து நீரில் செத்து மிதக்கும்<br/>குறுங்கொண்டை மீன் போலே<br/>குதிரைகள் குளம்படி<br/>குருதிச் சேற்றில் அழுந்தி பதிந்தனவே!<br/><br/>கட்கம் ஏந்திய காலாட் படையினில்<br/>காயம் ஒருசிலர் முதுகில் பட்டிடவே<br/>வெட்கம் துரத்த - அவர்கள்<br/>வீழ்ந்து மடிந்தனரே!<br/><br/>உறுதி முழக்கமும் குருதிப் புனலும்<br/>இறுதிப் பயணமும் எங்கும் நிறைந்த களம்!<br/>பொருதிடும் இருவரில் வெல்வது யாரென<br/>அறுதியிட முடியாமல் அமைந்த களம்!<br/><br/>ஆளுக்கொரு மின்னலை எடுத்து<br/>வாளுக்குப் பதிலாக வீசினரோ - எனச்<br/><br/>சோழன் சுழற்றும் வாள்பட்டுச்<br/>சூரர் தலைகள் ஆயிரம் உருளும்!<br/><br/>சேரன் வீசும் வாளின் பயனாய்<br/>செக்கர் வான்போல் சிவந்தது நிலமும்!<br/><br/>ஈட்டியின் பாய்ச்சல் கண்டு<br/>இமை கொட்டியவன் கோழை எனும்<br/><br/>இலக்கணத்தை இருவருமே<br/>இதயத்தில் பதித்தவர்தான் என்றாலும்<br/><br/>சேற்றில் சிக்கிய களிறெனவே - இறுதியில்<br/>சேரமான் இரும்பொறை சிக்கிவிட்டான்!<br/><br/>ஆற்றல் முழுதும் காட்டி நின்றான்;<br/>ஆயினும் சோழன் முன்னே அவன் தோற்று நின்றான்!<br/><br/>திருப்போர்ப் புறமெனும் சமர்க் களத்தில்<br/>திண்தோள் சேரன் தோற்றவுடன் -<br/><br/>அடடா; அவனைச் சோழமன்னன்<br/>குடவாயிற்கோட்டச் சிறைதனில் அடைத்தான்.<br/><br/>உறைவிட்டெழுந்த உடைவாள் பட்டு<br/>உயிர்விட்டுப் போரில் புகழ் சேர்க்க முடியாமல்<br/><br/>சிறைப்பட்டு வாழும் நிலை வந்ததே என்று<br/>சிந்தை அணு ஒவ்வொன்றும் நோக வாடினான் சேரன்<br/><br/>தாகமம் ஒருநாள் அவன் நாவை உலத்த - சிறைக்<br/>காவல் வீரனை அருகே அழைத்து -<br/><br/>தண்ணீர் விரைவில் கொணர்க என்றான் - சற்றுத்<br/>தாமதமாகக் குவளையில் தண்ணீர்<br/><br/>கொண்டு வந்து கொடுத்ததினாலே - நெருப்பை<br/>மொண்டு தகித்தது சேரனின் நெஞ்சம்!<br/><br/>பண்டு வாழ்ந்த குலப் பெருமையெலாம்<br/>துண்டுபட்டுப் போனதெனத் துவண்டுவிட்டான்!<br/><br/>ஏடொன்று எழுதிவைத்து<br/>எதிர்காலத் தமிழகமே - அதனைப்<br/>பாடென்று விட்டுச் சென்றான்!<br/><br/>\"பிள்ளையொன்று இறந்து பிறப்பினும் - சதைப்<br/>பிண்டமொன்று பிறந்து தொலைக்கினும்<br/><br/>மூப்பு கொண்டு மடிந்திடினும்<br/>முடிவிலொரு நோய் கண்டு முடிந்திடினும்<br/><br/>போரில் கலந்து விழுப்புண்பெற்று - மானமிகு<br/>வீரர் என மாண்டிடாத பழி துடைப்பதற்காக<br/><br/>வாள் கொண்டு உடலில் கீறி -<br/>வான்புகழ்; வாழ்வில் சேர்த்தபின்பே<br/><br/>உடலடக்கம் செய்திடும் மானமரபே<br/>அடலேறுகள் மலிந்திடும் தமிழர் மரபு!<br/><br/>அந்தப்<br/><br/>பண்பாட்டு மரபு வழி வந்துதித்த நானோ - மனப்<br/>புண்பட்டு பகையரசின் சிறைப்பட்டு நாய்போல<br/><br/>தண்ணீர்க்கும் காத்திருக்கலானேன் - இனி<br/>உண்ணீர் என்று பன்னீர் தந்து உபசரித்தபோதும்<br/><br/>தகுதியன்று இனி உயிர்வாழ்தல் - அதனாலே<br/>தமிழ்த்தாயே! என் மானங்காக்க<br/><br/>உன் மடிதனிலே விழுகின்றேன்;<br/>தாங்கிக் கொள்க!\"<br/><br/>இப்படி<br/><br/>ஏழடிப் புறப்பாடல் ஒன்றை எழுதிவிட்டு - சிங்க<br/>ஏறுபோல் சேரமன்னன் பெயரைப் புகழில் நட்டான்!<br/><br/>எப்படிப்பட்டோர் இருந்தார் மறைந்தார் என்பது - அவரவர்<br/>எச்சத்தாற் காணப்படும் என்பதற்கொப்ப<br/><br/>சேரமான் கணைக்கால் இரும்பொறை<br/>வீரவாளினை ஓலையாய்க் கொண்டு<br/><br/>தீட்டிய புறப்பாடலை<br/>நீட்டுகின்றேன் உம்மிடையே -<br/><br/>படித்துப்பாரீர்! - உணர்ச்சியால்<br/>துடித்துப் போவீர்!"))
        
        databank.append(DataModel(title: "யாதும் ஊரே; யாவரும் கேளிர்!", data: "மூன்று கூறாக ஆவதற்கு முன்பிருந்த<br/>முகவைப் பெரு மாவட்டத்தில் மூதறிஞர் பண்டிதமணி<br/>கதிரேசன் செட்டியார் பிறந்தார் எனக் கேள்வியுற்றுக்<br/>களிப்புடனே மகிபாலன்பட்டிக்குச் சென்றிருந்தபோது<br/>பூங்குன்றமெனப் பழையநாள் இலக்கியமாம்<br/>புறநானூற்றுக் குறிப்பில் இருந்த ஊரின் பெயர்தான்<br/>மகிபாலன்பட்டியாக<br/>மாறிற்றென்று, பெரும்புலவர்<br/>மாணிக்கனார் உரைக்கக் கேட்டு<br/>மகிழ்ச்சியிலே திளைத்துப் போனேன்.<br/><br/>கணியன் பூங்குன்றனார் எனும் அவ்வூர்க் கவிஞன்<br/>கற்குகையொன்றில் செதுக்கியுள்ள கவிதையினைக்<br/>காண வாரீர் என அழைத்திடவே; சென்று கண்டேன்! - அந்தப்<br/>பழம்புலவன் கரங்கொண்டு செதுக்கியதோ - அன்றி<br/>பார்புகழும் அப்பாட்டைப் பின்வந்தோர் செதுக்கியதோ?<br/>யார் செயலாய் இருந்திடினும் அப்பாட்டில்<br/>\"யாதும் ஊரே யாவரும் கேளிர்\" எனும்<br/>அறைகூவல்தனை அற்றைநாளில் ஒரு தமிழன்<br/>நிறைகுடமாய்த் திகழும் தன் நெஞ்சத்தைத் திறந்துகாட்டி<br/>விரிகடல்சூழ் உலக முழுதும் நம் ஊரே என்றும்<br/>விழியும் ஒளியும் போல் மக்கள் எலாம் நம் உறவே என்றும் - புதிய<br/><br/>வெளிச்சத்தால் பொல்லாத இருள் கிழித்து<br/>வேற்றுமையின் வேரறுத்துப் புரட்சி செய்தான்!<br/><br/>பூத்தமிழில் வண்டாகப் புணர்ந்து கலந்து - இந்தப்<br/>பூமிக்கே பொதுக்கவிதைக் கனியொன்றைத் தந்த கவிஞன்<br/>பூங்குன்றந்தனில் பிறந்த புலவன் கணியன்தான் என்கின்றபோது<br/><br/>புல்லரித்துப் பூரித்துப் போகுதம்மா; நம் எண்சாண் உடம்பு!<br/>அந்நாளில் புலவரெல்‘ம் அகிலத்தை ஆளுகின்ற<br/><br/>அரசர்களின் அவைக்களத்தில் அரிய தமிழ்க் கவிகள்பாடி<br/>ஆனையென்றும் சேனையென்றும் ஆயிரம் பொற்கிழிகள் என்றும்<br/>மானியங்கள் மற்றும் பல பரிசில் என்றும் மலைமலையாய்ப் பெற்றிடுவர்!<br/><br/>இந்நாளில் சில புலவர்போல் வாழ்வார்க்கு வாழ்த்துரைத்து<br/>இன்னலுற்று அவர் தாழ்வாராயின் அக்கணமே சிறகடித்து<br/>அற்றகுளத்துப் பறவைபோல அடுத்தகுளம் பார்க்கின்ற<br/>இயல்புடையார் அப்போதும் சிலர் இருந்திடத்தான் செய்தார்கள்!<br/>கணியூர்ப் பூங்குன்றனோ; அவர்கண்டு கவலைமிக்க கொண்டதனால்<br/>அணிமணிகள் தமிழில் செய்து அரசர்க்கும் வள்ளல்கட்கும்<br/>பூட்டி மகிழ்வதிலே ஆர்வமொரு கடுகளவும் காட்டாமல் இருந்திடவே;<br/>நீட்டி முழக்கிச் சிலபேர்; \"அரசை அணுகிப் பரிசு பெறப்<br/>பாடல்களை இயற்றிடுக!\" எனக் கேட்டபோது-கணியன்;<br/>பக்குவமாய் விடையளித்து அவர் விருப்பமதைத் தட்டிக் கழித்தான்!<br/><br/>\"நாடு பற்றிப் பாடுகின்றீர்-நன்று! நன்று!<br/>நாடாளும் மன்னர் பற்றிப் பாடுகின்றீர்- அதுவும் நன்று!<br/>நாடுதனை மீட்பதற்கும் புதிதாகப் பறிப்பதற்கும்<br/>கேடுகளைச் சுமந்து விழுப்புண்கள் ஏந்துகின்ற<br/>பீடுநடை வீரர் பற்றிப் பாடுகின்றீர் - மிகவும் நன்று!<br/>ஏடுகொள்ளா இலக்கியங்கள் காதலையும் களம்புகுவோர் காதையையும்\"<br/>எடுத்தியம்பும் காரணத்தால் என்வழியைத் தனிவழியாய் ஆக்கிக்கொண்டு<br/><br/>எல்லா ஊரும் எமது ஊரே<br/>எல்லா மனிதரும் எமது உறவே - என<br/>எழுதத் தொடங்குகின்றேன்\" என்றான்!<br/><br/>நற்றிணையில் வருகின்ற காதல் பாட்டொன்றைக் கூடக்<br/>கற்றறிவாளன் கணியன் பூங்குன்றன் எழுதுங்கால்<br/>\"மருந்துக்கு உதவுகின்ற மரத்தின் பட்டைகளை மட்டுமின்றி - அந்த<br/><br/>மரத்தையே வேரோடு பிடுங்குவதோ?\" எனக்கேட்டு,<br/>பய்னபடுவன எதனையும் பாதுகாக்க வேண்டுமெனப்<br/>பாங்காக அறிவுரையைப் பகருகின்றான் உலகிற்கு!<br/>\"ஒரே உலகம்\" எனும் கொள்கைதனை - மனிதரெல்லாம்<br/>\"ஒரே குலம்\" எனும் தத்துவத்தைப் புவியில்;<br/>கல் தோன்றி மண் தோன்றாக் காலத்திற்கு<br/><br/>முன் தோன்றிய மூத்த குடிப் பிறந்த தமிழ்க்<br/>கணியன் பூங்குன்றன், கவிதை நடைச்<br/>சொல்லூன்றி நிலைநாட்டுகின்றான்!<br/>விதிப்பயனே எல்லாம் என்றுரைத்துச் சோர்ந்து<br/>கதியற்றுக் கன்னத்தில் கைவைப்போர்தனைப் பார்த்து;<br/>\"நன்மைக்கும் தீமைக்கும் காரணங்கள்<br/>நமது செயல்களேயன்றி பிறரல்ல\" என்று<br/>நயம்படி உரைக்கின்றான் அந்த நற்றமிழ்ப் புலவன்!<br/><br/>அம்மட்டோ?<br/><br/>வேதனைகள் விரைந்து தொடர்வதும் - அவை<br/>விலகித் தொலைந்து போவதும் கூட<br/>நாம் ஏற்றிடும் செயல்களால் என்றே<br/>நன்கு கணித்து நவில்கின்றான் கணியன்!<br/>\"பிறந்த உயிர்கள் இந்த மண்ணில்<br/>இறந்து மறைதல் புதிதல்ல;<br/>இன்பமொன்றுக்காகவே வாழ்க்கை என்பதும்<br/>துன்பம் வந்திடின் வாழ்வை வெறுப்பதும்<br/>உறுதி கொண்ட மாந்தர்தம்<br/>உளத்திற்கேற்ற உயரிய செயலுமல்ல!<br/>ஆற்றொடு போகும் தெப்பம் போல்<br/>ஆடியும் அசைந்தும் அமைதி கண்டும்<br/>ஆருயிர்ப் பயணம் அவனியில் நடக்கும்! எனவே<br/>ஆன்றோர் காட்டிய அரிய வினைகளைத் தொடர்க!<br/>செழிப்பில் திளைத்தோர்க்கு சிரம் தாழ்த்தலுமில்லை;<br/>சிறியோர் எனப்படுவோரை இகழ்ந்துரைத்தலுமில்லை!\"<br/><br/>இவ்வாறு;<br/><br/>உலக ஒற்றுமை, ஒரு குலக் கொள்கை,<br/>உயிரின் தன்மை, உயரிய வினைகள் - வாழ்வில்<br/>உற்ற துன்பமும் உவகையின்பமும் ஒன்றெனக் கருதுதல்,<br/>உயர்ந்துள்ள செல்வர்க்குப் பணியாமை - வீணில்<br/>உழன்றிடும் சிறியோரை இகழாமை; எனப் பல<br/>வண்ணமிகு மலர்கள் கொண்டு - உயர்<br/>எண்ணங்களைத் தொடுத்தளிக்கும் பெரும்<br/>உள்ளம் காணுகின்றோம் இந்தக் கவிதையினில்; புது<br/>வெள்ளம் போல் இக்கருத்து பரவட்டும் காசினியில்!<br/>ஆனால் ஒன்று;<br/>ஊரெல்லாம் இணைந்து ஓருலகாய் ஆவதென்பதும்<br/>உறவுகொண்டே மனித குலம் ஒன்றே எனத் திகழ்வதும்<br/>உயரிய குறிக்கோளாம் என்றுரைத்த புலவன் கூட<br/>தனது ஊரை மறவாமல் பூங்குன்றமென இணைத்தே<br/>கணியன் பூங்குன்றன் எனும்<br/>கவிஞனாய் உலவியதைக் கவனமுடன்<br/>எண்ணிப் பார்த்தால் - அந்த மாமனிதனையும்<br/>மண்ணின் பற்று விடவில்லை என்பதுதான் உண்மையன்றோ?"))
        
        
        databank.append(DataModel(title: "மலர்மாரி பொழிகின்றேன்!", data: "பத்துப்பாட்டாம் குறிஞ்சிப் பாட்டில் - மலர்களைப்<br/>பட்டியல் போடும் பாங்கே; பாவையர்<br/><br/>சரம் தொடுத்ததுபோல் அமைந்திட்டதம்மா; இந்தச்<br/>சங்கத்தமிழின் பெருமையைச் சாற்றுதல் எளிதோ?<br/><br/>சங்கப்புலவர்கள் பாடிய தமிழை<br/>குங்குமம் இதழில் நூறுகிழமை<br/><br/>எளிய நடையில் எழுதிட முனைந்து<br/>என்னாலியன்ற பணியினை முடித்தேன்<br/><br/>கடல் குளித்து முத்தெடுக்கும் போதும்,<br/>கனிகள் வெட்டிப் பொன் குவிக்கும்போதும்,<br/><br/>களிப்பு துள்ளும் உள்ளத்தைப் போலே - இந்தக்<br/>கலைஞனது உள்ளமுமே துள்ளுவது உண்மை!<br/><br/>கருவூலப்பொருள்கூட அள்ள அள்ளக் குறையும்; - சங்கக்<br/>கவிப்பெரியோர்கள் கற்பனையோ அகழ்கின்ற ஊற்றாக நிறையும்!<br/><br/>பழம்புலவர்பெருமக்கள்; தமிழர் வீரம், காதல்,<br/>பண்பாடு போற்றுகின்ற வாழ்க்கை நெறிமுறைகள்;<br/><br/>பாட்டாலே தொகுத்தளித்து; நம்மைப் பிற<br/>நாட்டாரும் போற்றுகின்ற செயல் புரிந்தார்!<br/><br/>ஏடுகளாய் எழுத்துக்களாய் வாழ்கின்ற - அந்தப்<br/>பீடுநிறைப் பெரியோர்க்கு வணக்கம் சொல்லி;<br/><br/>காடுகளில் மலைச்சோலைகளில் கபிலப்புலவன்<br/>கண்டறிந்து குறிஞ்சிப்பாட்டில் கோடிட்டுக்காட்டியுள்ள;<br/><br/>மலரையெல்லாம் பறித்தெடுத்து என் மனம் பறித்த<br/>மாத்தமிழ்ச் சுவடிகள் மேல் மாரியெனப் பொழிந்திடுவேன்!"))
        
         databank.append(DataModel(title: "ஈழத் தமிழருக்கு கருணாநிதி கவிதை", data: "இலங்கைப் போரிலே செத்து மடியும் தமிழரெல்லாம்<br/>இந்தியா கை கொடுக்கும் என்று நம்பித் தான் இமை மூடுகின்றார்.<br/>உதவிக்கு நமை நாடுகின்ற அவர் தம் இல்லங்களில்<br/>உதிர்ந்து விட்ட இலைகளுக்குப் பின் இருக்கின்ற அரும்புகளைக்<br/>கருகாமல் காய்ந்திடாமல் காத்து வளர்த்து காலத்<br/>தருவாய் இருந்த தமிழினமே தரை மட்டமானது எனும்<br/>வரலாறுக்கு இங்குள்ள தமிழர் வழித் தோன்றல்களாகி விடாமல்;<br/><br/>வளர் பிறைகள் தேய்பிறைகளாகி நிரந்தர அமாவாசை நிலைத்துவிடாமல் -<br/>இடர் களைந்து இன்றே இலங்கைத் தமிழர்தம் உயிர் காத்து<br/>இனம் காத்தோம் எனும் பெருமூச்செறிந்திட வழி கண்டு<br/>இங்குள்ள நல்லோர் தரும் நன்கொடையாம் பரிவுத் தொகைகளை<br/>\"இதயமுள்ளோர் வாழ்க'' என்று இனிய நன்றி கூறிப் பெற்றுக் கொண்டு<br/>அவற்றையெல்லாம் பண்டங்களாக்கி உணவு உடை பொருள்களாக்கி<br/>அங்குள்ள உரியவர்க்குப் போய்க் கிட்டிட உகந்த வழி உடனே கண்டு<br/>சர்வ தேச அமைப்புகள் மூலமாக அனுப்பி வைக்கவிருக்கின்றோம் -<br/><br/>அது போய்ச் சேராது என்றும் அது ஓர் நாடகமென்றும் அவசரக்கார தம்பி ஒருவரும்<br/>அவையெலாம் வீணாக விடுதலைப் புலிகட்கே பயன்படுமென்று அம்மையார் ஒருவரும்<br/>அதனால் நிதி கொடுக்காதீர் - இலங்கைத் தமிழரை வாழ வைக்காதீர் என்று<br/>வெறிக் கூச்சல் போடுகின்றார் - அவற்றை நாம் பொருட்படுத்தாமல்<br/>வெற்றுக் கூச்சல் என்றே எண்ணிக்கொண்டு இன்னும் வேகமாக<br/>வெந்தணலில் கிடக்கின்ற ஈழத் தமிழருக்கு<br/>இதயத்தைத் தந்திடுவோம் - தேவையெனில்<br/>இன்னுயிரையும் வழங்கிடுவோம்!<br/>"))
         databank.append(DataModel(title: "கவிஞர்கள் தெளித்த பன்னீரும் வடித்த கண்­ரும்!", data: "உறை விட்டெழுந்தது வாள் என்றால்,<br/>ஒரு பகைப்புலம் ஒழிந்தது பார் என்று<br/>உறையூரில் ஆண்ட மன்னன் - தமிழ்<br/>உலகு புகழ் சோழ மன்னன்!<br/><br/>வளமுற்ற பொன்னி நிலப் பெருவேந்தன் - கண்ணில்<br/>குளமுற்றொருவர் வந்துவிட்டால் உளமுருகி வரவேற்று<br/>அளவற்ற பொற்குவியல் கொடையாய் வழங்கிடுவோன்<br/>குளமுற்றம் எனும் ஊரில் இயற்கை எய்தியதால்<br/>சோழன் குளமுற்றத்துத் துஞ்சிய கிள்ளிவளவன் என<br/>வாழும் வரலாற்று இலக்கியத்தில் நிலைத்திருப்போன்!<br/><br/>புறநானூறுக் கருவூலத்தில் இவன் பெருமைதனைப் பாடுகின்ற<br/>புகழ்க் கவிதை பல உண்டு! பாடிய புலவர்களும் பலராவர்!<br/><br/>\"பசுவின் காம்பை அறுத்திடும் பாவிகட்கும்<br/>பாவையின் கருவை மிதித்துச் சிதைத்தோர்க்கும்<br/>பாசமிகு பெற்றோரை வதைத்தோர்க்கும் மன்னிப்பு உண்டெனினும்<br/><br/>பார்முழுதும் தலைகீழாய்த் திசைமாறிக் கவிழ்ந்தாலும்<br/>பாதகராம் நன்றியினைக் கொன்றோர் வாழ்வு<br/>பாழ்பட்டே நாசமாகு\" மென்று - அவன்பால்<br/>பரிசுபெற்ற ஆலத்தூர்கிழார் எனும் பாவாணர்,<br/>பரிவுடனே நன்றிகூறி நல்வாழ்த்துச் சொன்னார்.<br/><br/>கருவூரைக் கிள்ளி மன்னன் முற்றுகையிட்டபோது,<br/>\"காவல் மரங்கள் சாய்கின்ற ஒலி கேட்டு;<br/>அரண்மனைக்குள் ஒளிந்துகொண்டு அஞ்சி நடுங்கும்<br/>ஆண்மையற்ற கோழையினை வெற்றிகொளல் பெருமையாமோ?\" என -<br/><br/>கிள்ளிவளவன் முன் சென்று அந்த ஆலத்தூர்கிழார்<br/>அள்ளிப் பொழிந்த அறிவுரைப் பாடல் கேட்டு<br/>துள்ளி வந்த படையுடனே திரும்பிச் சென்றான் -<br/>கொள்ளி மீது வீழ்ந்த புழு மீண்டது போல் மீண்டான் கருவூர் மன்னன்!<br/><br/>ஆலத்தூர் கிழாரை யடுத்து அரிய பாடலொன்று<br/>ஆவூர் மூலங்கிழார் அவன் மீது பாடியுள்ளார்.<br/>\"ஆற்றல்மிகு அடலேறே! அரசர்க்கரசே!<br/>அனல் பறக்கும்; நீ சினங் கொண்டால்!<br/>அருள்கொண்டு நீ பார்த்தாலோ அவ்விடத்தே,<br/>சுடர்ப் பொன்மணிகள் ஒளி உமிழும்!<br/>கோடைக் கதிரவனைக் குளிராக்கிக் காட்டிடவும்,<br/>குளிர்நிலவை தணலைப் போல் சூடாக்கித் தந்திடவும்,<br/>கொற்றவனே உன்னால்தான் முடியும்\" என்று<br/>நற்றமிழால் போற்றியுள்ளார் அப்புலவர்!<br/>\"காலனும் காலம் பார்ப்பான், உயிர் மொண்டு செல்ல; - சோழர் குலக்<br/><br/>காவலனே! நீயோ; களத்தில் பகைவர் உயிரை<br/>நினைத்தவுடன் ஒரு கணத்தில் போக்குகின்றாய்!\" - என்று<br/>வினை முடிக்கும் அவன் வீரத்தை வியந்துரைக்கும் கோவூர்க்கிழார்;<br/>\"விறகு வெட்டச் சென்றவர்க்குக் காட்டில் பொன் கிடைத்தாற்போல்<br/>வேந்தன் கிள்ளியிடம் வந்தோர்க்கெல்லாம் பரிசுப் போருள் கிட்டும்\" என்று கூறுகின்றார்.<br/><br/>\"தாய்ப்புலி; தன் குட்டிதனைக் காப்பது போல்<br/>தமிழ்க்குலம் காக்கின்ற வேந்தன் நீதான்!<br/>கரும்பிடைத் தேனும், கடைமடை வாளை மீனும்,<br/>கன்னியர் பறித்து வரும் செங்கழுநீர்ப் பூவும்,<br/>கழனியில் உழுவோர் கண்டெடுத்த ஆமைகளும்<br/>காவிரியின் நீர்ப் பெருக்கால்<br/>திரும்பிய பக்கமெலாம் நெல்மணியும்<br/>திகழ்சோழ வளநாட்டை ஆளுகின்ற கோவே!\" என்று<br/>இடைக்காடனார் எனும் புலவர்;<br/>ஏற்றிப் புகழுகின்றார்; கிள்ளிவளவனையே!<br/>\"வெயிலுக்கு மறைக்கின்ற குடையல்ல; வேந்தே - உந்தன் குடை; அது,<br/>வேதனைப்படாமல் குடிகளுக்கு நல்லாட்சி தருகின்ற வெண்கொற்றக் குடையாகும்.<br/>வெள்ளப் பாழ் வறட்சியினால் பாதிக்கப்பட்டாலும்<br/><br/>உள்ளம் தளராமல் உழைக்கின்றார் வேளாண் குடியினர்<br/>போர்முனையில் உன் வீரர் தீட்டுகின்ற வெற்றியெலாம்<br/>ஏர்முனையின் துணையின்றி நடவாது என்பதாலே;<br/>மழவர் குலம் வாழ வைக்க<br/>உழவர் குலம் போற்றி வாழ்க!\" என்று<br/>வெள்ளைக்குடி நாகனார் எனும் புலவர்<br/>வேந்தனுக்கு அறிவுரையும் வாழ்த்தும் சொன்னார்!<br/><br/>\"ஆடுகின்ற மங்கையரின் இடையைப் போல்<br/>நாடு முற்றும் வறட்சியினால் மெலிந்த போதும்<br/>கருணை மழை பொழிந்து எமைக் காக்கும்<br/>காவிரிசூழ் வளநாடன் கிள்ளிவளவன்\" என<br/>நல்லிறையனார் எனும் புலவர்<br/>நயமிகு உவமையுடன் போற்றுகின்றார்!<br/><br/>\"இயற்கையின் சிற்றத்தால் இவ்வுலகே அழிய நேரிடினும்<br/>எதற்கு நாம் அஞ்ச வேண்டும் கிள்ளிமன்னன் இருக்கையிலே\" என்று<br/>எண்ணற்ற புகழ் முத்துக் கவியாரம் தொடுத்தார்;<br/>எருக்காட்டூர்ப் புலவர் தாயங் கண்ணனார்! ...... அவர்<br/>தஞ்சைக் குளிக்கரைக்கு அருகில் வாழ்ந்த<br/>தமிழ்பூ புலவரேறு என கூறிடுவர் ஆய்வாளர்!<br/><br/>இவ்வாறு,<br/>ஆவூர் மூலங்கிழார், ஆலத்தூர்கிழார்,<br/>ஆடுதுறை மாசாத்தனார், ஐயூர் முடவனார்,<br/>நல்லிறையனாருடன், மாறோக்கத்து நப்பசலையார்,<br/>எருக்காட்டூரார், இடைக்காடனார்,<br/>வெள்ளைக்குடி நாகனார், கோவூர்கிழாரெனப்<br/>புலிகள் எல்லாம் சூழ்ந்துகொண்டு சிங்கத்தைப் புகழ<br/>புகழ்பெற்ற அச்சிங்கம் புள்ளிமான் ஒன்றைத் தேடிச்சென்று<br/>புறப்பாட்டால் வாழ்த்துரைத்து வணங்கிய கதை போல<br/>சிறுகுடி யெனும் சோழ நாட்டுச் சிற்றூரில்<br/>தருநிழல் போல ஒரு நண்பன் உண்டு கிள்ளிக்கு! அப்<br/>பெருங்குடிக் கிழவன் பண்ணன் நிலத்தில்<br/>அறுவடையாகும் நெல்மணி யாவும் வறியோர்க்கு<br/>அளித்தனன் நன்கொடையாக என<br/>அறிந்ததும் கிள்ளி அகமிக மகிழ்ந்து ஆங்கு சென்றான்.<br/>அவனக்குப் \"பசிப்பிணி மருத்துவன்\" என்று பட்டமும் ஈந்தான்!<br/><br/>\"வான் போலக் கொடை வழங்கும் பண்ணனே - நீ<br/>யான் வாழும் நாளும் சேர்த்து வாழியவே!\" என்று<br/>புறப்பாடல் ஒன்றெழுதி அவன் கையில் கொடுத்துப்<br/>புறப்பட்டான்; தலைநகராம் உறையூரை நோக்கி!<br/><br/>மாற்றார் எப்படை கொண்டுவரினும்; இவனிடம்<br/>தோற்றார் என்றே வீரவரலாற்றைத் தீட்டிய வளவன்;<br/>ஆவின் பாலொத்த அருள்வடியும் முகத்தழகன்,<br/>சாவின் பிடிதனிலே ஒரு நாள் சாய்ந்து விட்டான்...<br/>செய்தி கேட்டு செவிப்பறை கிழிந்தது போல்<br/>செந்தமிழ்ப் புலவர் கூட்டம் சிந்தியது கண்ணீர் அருவி!<br/><br/>\"இறந்தோரைப் புதைப்பதற்குத் தாழிதனைச் செய்வதிலே<br/>சிறந்தோரைக் கேட்கின்றேன்; எம்மன்னன்<br/>கிள்ளியினை வைத்துப் புதைக்கின்ற தாழி யெனில்<br/>கீழ்த்திசை மேற்றிசை வானத்தை வளைத்து வைத்து<br/>நிலவுலக முழுவதையும் குயவர் ஆழியாக அமைத்து<br/>நிமிர்ந்து நிற்கும் மேருமலையை மண்திரளாய்க் கொண்டன்றோ<br/>தாழி செய்தல் வேண்டும்!<br/><br/>பூத உடல் புதைப்பதெனில் பொருந்தும் உம் தாழி - கிள்ளியின்<br/>புகழ் உடலை வைப்பதற்கு இந்தப் பூதலமே ஏற்ற தாழி\" - என்று<br/>சோகமுடன் ஐயூர் முடிவனார் இரங்கற்பா எழுதும்போதும்<br/>சோழனது கீர்த்திக்கு எல்லையில்லை என்று கண்டார்!<br/><br/>\"நன்றியில்லா கூற்றுவனே! எத்தனைப் பகைவர்களைப் போரில்<br/>கொன்று தந்தான்; நீ தின்று கொழுத்தாய்!<br/>இன்று நீ அவனையே தீர்த்து விட்டாய் -<br/>இனி யார் உனக்கு இருக்கின்றார் தீனி போட?\" என்று<br/>ஆடுதுறை மாசாத்தனார்; அவன், களத்தில்<br/>நீடுபுகழ் பெற்ற கதை சொல்லிக் கண்ணீர் விட்டார்!<br/><br/>மாறோக்கத்துக் கவியரசி நப்பசலை எனும் பெருமாட்டி<br/>மாமன்னன் மறைவுற்ற செய்தியினை நம்பவில்லை.<br/>குளமுற்றம் அரண்மனைக்குப் பறந்து போனார் - அங்கே<br/>களம்பட்ட யானை போலக் காவிரிநாடன் கிடந்தான்;<br/>காயமேதும் உடலில் இன்றி!<br/><br/>உளம் பதறி நப்பசலை கேட்கின்றார்;<br/>உண்மைதானோ நான் காணும் காட்சி?<br/>உரைத்திடுவீர்; உறையூர் மன்னர்தம்<br/>உயிர் பறிக்கும் ஆற்றல் சாவுக்கு உண்டா சொல்வீர்!<br/><br/>பின் எப்படித்தான் நடந்ததிந்தச் செயல்?<br/>என் மனதில் பட்டதை நான் இயம்புகின்றேன் - கேளீர்! என்றார்.<br/><br/>* பொன் ஒளி சேர் மாலை யணிந்து<br/>மின்னொளி யாய் வாள் வீசி<br/>மண் மீது மாற்றார் படை அழிக்கும்<br/>திண் தேர் வளவனை யாரும் வெல்லுதல் அரிதே!<br/>வளவன் உயிர் கொண்ட சாவு; நெஞ்சில்<br/>வஞ்சப் பகை கொண்டோ; அன்றி நேர்நின்று தன்<br/>வலிமை காட்டிப் போர் புரிந்தோ - அன்றி<br/>வரிந்து கட்டி அவன் உடல் தீண்டி வளைத்துப் போட்டோ<br/><br/>வென்று விட்டதாய் நான் எண்ணவில்லை; அவனைக்<br/>கொன்று செல்ல வந்த சாவு; ஓலைச் சுவடியுடன் ஒரு<br/>புலவரைப் போல் வேடமிட்டு கை தொழுது அவன் உயிரைப்<br/>புதிய பரிசாகக் கோட்டுப் பெற்றிருக்கும் - அல்லால்<br/>அவன் உயிரை எடுப்பதற்கு<br/>எவனுக்கும் உடலில் உரமே இல்லை!<br/><br/>இவ்விதம்,<br/><br/>அப்பழுக்கற்ற அன்பின் பெருக்கத்தால்<br/>நப்பசலைப் பெருமாட்டி பாடியதும் உண்மைதானே!<br/>செப்பரிய திறமுடைய செம்பியனாம் கிள்ளியின்<br/>ஒப்பரிய புகழ்மணக்கும் தமிழர் நாட்டின்<br/>பெருமை யெலாம் காப்பதற்கு முன்வராமல்<br/>பேய்க் காற்றில் ஒடிந்து விழும் மரமாக இனப்பெருமை<br/>ஆகலாமோ? உணர்ந்து சொல்வீர்!"))
        databank.append(DataModel(title: "தேனாகச் சொட்டும்; தேளாகக் கொட்டும்!", data: "தோழி:<br/>என்னடி நேற்று விடியுமட்டும் வளையல் சத்தம்?<br/>எத்தனை கொடுத்தான்? நூறா, ஆயிரமா; நீ பெற்ற முத்தம்?<br/>இமை மூட முடியாமல் நான் பட்டபாடு கொஞ்சமா?<br/>இடி முழக்கம் எழுப்பியதே; அதுவும் ஒரு மஞ்சமா?<br/>சத்தம் போடாதவாறு கட்டில் வாங்க உன் வீட்டில் என்ன பஞ்சமா?<br/>சத்தியமாய்ச் சொல்லடி தலைவி; என்மீது உனக்கேதும் வஞ்சமா?<br/>திண்ணையில் ஒருத்தி படுத்திருக்கிறாள் என்பதும் தெரியாதா?<br/>திருமணமாகாத பருவமங்கை அவள் என்பதும் புரியாதா?<br/>ஒருநாள் மட்டுமே இந்த உலகம் இருக்குமெனக் கணக்கிட்டு இன்பத்<br/>திருநாள் முழுவதையும் நடத்தி முடித்துவிட்டீர்; வாழ்க! வாழ்க!<br/>இருந்தாலும் என்னுயிர்ப்பாவாய் ஒன்று கேட்பாயா? - இரவு,<br/>விருந்துண்ட மகிழ்ச்சியிலே எனக்கும் சிறிது செவி கொடுப்பாயா?<br/>மாம்பழம் இனிக்குதென்று மரத்தோடு பிடுங்கலாமா?<br/>மலர் மணம் இனிமையென்று கொடியோடு பெயர்க்கலாமா?<br/>அள்ளிப் பருகி நீரைத் தாகம் தணித்தலன்றி - ஆற்றின்<br/>வெள்ளத்தோடு வீழ்ந்தருந்தி அறிவுடையோர் மிதப்பதுண்டோ?<br/><br/>\"இன்பத்தைப் பற்றி உனக்கென்ன தெரியும்?\" - என்று என்னை<br/>எடுத்தெறிந்து பேசாமல் எதுவும் அளவு மீறினால் எழுகின்ற<br/>துன்பத்தைப் பற்றித் தொலைநோக்கில் சிந்தித்திடுக!<br/>தோழியுங்கள் இருவர் நன்மைக்கே சொல்லுகின்றாள்; எனக் கொண்டிடுக!<br/><br/>இரும்பினால் வார்த்ததல்ல மாந்தர் மேனி; எனவே<br/>கரும்புதான் காம இன்பம் எனினும் அதனை;<br/>விரும்பும்போதெல்லாம் கண்டபடி சுவைத்துத் திளைத்தால்<br/>துரும்பு போல் இளைத்துப் போகும் ஆண் பெண் வடிவிரண்டும் வளைந்து கூனி!<br/>உடற்கூறு வல்லுநர் என் பாட்டியம்மை; அவள் பிறர்க்கு<br/>உரைக்கின்ற வாழ்க்கை முறையை ஒளிந்திருந்து தினமும் கேட்டு நான்;<br/><br/>உணர்ந்துகொண்ட உண்மையைத்தான் - என்<br/>உற்ற தோழியாம் தலைவி உனக்குச் சொன்னேன்!<br/><br/>ஆறு திங்களுக்கு மேலாக உனை விடுத்து அகலாமல் - உன்<br/>ஆளன் அருகில்தானே இருக்கின்றான்;<br/>அப்படியென்னடி வந்தது நேற்று மட்டும் தோழி? உங்களிருவரையும்<br/>அணைபோட்டுத் தடுக்கத்தான் அவசரமாய்க் கூவியதோ கோழி?<br/><br/>தலைவி:<br/>கூவியது நிச்சயமாய்க் கோழியல்ல என்பதை - என்<br/>ஆவிநிகர்க் கண்ணாளன் நன்றாகப் புரிந்துகொண்டு<br/>\"குரல்மாற்றி உன் தோழிதான் இப்படிக்<br/>குறும்புத்தனம் செய்கின்றாள்\" எனக் கூறிவிட்டான்! அந்தத்<br/>திறல் மறவன் பிடிக்குள்ளே சிக்கிவிட்டால்<br/>குறள் பாட்டின் ஈரடிபோல் இணைந்துதான் கிடக்கத் தோன்றும்! அதுவும்;<br/>அறம் பொருள் இரண்டும் கடந்த இன்பத்துப்பால் என்னும் போது; அவனைப்<br/>புறம் தள்ளி பிரிந்து வரல் எனக்கு ஆகின்ற காரியமா?<br/>காலம் இன்னும் இருக்குதடி கண்மணியே, கதிர்மலர; - எனக் கூறிக்<br/>கோலம் வரைந்தான் விரல் நுனியால் என் உச்சி முதல் உள்ளங்கால் வரையில்!<br/>விரல்பட்டால் யாழின் நரம்பு இசையோடுதானே துடிக்கும் - அவன்<br/>கரம் பட்டுச் சுகஉலகச் சுற்றுலாவில் பறந்தேன்! - இதுவரையில்;<br/>காணாத இன்பத்தில் எனைநானே மறந்தேன்!<br/>காலைக்கதிர் உதித்தபிறகுதானடி - அந்தக்<br/>கள்வனின் செயலுக்கெல்லாம் காரணத்தை அறிந்தேன்!<br/><br/>இமை நான்கை இதழ்போலக் கவ்விக் கொண்டு - அவனளித்த<br/>சுவை முத்தம் அத்தனையும் தேனூறிய கற்கண்டு!<br/><br/>இடை வளைத்துக் கொடிபோல அவன் தோள்மீது சுற்றிக் கொண்டான்.<br/>\"இனி அணுப்பொழுதும் எனைப்பிரியாதே\" என அவனை நான் பற்றிக் கொண்டேன்!<br/><br/>தோழி:<br/>போதுமடி; பொற்கொடியே; பொற்கொடியே! எனக்கும் நீ<br/>போதை ஏற்றிவிடாதே! பிரிந்தென்னைப்<br/>பொருள் தேடப் போயுள்ள காதலன் வரும்வரையில்<br/>பொறுத்திருக்கும் என் மனத்தை<br/>வறுத்தெடுத்துத் தொலைக்காதே; எனை<br/>வாடவிட்டு வேடிக்கை பார்க்காதே!<br/><br/>தலைவி:<br/>களிப்பிலாடும் கானமயிலாகி - எதையும்<br/>ஒளிப்பின்றி உன்பால் உரைத்து விட்டேனடி<br/>கண்ணயர்ந்து நீ இரவெல்லாம் தூங்காததை - அவனோடு<br/>விண்ணுயர்ந்து பறந்ததாலே நான் உணர முடியவில்லை!<br/>தலைவியென்றும் தோழியென்றும் தகுதி பிரித்து நாம் பழகவில்லை!<br/>அலைகடல் போல் பரந்ததன்றோ நமது நட்பு; அதனாலே மன்னித்திடுக!<br/>விடியும்வரை நேற்றிரவு மட்டும் இன்ப விளையாட்டு ஏனென்று<br/>வினவினாயே; அதற்கு நானுந்தான்<br/>விடை கிடைக்காமல் வியப்புற்றேன் எனினும்<br/>விருந்து வலிய வலிய வரும்போது வீணில்<br/>அருந்தாமல் தட்டிக்கழிக்க மனமின்றி<br/>இரவுக்கு வாழ்த்துச் சொல்லி - இடைவிடா<br/>உறவுக்கு எனையளித்துச் சுகம் கண்டேன்.<br/>கிழக்கினில் சூரியன் எழுந்தபின் தானே;<br/>வழக்குக்கு மாறாக இரவு நடந்த கதைக்கு;<br/>விளக்கத்தைப் புரிந்து கொண்டேன் - மனக்<br/>கலக்கத்தால் தவித்து நின்றேன்!<br/><br/>தோழி:<br/>புதிர் போட்டுக்கொண்டே இருக்கின்றாய் தலைவி;<br/>கதிர் வந்தபின்னர் கவலை ஏன் உனக்கு?<br/>எதிர் நின்று இரவெல்லாம் ஈடுகொடுத்த நீ; இப்போது<br/>அதிர்கின்ற அளவுக்குக் கண்ட காரணம்தான் என்ன?<br/><br/>தலைவி:<br/>அதோ பாரடி; அவனை!<br/>வேல் முனையைத் துடைத்துக் கூரேற்றியவாறு<br/>வீரர்களை அழைத்து விரைவுபடுத்துகின்றான்!<br/>மயிற்பீலி சூட்டி மணியும் அணிவித்து<br/>மலையொத்த தோளிரடைக் குலுக்கிக்கொண்டே<br/>ஆடவர்க்கிடையே ஓர் அடலேறு போல<br/>கேடயத்தைத் தாங்கி எனை நோக்கி வருகின்றான்!<br/>இச்சையில்லை உயிர்மீது என்ற துணிவுடனே<br/>கச்சை கட்டிக் கவசம் பூண்ட அந்தக் காளையினைக் காண்க!<br/>இரவெல்லாம் துயில் கொள்ளாத உன் கண்ணால் பாரடி!<br/>இனி எந்தன் கண்களுக்குத் தூக்கமுண்டோ கூறடி!<br/>பரந்து பட்ட தீயினால் காடழியப்<br/>பாலையாய் மாறிவிட்ட நிலங்கடந்து<br/>இறந்துபடினும் இந்நாடு காப்போம் எனும் உறுதியுடன் உற்றாரைத்<br/>துறந்து செல்லும் படை வீரர் பல்லாயிரத்தோர்<br/><br/>நடந்து செல்கின்றார்; போர்க்களம் நாடி! அவனும்<br/>விரைந்து வருகின்றான் விடைகேட்க எனைத் தேடி!<br/>புரிந்ததா உனக்கு இப்போது - இரவு அவன்;<br/>புரிந்திட்ட செயலுக்கெல்லாம் பொருள் விளக்கம்?<br/>இன்பவெள்ளத்தில் மிதந்ததாக எனைக்கடிந்து<br/>எரிந்து விழுந்தாயே; எனைப் பார் தோழி!<br/><br/>இமையிரண்டில் வெள்ளம் கரைபுரண்டு - என்<br/>இருவிழியின் பாவைகளும் மறைந்துருண்டு<br/>இரவு கண்ட இன்பமெல்லாம் செலவுக்கணக்காகி;<br/>வரவுக்கணக்கில் துன்பம்; தொகை தொகையாய்<br/>இப்போதே வரப்போகும் காட்சியினைப்பார்!<br/>தப்பாது நடந்ததடி நான் கணித்த குறிப்பு!<br/><br/>நாடகத்தை நன்றாக நடத்தியுள்ளான் விடியுமட்டும்; என்<br/>ஆடகப் பொன்மேனியினை ஆரத்தழுவி அமுதமூட்டியபின்<br/>இடர்விளைத்துப் பிரிந்து சென்று என் நெஞ்சத்தைப் பிழிந்தெடுக்க<br/>சுடர் முகம் தூக்கியவன்; சூழ்ந்து வரும் பகை எதிர்க்கக் கிளம்பிவிட்டான்!<br/><br/>படை நடத்தப்போகின்றேன் கண்ணே<br/>பகைவர்களின் சூழ்ச்சியினைச் சுக்குநூறாய்<br/>உடைத்தெறிந்து வாகைசூடி<br/>உனைவந்து காணுகின்றேன் பெண்ணே;<br/>விடைகொடுப்பாய் மானே என்பான்!<br/>அவன் பேச்சென்னவோ தேனாகத்தான் சொட்டும்;<br/>ஆனால் என் இதயத்தில் அது தேளாகவன்றோ கொட்டும்!"))
        databank.append(DataModel(title: "வண்டு வந்தது ஏனடி?", data: "பழத்துக்குள் சிறையிருக்கும் விதையைப்போல்<br/>பருவப்பாவையினை வீட்டுக்குள் பாதுகாத்தாள் அன்னை; அந்தக்<br/><br/>கிழத்துக்குக் காதுகுத்தி; மெல்ல, அருகிருக்கும்<br/>குளத்துக்குச் செல்வதாகக் குமரிப்பெண் கூறிவிட்டு,<br/><br/>அடக்கத்தின் உருப்போல நடந்து காட்டி; அன்னை முகம் மறைந்தவுடன்<br/>அங்குள்ள மலையருவி தேங்குகின்ற குளம் தேடி ஓடிடுவாள்!<br/><br/>களத்துக்கு வருகின்ற கேடயத்தை முத்தமிடக்<br/>காத்திருக்கும் வீரன் கை வாள் போலக்<br/><br/>காளையவன் காத்திருப்பான் கண்மணியின் வரவு நோக்கி!<br/>தோளைத் தழுவுகின்ற மாலையாக அவளும் தொத்திக் கொள்வாள்!<br/><br/>\"களவொழுக்கம் தீதாகும்; அன்னைக்கும் அலர்தூற்றும் ஊரார்க்கும்;<br/>காதில் பட்டவுடன் கடும் எதிர்ப்பு மூண்டுவிடும் - அதனாலே<br/>அளவோடு இருக்கட்டு\"மென்று அவள்தோழி அடிக்கடி குறுக்கிட்டு<br/>ஆயிரம் முறை தடுத்திட்டாள்; அவள் அதனைக் கேட்டால்தானே!<br/>அன்றொருநாள் அருவிக்குளம் போவதாக அன்னைகாதில் பூ சுற்றிவிட்டு<br/>குன்றோரம் முகிலிரண்டு ஒன்றானதுபோல் ஆகிவிட்டார் அவளும், அவனும்!<br/><br/>\"பொறிகிளர் ஆகம் புல்லத் தோள் சேர்பு\" எனப்<br/>புலவர் வழுதியார் எழுத்தில் பொறித்தது போல;<br/><br/>அழகிய மார்புகள் இரண்டும் அழுந்தத் தழுவி மகிழ்ந்து<br/>பழச்சுவை முத்தம் பத்து, நூறு, ஆயிரமெனப் பகிர்ந்து கொண்டனர்!<br/><br/>அடுத்தநாள் காலை அடுக்களைப்பக்கம் அன்னையிருந்தனள்<br/>எடுத்தும் கொடுத்தும் இட்டபணிகளைச் செய்ய மகளும் சென்றனள்!<br/><br/>ஆரத்தழுவி அருவியோரம் நடந்ததொரு நிகழ்ச்சியால்<br/>அரிவையின் தோளில் வீசய நறுமணமுணர்ந்து,<br/><br/>அவளையே சுற்றிப் பறந்தன வண்டுகள்!<br/>அன்னையும் அதனைப் புரிந்து கொண்டனள்!<br/><br/>\"என்னடி பெண்ணே; சொல்லடி எனக்கு<br/>உன்னடி தவறி எவன் மீது வீழ்ந்தனை?\" என்றாள்!<br/><br/>வண்டுகள் மொய்த்தலால் காதற்கள்வனின் செயலைக்<br/>கண்டு கொண்டனளே அன்னையென்று; பூச்<br/><br/>செண்டுகள் இரண்டையும் துகிலால் மறைத்தாள்,<br/>மண்டிய பற்குறி நகக்குறி தெரியாதிருக்க!<br/><br/>வாய்மூடி ஆட்டம் காட்டாதே மகளே; உனையீன்றுப் பாதுகாக்கும்<br/>தாய்தான் கேட்கின்றேன், நடந்ததைச் சொல் என்றாள்! தணலானாள்!<br/><br/>இனி அவளைத் தப்புவிக்க ஏற்றதொரு பொய்தான் தேவையென<br/>இனியவளாம் அவள்தோழி வெடுக்கெனப் பாய்ந்து வந்து;<br/><br/>\"அன்னையே கேள் நீ; அறியாமல் அவள்மீது பழிதூற்றுகின்றாய்<br/>அடுப்பினிலே சந்தன விறகுகளை வைத்து எரிப்பதாலே<br/><br/>அடுக்களைக்குள் அந்தமணம் பரவியெங்கும் படர்ந்துள்ளது<br/>அடுக்கடுக்காய் வண்டுகளும் அதனால்தான் பறக்குதம்மா!<br/><br/>அஃதன்றி அன்னை நீ ஐயுறுதல் போலே; நீ பெற்ற<br/>அருமை மகள் ஆடவர் எவரையுமே ஏறெடுத்துப் பார்த்ததில்லை!<br/><br/>ஆணையிட்டுக்கூட நான் சொல்வேன்; அது தேவையில்லை எனக் கருதுகின்றேன்! இந்த<br/>வீணையினை மீட்டுதற்கு உன் விழி மூலம் இசைவுதனைப் பெறுவதன்றி; இவளைக்<br/><br/>காதலிக்கும் திருமகன் இவள்மீது மறந்தும்கூட<br/>கைகளையே வைப்பதற்கும் ஒப்பமாட்டாள்; இது உண்மை\" என்றாள்!<br/><br/>தாயாரும் நம்பிவிட்டாள்; அடுப்பின் நெருப்புத்<br/>தணலாலே சந்தன விறகுதான் மணக்குதென்றும்<br/><br/>தங்க மகள் தவறொன்றும் செய்யமாட்டாள் என்றும்<br/>அங்கமெலாம் பூரிக்க அவள் உச்சிமோந்து முத்தமிட்டாள்!<br/><br/>இப்படி வீட்டுக்குள் நடந்த கதையினையே - தலைவியின்<br/>சொற்படி தோழிசென்று தலைவனுக்குரைத்து நின்றாள்.<br/><br/>\"மூங்கில் நிறை பாறைமிகு பாதைகளில்<br/>வேங்கைகளின் நடமாட்டம் கண்டும் பதறாமல்;<br/><br/>ஓங்குபுகழ் நாடனே! நீ ஒவ்வொருநாளும் வந்து<br/>ஏங்குகின்ற தலைவிதுயர் துடைக்கின்றாய்; நன்று!<br/><br/>ஆங்கவளோ அன்னையிடம் படும்பாடு நீ அறிந்திலையோ?<br/>பாங்கிநான் ஒருகேள்வி கேட்கின்றேன் பதறிடாதே!<br/><br/>திருமணநாள் குறிப்பதாக தினந்தோறும் கூறுகின்றாய்;<br/>தேன் என்று சொல்லிவிட்டால் இனித்தா விடும்?<br/><br/>தேர்ந்திடுவீர் ஒருநாளை; இருவருமே வாழ்வில் துணையாக<br/>சேர்ந்திடுவீர் பெற்றோர் உற்றாரின் வாழ்த்தோடு!<br/><br/>இல்லையெனில் இந்த மலைநாட்டு மாவீரன்பால்<br/>இல்லையென்றாகிவிடும் வாய்மை! வாய்வீரம் மட்டும்தான் மிஞ்சும்!\" என்றாள்.<br/><br/>\"முல்லையிடம் கூறிவிடு; பெண் கேட்க நாளை என் பெற்றோர் வந்திடுவர்!<br/>கொல்லை வழிச் சந்திப்பு எனும் தொல்லைக்கே இனி இடமில்லை!\" என்று சொல்லி;<br/><br/>நடந்திட்டான் மலைநாடன்; அவன் சொன்னதுபோல்<br/>நடந்திருக்கும் திருமணமும்!"))
        
    
        databank.append(DataModel(title: "மயங்கா மார்புடை மறவன் நீயே!", data: "தென்புலத்துக் கடலோரம் தீமை விளைவித்தோரை;<br/>வென்றடக்கிய வீரவாளுக்குடையோன்<br/><br/>வடபுலத்து வடுகமன்னர் படையொடுக்கி<br/>வாகை மாலைதனைத் தோளில் சுமந்தோன்<br/><br/>இளஞ்சேட் சென்னியெனும் பெயருக்குரியோன்<br/>வளமிகு சோழமன்னன் திறன்மிகு வலிமையினை<br/><br/>ஊன்பொதிப் பசுங்குடையார் எனும் ஒரு புலவர்<br/>மீன்துள்ளும் அருவிநடைத் தமிழால் தந்தார்!<br/><br/>நெய்தலங்கானல் சோழன் எனும் பிறந்த ஊர்ப் பெருமையுடன்<br/>நெடிய புகழ் செருப்பாழியும் பாமூளூரும் வென்ற சோழன் எனும் அடைமொழியும்,<br/><br/>இரு கருத்துக்கு இடமேயின்றி, இற்றைப்புலவர்கள்<br/>இளஞ்சேட் சென்னியினையே குறிக்குமென்பர்!<br/><br/>புறப்பாடல் தமிழ்ப்புலவர் இயற்றும்போதே<br/>புராணக் காதைகளும் புழக்கத்தில் உண்டு போலும்!<br/><br/>அதனாலே அற்புதமாய் உவமையொன்றை<br/>அரும்புலவர் பசுங்குடையார் அன்றைக்கே எழுதியுள்ளார்.<br/><br/>அரசிருக்கை கொண்டிருந்தபோது சோழனிடம்<br/>பரிசில் வேண்டிப் பாவலர் \"பாட்டாடை\" போர்த்த;<br/><br/>\"பாட்டாடை\"க்குப் பதிலாக \"பட்டாடை\" போர்த்திப்<br/>பார்வேந்தன் பல்வகை அணிமணி பரிசாய்த் தந்தான்!<br/><br/>மன்னர் குலத்தோர் மட்டுமே அணிகின்ற முத்துவடம்<br/>மரகத மணிமாலை மாணிக்க வளையற் குவியல்,<br/><br/>இன்னபிற நகைகளையும் இளஞ்சேட் சென்னி<br/>புன்னகை மிளிரப் பொழிந்தான் புலவரிடம்!<br/><br/>உள்ளமெல்லாம் உவகையால் நிறைந்து பொங்க<br/>கள்ளமில்லாத தன்சுற்றத்தார்க்கு வாரிவழங்க<br/><br/>கடுவேகப் பயணம்செய்து உற்றார் கைவழியக்<br/>களிப்புடனே அளித்திட்டார் கவிஞர்பிரான்!<br/><br/>காணாக அணிமணிகள் கண்டதாலே; சுற்றத்தார்<br/>கண்டபடி அவற்றை எடுத்துக் கழுத்துக்குரியவற்றைக்<br/><br/>கைகளிலும்; கைகளுக்குரியவற்றை அவசரமாய்க்<br/>கழுத்தினிலும் மாற்றிமாற்றி மாட்டிக்கொண்டு;<br/><br/>கணையாழி விரலுக்குரியதெனப் புரியாமல்<br/>காதணியிதுவென்று பூட்டிக்கொண்டார்!<br/><br/>அதுகண்டு;<br/><br/>ஊன்பொதிப் பசுங்குடையார் உரக்கச் சிரித்தார் - அந்த<br/>உறவினரின் செயலுக்கு உவமையொன்றும் கண்டார் அங்கே!<br/><br/>இராமனது மனைவியாம் சிதைதனை இராவணனாம்<br/>இலங்கை வேந்தன் சிறையெடுத்துச் சென்றபோது;<br/><br/>வழியெல்லாம் அவள் கழற்றி எறிந்திட்ட அணிமணியை<br/>வானரங்கள் எடுத்து வந்து அணியும் வகை அறியாமல்<br/><br/>இடைக்கென உரியதை மந்திகள் தம் கழுத்துக்கும்<br/>எழில் கழுத்துக்குரியதைக் காலுக்கும் தொடைக்குமடாக<br/><br/>அணிந்தலைந்து திரிந்ததாகக் கற்பனை உண்டன்றோ?<br/>அக்காட்சியினை உவமையாக்கிப் புலவர்ஏறு; அவர்தம்<br/><br/>உற்றார் உறவினரின் செயல்பாட்டைத் தமிழ்<br/>கற்றார் களிக்கும் வண்ணம் தீட்டியுள்ளார்.<br/><br/>வளமிகு சொற்செல்வராம் அப்புலவர்;<br/>இளஞ்சேட் சென்னியினை இன்னொரு பாட்டில்<br/><br/>புகழ்ந்திடும் விதமோ பொழில் மலர் அமுதமாம்;<br/>போற்றிடும் பாங்கோ பொதிகைமலைத் தென்றலாம்!<br/><br/>அது; இதோ!<br/>\"வழிபடுவோர்க் கெல்லாம் விழிகாக்கும் இமையானாய்!<br/>பழிகூறிப் பிறரை வீழ்த்த உன்பால் வருவோரை<br/><br/>இழிதகையோர் எனஎண்ணி உடன் ஒதுக்கி<br/>இடமில்லை இங்கென்று விரட்டுகின்ற விவேகியானாய்!<br/><br/>குற்றம் கண்ட இடத்து அதனைச் செய்தோர் யாராயினும்<br/>கொற்றம் சாயாது காத்திடக் கடுந்தண்டனை கொடுத்திடுவாய்!<br/><br/>நீதியும் நெறியும் சாயா நேர்மையே உன்னாட்சியான<br/>வீதியில் முரசம் விதிமுறைப்படி ஒலிக்குமே!<br/><br/>பிழையினை உணர்ந்தோர் திருந்திப்பணிந்திடின் - அன்பு<br/>இழையிலே கோத்த அருளினை வழங்குவாய்!<br/><br/>வரும் விருந்தினர் போற்றி - அவர்தம்<br/>வாழ்த்தினைப் பெற்றிடும் பொற்புடை அரசியின்<br/><br/>மாங்கனி மேனி, பட்டால் மட்டுமே மயங்குமேயன்றி;<br/>மாற்றார் கணைகள் ஆயிரம் வரினும் மயங்கா மார்புடை மறவன் நீயே!\"<br/><br/>இவ்வாறு<br/>ஊன்பொதிப் பசுங்குடையார் கவிப்பெருக்கில்<br/>உவமையோ உள்ளத்தைக் கொள்ளை கொள்ளும்!<br/><br/>உறுபகைக்கு அஞ்சாத சோழ வேந்தனது<br/>உரமான மார்பு குறித்த மாண்போ; நம் நெஞ்சை அள்ளும்!"))
            databank.append(DataModel(title: "புரிந்து கொண்டான்; பிரிந்து சென்றார்!", data: "இருவீணை, தலைகீழாய் இருத்தல் போன்று இடைக்குக் கீழ் இன்பபோதை ஊட்டுகின்ற உருவத்தாள்!<br/><br/>இணையாகக் குலுங்குகின்ற இளநீரைக் கெட்டியாக<br/>இறுக்கிப்போடும் கச்சையணிந்த கன்னிப் பருவத்தாள்!<br/><br/>கன்றுக் குட்டியொன்றைத் தாம்பில் பிணைத்து<br/>என்றும்போல் இசைபாடிக் குதித்து மகிழ்ந்து,<br/><br/>குன்றுக்கு அருகேயுள்ள தோட்டத்தை நெருங்கிடவே;<br/>குறுக்கிட்டான் எதிரே; குறுஞ்சிரிப்புக் காளையொருவன்!<br/><br/>ஆம்பற்கொடி போன்று அவள் கையில் பிடித்திருந்த<br/>தாம்புக் கயிறுதனை ஒருகையால் பற்றிக்கொண்டு<br/><br/>\"சாம்பல் நிறக் கன்றுக்குட்டி நலந்தானா? அதனை உயிராக<br/>ஓம்புகின்ற என்னுயிரே; நீயும் நலந்தானா? எனக் கேட்டான்.\"<br/><br/>துள்ளிப் பாய்ந்திட்டாள் அத்துடியிடையாள் - \"நீ<br/>அள்ளிப் பருக நான் என்ன அருவி நீரா?<br/><br/>வழியில் போகும் என்னிடம் எதற்காக வீண்வம்பு? என்<br/>விழியின் அனல்கண்டு விலகிப்போ; இல்லையேல் என்மொழியும் கனலாகும்\"<br/><br/>என எச்சரித்த இளமங்கைக்கு நேர்நின்று,<br/>\"ஏகந்திழையே! எதற்காக நான் விலகிப்போகவேண்டும்?<br/><br/>பிடிக்க வருவோரைப் பின்காலால் உதைத்து; வாலால் அடித்துப்<br/>பிடிபடாமல் ஓடுகின்ற பெண்எருமைக் கன்றைப்போல்; எனை<br/><br/>இடித்துத் தள்ளிவிட்டு இங்கிருந்து நீ ஓடிவிடு! உரையாட<br/>இஷ்டந்தான் உனக்கும்! ஊர்வாயை மூடமுடியாதே என அஞ்சுகின்றாய்!<br/><br/>முதலில் உன் வாயை என் உதடுகளால் மூடட்டுமா? ஓகோ!<br/>பதிலில்லை எனில் ஒப்புதலுக்கு அடையாளமோ?\" என்றான்.<br/><br/>\"என்ன துணிவிருந்தால்; எனை வழிமடக்கி<br/>இன்னல் விளைத்திடுவாய்? என் தாய்க்குச்<br/><br/>சேதி தெரிந்தால் இந்நேரம் ஓடிவந்து<br/>வீதியிலே கன்றை வளைப்போர் மீது சினம்பொங்க<br/><br/>வேகமாய்ப் பாய்ந்து முட்டும் பசுவைப் போல்<br/>விட்டியடிப்பாள் உனை!\" என்றாள்; அவன் சிரித்தான்!<br/><br/>\"உன் தாயே வரட்டும்; அல்லால் இந்த ஊரே வரட்டும்!<br/>ஊராளும் உன் தந்தை கூட உருவிய வாளுடன் வரட்டும்!<br/><br/>யாருக்கும் நான் அஞ்சத்தேவையில்லை; இந்தச் சின்ன எழில்<br/>தேருக்கு நான்தான் சாரதியென நீ சொல்லிவிட்டால்!\" என்றான்.<br/><br/>அவளோ; உடல் குலுக்கி, முகம் சுளித்து, அவனை நோக்கி<br/>ஆர்ப்பாட்டம் பலபுரிந்து எதிர்ப்பு காட்டி<br/><br/>\"அய்யா; நீ பெருமழை பொழியும்போதும் அறியாமல்<br/>அசையாமல் நிற்கின்ற காளைமாடென ஆகிவிட்டாய்!<br/><br/>ஏட்டிக்குப் போட்டியாக ஏதேதோ சொல்லி<br/>காட்டிவிட்டாய் உன்காதல் எண்ணத்தை!\"<br/><br/>எனக்கடு மொழியுரைப்பவள்போல் நடித்தவாறு<br/>\"எள்ளளவும் வெட்கமின்றிப் போனவனே!\" என இழித்துரைத்து<br/>பட்டென்று பதிலாய்ப் படபடவெனப் பொறிந்தாள் எனினும்<br/>பட்டுப்போல் மென்சொற்களாலே ஒன்று கேட்டாள்;<br/><br/>\"நாளை நான் கறவைக் கலமுடனே பசுமேயும் இடத்துக்குச் செல்லுகிறேன்<br/>காளைநீ அப்போதும் அங்கேயும் வருவாயோ?\" என்பதுதான் அக்கேள்வி!<br/><br/>புரிந்து கொண்டான் அவள் உள்ளம்; பின்னர்<br/>பிரிந்து சென்றார் புன்னகை புரிந்தவாறே!"))
            databank.append(DataModel(title: "அவள் நிலமானாள்; அவன் மழையானான்!", data: "நட்சத்திரங்கள் முகம்பார்த்து மினுக்கிக் கொள்<br/>நாளெல்லாம் தேடித் தொங்கவிட்ட நிலவென்னும் கண்ணாடியில்,<br/>நம்மிருவர் முகம் பார்க்க முடியாதெனினும்; கண்ணே!<br/>நான் உன் முகத்தை நிலவாகவே பார்க்கின்றேன்!<br/>மெய்யாகவே நிலவைப்போல் இருக்கவேண்டுமென்றுதான் - நீ<br/>மைகொண்டு கண்ணெழுதி, கன்னத்தில் புள்ளியொன்றும் கருநிறத்தில் வைத்துக் கொண்டாய்!<br/>தேங்காய்க் கீற்று போன்ற பிறை காட்டு எனக் கேட்டால்<br/>பாங்காய் உன் முகத்தை என் முகத்தில் பதித்துப் பைங்கிளியே!<br/>நேர்வகுடுக்குக் கீழுள்ள உன் நெற்றியினைக் காட்டிடுவாய்!<br/>நேரிழையே! நீ எனக்கு விளக்க வேண்டும்;<br/>நிலவைக் கறுப்பாக்கும் \"அமாவாசை\" ஒன்று வருமே!<br/>நீ அதற்கு விதிவிலக்கா? என நான் கேட்டபோது<br/>என் மடிமீது முகமுழுதும் புதைத்துக்கொண்டு,<br/>உன் கருங்கூந்தல் மட்டுமே நான் காணப் படுத்துக் கொண்டாய்!<br/>அதனை நான் அமாவாசையென எண்ணிக்கொள்ள வேண்டுமென;<br/>அழகோவியமே! நீ உன் விரல் கொண்டு சுட்டிக்காட்ட,<br/>கார்குழலே நான் கோதிக்கொண்டே<br/>கண்மணியே உன் உச்சிமீது முத்தமீந்தேன்!<br/>பாம்பு; நிலவை விழுங்குகின்ற பழம் புராணக் கதையொன்றை<br/>ஆம்பற் கொடியிடையாளே! நம்புவதற்கில்லை யென்றேன்; நீயோ,<br/>மறுத்துரைத்து வாதிட்டு \"இதோ, இந்த நிலவின் கன்னத்தை<br/>அறுத்தெடுக்காமல் வாய்க்குள் விழுங்குகின்ற உமது செயலுக்கு என்ன பெயராம்?\" எனக்கேட்டு,<br/>\"பழம் புராணப் பாம்பு விழுங்கும் கதை பொய் எனினும்;<br/>பழம் போல எனை விழுங்கி விழுங்கி விடுவிக்கும் இந்தப் பள்ளியறைக் கதை மெய்தானே\" என்றாய்!<br/>உன் கவிதை நடைப் பேச்சில் மயக்கமுற்று<br/>ஒரு நூறு முத்தங்கள் உடனே தந்தேன்! நீயும் திருப்பித் தந்தாய்!<br/>அதையெல்லாம் மறந்துவிட்டு; உனைப் பிரிந்து<br/>அயலூரில் நெடுநாள் தங்கிவிட்டேன் என்று<br/>ஆருயிரே! அகம் நொந்து நீ ஊடுவது நியாயம்தானா?<br/>ஊடல் புரிவதிலும் ஓர் அழகைத்தான் காணுகின்றேன்.<br/>பாடல் பிறப்பதற்கு இசை கூட்டல் வேண்டுமன்றோ! அதுபோலக்<br/><br/>கூடல் விழா தொடங்குவதற்கு,<br/>ஊடல்தான் கொடியேற்றி வைக்க வேண்டும்!<br/><br/>அதனாலே உன் ஊடல் கண்டு உவகை மிகக் கொள்கின்றேன்!<br/>ஆனாலும் அளவுக்கு மீறிவிட்டால் அமுதமும் நஞ்சாகிவிடுமன்றோ!<br/>தொட்டால் நெருங்காமல் நீ எட்டி விலகும்போது<br/>தொலைவிலிருந்து உன் முழு எழிலைப் பருகுகின்றேன்!<br/>மொட்டாய்க் குவிந்து நிற்கும் மார்பகத்து ஆடையினை நான் நகர்த்த - அது<br/>கட்டோடு பிடிக்காமல் இழுத்துப் போர்த்தி - வாய்<br/>மொழியால் \"விடுங்கள்\" என்று நீ வெடுக்கென்றுரைத்தாலும் - உன்<br/>விழி மட்டும் ரகசியமாய் ஓர் உடன்பாட்டுக்கு வருதல் கண்டு; இதயம்,<br/>பொழிகின்ற இன்ப மழைச் சுகத்தை நான் என்னென்று சொல்வேன்!<br/>வழிகின்ற தேனருவிப் பக்கம் போவோம் வா! - வீணாய்க்<br/>கழிகின்றதே பொழுது என; நம் வரவுக்காகக்<br/>காத்திருக்கும் மலர் மெத்தையினைப் பார்!<br/>கோத்திருக்கும் முத்தாரப் பல்வரிசைப் பேழையின்<br/>மூடியினைப் புன்சிரிப்புத் திறவுகோலால் திறந்துவிட்டு<br/>ஊடியது போதுமென என் தோளில் ஊஞ்சல் ஆடிடுக!<br/>வாடியதோ என வண்ணத் தமிழ்க்கிளியின் நெஞ்சம்?<br/>தேடியதோ? தேடிப்பின்னர் திகைத்ததோ? ஒருவேளைத்<br/>திரும்ப வருவேனோ, மாட்டேனோ என்று<br/>இரும்படிக்கும் உலைவீழ்ந்த புழுவாகத் துடித்ததோ?\"<br/><br/>இவ்வாறு<br/>பிரிந்திருந்த காதலனின் வரவு பார்த்து - மனம்<br/>வருந்தி வீழ்ந்த பெண்மான் ஒன்று - அவன்<br/>வந்தபின்னும் ஊடலுற்றுச் சினந்தபோது, அவளைத் தன்<br/>வசமாக்க வாரியிறைத்திட்டான் வர்ணனைப் பூமாரி!<br/><br/>அவளோ;<br/>இன்னும் ஏனவன் பேசிக்கொண்டு நிற்கின்றான் -<br/>இழுத்தணைத்துப் பசும்புல் தரையில் படுக்கவைத்து<br/>கன்னம் சிவக்க, கனியுதடு மெல்ல வீங்க - முன்போல்<br/>கணக்கென்ன நூறு? முன்னூறு நானூறு முத்தங்கள் கொடுத்திட்டால்<br/>கசக்குதென்று கழுத்தைப் பிடித்துத் தள்ளியா விடுவேன்?<br/>கட்டியணைக்கவே அவன் கரம் தாவாதோ தன்மீதென்று,<br/>தணலைப் போல் கொதிக்கின்ற காதல்தனை<br/>தன் நெஞ்சுக்குள் அடைத்து வைத்துத் தத்தளித்தாள்!<br/><br/>\"அன்றென்னை அமளியிற் கிடத்தி<br/>அன்றிலடி நாமிருவர் அரைநொடியும்<br/>பிரிவதில்லை! ஆணையடி அன்பே என்று<br/>விரிவுரைகள் ஆற்றினீரே - என்ன பயன்?<br/>விடிந்தால் ஒரு திங்கள் முடிந்துவிடும் - இவள்<br/>மடிந்தால் மடியட்டுமென்றுதானே கவலையற்று<br/>மறுநாளே வருவதாய்ச் சொன்ன சொல்லை மறந்து போனீர்!<br/>மறப்பது ஆடவர்க்கு இயற்கையெனக் கூறிவிடும்! நானும்; உயிர்<br/>துறப்பதும் மகளிர்க்கு எளிதேயென்று காட்டுகின்றேன்.\"<br/><br/>இவ்விதம்<br/><br/>சொற்களுக்குச் சோக இசை சேர்த்து - அந்தச்<br/>சொர்ணத்தின் வார்ப்படம் சுளையிதழ்கள் மூடுமுன்பே,<br/>ஓடிவந்து கட்டிக் கொண்டான்! \"நான்<br/>தேடிநின்ற தெள்ளமுதே!\" என அவளும் ஒட்டிக் கொண்டாள்!<br/>வானூர்ந்த நிலவழகி; முகில் கொண்டு முகம் மறைத்தாள் வெட்கத்தாலே!<br/>மானொன்று நாணமுற்று புதர் மறைவில் ஒதுங்கிற்றாங்கே!<br/>கிள்ளைகளும், புறாக்களும் இணை இணையாய்க்<br/>கிளைகளில் இருந்தெழுந்து \"சிறிதேனும் இந்தப்<br/>பிள்ளைகட்கு வெட்கமிலையோ! இத்தனைபேர் நம் மத்தியிலே<br/>வெள்ளை மலர்ப் படுக்கையிலே காம விளையாட்டா? சிச்சி\" எனக்கூறிப் பறந்தனவே!<br/><br/>ஊடலுக்கு முற்றுப்புள்ளி வைத்துவிட்டு - இன்பக்<br/>கூடல் முடித்து எழுந்தபோது - \"இனியொருமுறை<br/>\"வாடல்\" என்பது வாழ்க்கையில் வாராதே\" என்று<br/>ஆடல் தீர்ந்து தோகை மூடிய மயில் கேட்டாள்!<br/><br/>அவன், அவளைப் பார்த்து:<br/>*\"உயிரே! ஒன்று கேள்! உன்னையும் என்னையும்<br/>உலகில் பிரிக்கின்ற சக்தி எதுவுமில்லை!<br/>உனைப்பெற்ற தாய் யார் என்றோ<br/>எனையீன்ற தா யார் என்றோ<br/>உன் தந்தைக்கும் என் தந்தைக்கும்<br/>உறவுமுறை என்னவென்றோ<br/>இருவர் நாம் எவ்வழியில் வந்தோர் என்றோ<br/>அறிந்திட விரும்பாமலே அறிமுகமானோம்!<br/>எங்கிருந்தோ வந்தாள் என உன்னை நானும்,<br/>எங்கிருந்தோ வந்தான் என என்னை நீயும்<br/>ஏற்றுக்கொண்டோம்! இதயத்தில்;<br/>ஏற்றிக்கொண்டோம்! இனியவளே!<br/>செம்மண் நிலத்தில் மழை பொழிந்தால் - அந்த<br/>நிலத்தொடு கலந்த நீரில்<br/>சிவப்பு வண்ணத்தைப் பிரிக்க முடியாதன்றோ!<br/>அஃதேபோல் நமது<br/>நெஞ்சங்கள் இரண்டும் இணைந்துவிட்டன!<br/>எனவே பிரிவு எனும் நினைப்பை<br/>இக்கணமே அகற்றிவிடு!\"<br/><br/>இந்தக் குறுந்தொகைப் பாடலை<br/>இளங்குமரன் எளிமையாக்கிச் சொன்னவுடன்<br/>மங்கைநல்லாள், மீண்டும் நிலமானாள் - அவன்<br/>மழையானான்!"))
        
        
        
        
    }
}
